/**
 * Customer Management Module
 * 
 * Manages customer data, CRUD operations, and state synchronization
 * with localStorage for persistence.
 */

export const CustomerManagement = {
  version: '1.0.0',
  initialized: false,
  customers: [],

  // Initialize the module
  init: async function() {
    try {
      if (this.initialized) {
        return this;
      }

      AppLogger.log("Initializing Customer Management module");

      // Load customer data from storage
      await this.loadCustomers();

      // Initialize dashboard
      this.refreshDashboard();

      // Set up event listeners
      this.setupEventListeners();

      this.initialized = true;
      AppLogger.log("Customer Management module initialized successfully");

      return this;
    } catch (error) {
      AppLogger.error("Failed to initialize Customer Management module", error);
      throw error;
    }
  },

  // Load customers from localStorage
  loadCustomers: async function() {
    try {
      const storedCustomers = localStorage.getItem("customers");

      if (storedCustomers) {
        this.customers = JSON.parse(storedCustomers);
        AppLogger.log(`Loaded ${this.customers.length} customers from localStorage`);
      } else {
        // Initialize with empty array
        this.customers = [];
        this.saveCustomers();
        AppLogger.log("No customers found in localStorage, initialized empty array");
      }

      return this.customers;
    } catch (error) {
      AppLogger.error("Error loading customers from localStorage", error);
      this.customers = [];
      return [];
    }
  },

  // Save customers to localStorage
  saveCustomers: function() {
    try {
      localStorage.setItem("customers", JSON.stringify(this.customers));
      return true;
    } catch (error) {
      AppLogger.error("Error saving customers to localStorage", error);
      return false;
    }
  },

  // Get customers array
  getCustomers: function() {
    return this.customers;
  },

  // Add a new customer
  addCustomer: function(customerData) {
    try {
      // Validate required fields
      if (!customerData.name || !customerData.email || !customerData.phone) {
        throw new Error("Missing required customer fields");
      }

      // Generate a unique ID
      const newCustomer = {
        id: Date.now(),
        name: customerData.name,
        email: customerData.email,
        phone: customerData.phone,
        currentEmployer: customerData.currentEmployer || "Not specified",
        annualSalary: customerData.annualSalary || "0",
        latestNote: customerData.initialNote || "",
        status: "green",
        journeyData: {}, // Initialize empty journey data
        lastUpdated: new Date().toISOString(),
        createdAt: new Date().toISOString(),
        // Additional fields from enhanced form
        address: customerData.address || "",
        employmentType: customerData.employmentType || "fullTime",
        payCycle: customerData.payCycle || "fortnightly",
        currentVehicle: customerData.currentVehicle || "",
        leaseStatus: customerData.leaseStatus || "",
        interestedVehicle: customerData.interestedVehicle || "",
        annualKilometers: customerData.annualKilometers || "15000",
        vehicleType: customerData.vehicleType || "new",
        isEVPHEV: customerData.isEVPHEV || "no",
        vehicleValue: customerData.vehicleValue || "",
        aftermarketProducts: this.processAftermarketProducts(customerData)
      };

      // Add to customers array
      this.customers.push(newCustomer);

      // Save to localStorage
      const saved = this.saveCustomers();

      if (saved) {
        AppLogger.log(`Added new customer: ${customerData.name}`);
        return this.customers.length - 1; // Return index of new customer
      } else {
        throw new Error("Failed to save customer data");
      }
    } catch (error) {
      AppLogger.error("Error adding customer", error);
      throw error;
    }
  },

  // Process aftermarket products from form data
  processAftermarketProducts: function(customerData) {
    const products = [];

    // Check each possible aftermarket product
    const possibleProducts = [
      "motorInsurance", "leaseGuard", "minorDamageRepair", "carbonOffset",
      "roadside", "windowTint", "exteriorProtection", "interiorProtection", "dashCam"
    ];

    possibleProducts.forEach(product => {
      if (customerData[product]) {
        // Convert camelCase to display name
        const displayName = product
          .replace(/([A-Z])/g, ' $1')
          .replace(/^./, str => str.toUpperCase());

        products.push(displayName);
      }
    });

    return products;
  },

  // Update an existing customer
  updateCustomer: function(index, customerData) {
    try {
      if (index < 0 || index >= this.customers.length) {
        throw new Error("Invalid customer index");
      }

      const customer = this.customers[index];

      // Update fields
      for (const [key, value] of Object.entries(customerData)) {
        customer[key] = value;
      }

      // Update last modified timestamp
      customer.lastUpdated = new Date().toISOString();

      // Save changes
      const saved = this.saveCustomers();

      if (saved) {
        AppLogger.log(`Updated customer: ${customer.name}`);
        return true;
      } else {
        throw new Error("Failed to save customer data");
      }
    } catch (error) {
      AppLogger.error("Error updating customer", error);
      return false;
    }
  },

  // Delete a customer
  deleteCustomer: function(index) {
    try {
      if (index < 0 || index >= this.customers.length) {
        throw new Error("Invalid customer index");
      }

      const customerName = this.customers[index].name;

      // Remove customer from array
      this.customers.splice(index, 1);

      // Save changes
      const saved = this.saveCustomers();

      if (saved) {
        AppLogger.log(`Deleted customer: ${customerName}`);
        return true;
      } else {
        throw new Error("Failed to save customer data");
      }
    } catch (error) {
      AppLogger.error("Error deleting customer", error);
      return false;
    }
  },

