  // Refresh dashboard UI
  refreshDashboard: function() {
    try {
      const customerListDiv = document.getElementById("customerList");
      if (!customerListDiv) return false;

      // Show loading state
      customerListDiv.innerHTML = "";
      const loadingIndicator = window.NovAssistant.ui.showLoadingIndicator(customerListDiv, "Loading customers...");

      // Get filter values
      const statusFilter = document.getElementById('statusFilter')?.value || 'all';
      const searchTerm = document.getElementById('searchCustomers')?.value || '';

      // Filter customers
      let filteredCustomers = this.customers;

      if (statusFilter !== 'all') {
        filteredCustomers = filteredCustomers.filter(customer => customer.status === statusFilter);
      }

      if (searchTerm.trim() !== '') {
        const search = searchTerm.toLowerCase();
        filteredCustomers = filteredCustomers.filter(customer => 
          customer.name?.toLowerCase().includes(search) ||
          customer.email?.toLowerCase().includes(search) ||
          customer.phone?.toLowerCase().includes(search) ||
          customer.currentEmployer?.toLowerCase().includes(search) ||
          customer.interestedVehicle?.toLowerCase().includes(search)
        );
      }

      // Update in next frame for performance
      setTimeout(() => {
        try {
          window.NovAssistant.ui.hideLoadingIndicator(loadingIndicator);

          if (filteredCustomers.length === 0) {
            customerListDiv.innerHTML = `
              <div class="empty-state">
                <i class="fas fa-users" style="font-size: 3rem; color: #ccc; margin-bottom: 1rem;"></i>
                <p>No customers found. ${this.customers.length === 0 ? 'Click "New Enquiry" to add your first customer.' : 'Try adjusting your filters.'}</p>
              </div>
            `;
            return;
          }

          // Use document fragment for better performance
          const fragment = document.createDocumentFragment();

          filteredCustomers.forEach((customer, index) => {
            try {
              // Find the original index in the full customers array
              const originalIndex = this.customers.findIndex(c => c.id === customer.id);

              const card = document.createElement("div");
              card.className = "customer-card";
              card.innerHTML = `
                <h3>${customer.name || "Unnamed Customer"}</h3>
                <p><strong>Employer:</strong> ${
                  customer.currentEmployer || "Not specified"
                }</p>
                <p><strong>Status:</strong> <span class="status ${
                  customer.status || "green"
                }">
                  ${
                    (customer.status || "green").charAt(0).toUpperCase() +
                    (customer.status || "green").slice(1)
                  }
                </span></p>
                <p><strong>Progress:</strong> ${this.calculateJourneyProgress(
                  customer
                )}%</p>
              `;

              if (customer.status === "orange" || customer.status === "red") {
                card.style.borderLeft = "5px solid #FFA500";
              }

              card.addEventListener("click", () => {
                this.openCustomerProfile(originalIndex);
              });

              fragment.appendChild(card);
            } catch (e) {
              AppLogger.error(
                `Failed to render customer card at index ${index}`,
                e,
                { customerId: customer.id }
              );

              // Create basic error card to prevent empty UI
              const errorCard = document.createElement("div");
              errorCard.className = "customer-card error-card";
              errorCard.innerHTML = `
                <h3>${customer.name || "Customer"} (Display Error)</h3>
                <p>This customer card couldn't be displayed properly.</p>
              `;
              fragment.appendChild(errorCard);
            }
          });

          customerListDiv.appendChild(fragment);

          // Update other dashboard elements
          this.updateDashboardMetrics();
          this.updateHotLeads();

        } catch (e) {
          AppLogger.error("Error in async dashboard refresh", e);

          // Show error state in UI
          customerListDiv.innerHTML = `
            <div class="error-state">
              <i class="fas fa-exclamation-triangle" style="font-size: 3rem; color: #f44336; margin-bottom: 1rem;"></i>
              <p>There was an error loading the customer list.</p>
              <button onclick="window.NovAssistant.CustomerManagement.refreshDashboard()" class="secondary-btn">
                <i class="fas fa-sync-alt"></i> Retry
              </button>
            </div>
          `;
        }
      }, 10);

      return true;
    } catch (e) {
      AppLogger.error("Error refreshing dashboard", e);
      return false;
    }
  },

  // Update dashboard metrics
  updateDashboardMetrics: function() {
    try {
      const totalEnquiries = document.getElementById("totalEnquiries");
      const actionRequired = document.getElementById("actionRequired");
      const monthlyDeals = document.getElementById("monthlyDeals");

      if (totalEnquiries) totalEnquiries.textContent = this.customers.length;

      if (actionRequired) {
        const actionRequiredCount = this.customers.filter(
          c => c.status === "orange" || c.status === "red"
        ).length;
        actionRequired.textContent = actionRequiredCount;
      }

      if (monthlyDeals) {
        // Calculate deals closed this month
        const now = new Date();
        const monthStart = new Date(now.getFullYear(), now.getMonth(), 1);

        const monthlyDealsCount = this.customers.filter(c => {
          if (!c.closeDate) return false;
          const closeDate = new Date(c.closeDate);
          return closeDate >= monthStart && closeDate <= now;
        }).length;

        monthlyDeals.textContent = monthlyDealsCount;
      }

      return true;
    } catch (e) {
      AppLogger.error("Error updating dashboard metrics", e);
      return false;
    }
  },

