        let completed = 0;
        checkboxes.forEach((checkbox) => {
          if (checkbox.checked) completed++;
        });

        // Update milestone checkbox
        if (checkboxes.length > 0 && completed === checkboxes.length) {
          milestoneCheckbox.checked = true;
          milestone.classList.add("completed");
        } else {
          milestoneCheckbox.checked = false;
          milestone.classList.remove("completed");
        }
      });

      return true;
    } catch (error) {
      AppLogger.error("Error updating milestone checkboxes", error);
      return false;
    }
  },

  // Handle task checkbox changes
  handleTaskCheckChange: function(checkbox) {
    try {
      // Get current customer
      const customerIndex = window.NovAssistant.state.currentCustomerIndex;
      if (customerIndex === null) return false;

      const customers = window.NovAssistant.CustomerManagement.getCustomers();
      const customer = customers[customerIndex];
      if (!customer) return false;

      // Get task, milestone, and phase IDs
      const taskId = checkbox.id;
      const milestone = checkbox.closest(".milestone");
      const phase = checkbox.closest(".journey-phase");

      if (!milestone || !phase) return false;

      const milestoneId = milestone.getAttribute("data-milestone");
      const phaseIndex = Array.from(
        document.querySelectorAll(".journey-phase")
      ).indexOf(phase);

      if (phaseIndex < 0 || phaseIndex >= this.journeyStructure.phases.length)
        return false;

      const phaseId = this.journeyStructure.phases[phaseIndex].id;

      // Initialize phase and milestone objects if they don't exist
      if (!customer.journeyData[phaseId]) {
        customer.journeyData[phaseId] = {};
      }

      if (!customer.journeyData[phaseId][milestoneId]) {
        customer.journeyData[phaseId][milestoneId] = {};
      }

      // Update task status
      if (checkbox.checked) {
        customer.journeyData[phaseId][milestoneId][taskId] = true;
      } else {
        delete customer.journeyData[phaseId][milestoneId][taskId];

        // Clean up empty objects
        if (
          Object.keys(customer.journeyData[phaseId][milestoneId]).length === 0
        ) {
          delete customer.journeyData[phaseId][milestoneId];
        }

        if (Object.keys(customer.journeyData[phaseId]).length === 0) {
          delete customer.journeyData[phaseId];
        }
      }

      // Update customer's last modified timestamp
      customer.lastUpdated = new Date().toISOString();

      // Save changes
      window.NovAssistant.CustomerManagement.saveCustomers();

      // Update milestone checkbox state
      this.updateMilestoneCheckboxes();

      // Update progress bar and status
      this.updateCustomerJourney(customer);

      return true;
    } catch (error) {
      AppLogger.error("Error handling task check change", error, {
        checkboxId: checkbox?.id || 'unknown',
        customerIndex: window.NovAssistant.state.currentCustomerIndex || 'unknown'
      });
      return false;
    }
  },

  // Initialize journey flow visualization
  initJourneyFlow: function() {
    try {
      const journeyContainer = document.querySelector('.journey-flow-chart');
      if (!journeyContainer) return false;

      // Clear existing content
      journeyContainer.innerHTML = '';

      // Build journey flow HTML
      this.journeyStructure.phases.forEach((phase) => {
        // Create phase container
        const phaseDiv = document.createElement('div');
        phaseDiv.className = 'journey-phase';

        // Phase header
        phaseDiv.innerHTML = `
          <div class="phase-header">
            <div class="phase-icon">
              <i class="${phase.icon}"></i>
            </div>
            <h3 class="phase-title">${phase.name}</h3>
            <span class="phase-status not-started">Not Started</span>
          </div>
        `;

        // Create milestones
        const milestoneGroup = document.createElement('div');
        milestoneGroup.className = 'milestone-group';

        phase.milestones.forEach((milestone) => {
          const milestoneDiv = document.createElement('div');
          milestoneDiv.className = 'milestone';
          milestoneDiv.setAttribute('data-milestone', milestone.id);

          // Milestone header
          milestoneDiv.innerHTML = `
            <div class="milestone-header" onclick="window.NovAssistant.JourneyTracker.toggleMilestoneDetails(this)">
              <input type="checkbox" class="milestone-check" disabled>
              <h4 class="milestone-title">${milestone.name}</h4>
              <span class="milestone-expand">
                <i class="fas fa-chevron-down"></i>
              </span>
            </div>
            <div class="milestone-details">
              <p>${milestone.description}</p>
              <ul class="task-list">
          `;

          // Add tasks
          milestone.tasks.forEach((task) => {
            milestoneDiv.querySelector('.task-list').innerHTML += `
              <li class="task">
                <input type="checkbox" class="task-check" id="${task.id}" onchange="window.NovAssistant.JourneyTracker.handleTaskCheckChange(this)">
                <label for="${task.id}">${task.name}</label>
              </li>
            `;
          });

          // Close milestone details
          milestoneDiv.querySelector('.milestone-details').innerHTML += `
              </ul>
              <div class="milestone-actions">
                <button class="action-btn" onclick="window.NovAssistant.JourneyTracker.addTaskNote('${phase.id}', '${milestone.id}')">
                  <i class="fas fa-sticky-note"></i> Add Note
                </button>
                <button class="action-btn" onclick="window.NovAssistant.JourneyTracker.markAllTasksComplete('${phase.id}', '${milestone.id}')">
                  <i class="fas fa-check-double"></i> Mark All Complete
                </button>
              </div>
            </div>
          `;

          milestoneGroup.appendChild(milestoneDiv);
        });

        phaseDiv.appendChild(milestoneGroup);
        journeyContainer.appendChild(phaseDiv);
      });

      return true;
    } catch (error) {
      AppLogger.error("Error initializing journey flow", error);
      return false;
    }
  },

  // Toggle milestone details visibility
  toggleMilestoneDetails: function(headerElement) {
    try {
      const milestone = headerElement.closest(".milestone");
      if (!milestone) return false;

      milestone.classList.toggle("expanded");

      return true;
    } catch (error) {
      AppLogger.error("Error toggling milestone details", error);
      return false;
    }
  },

  // Mark all tasks in a milestone as complete
  markAllTasksComplete: function(phaseId, milestoneId) {
    try {
      // Get current customer
      const customerIndex = window.NovAssistant.state.currentCustomerIndex;
      if (customerIndex === null) return false;

      const customers = window.NovAssistant.CustomerManagement.getCustomers();
      const customer = customers[customerIndex];
      if (!customer) return false;

      // Find the milestone in the DOM
      const milestone = document.querySelector(`.milestone[data-milestone="${milestoneId}"]`);
      if (!milestone) return false;

      // Initialize journey data if needed
      if (!customer.journeyData) {
        customer.journeyData = {};
      }

      if (!customer.journeyData[phaseId]) {
        customer.journeyData[phaseId] = {};
      }

      if (!customer.journeyData[phaseId][milestoneId]) {
        customer.journeyData[phaseId][milestoneId] = {};
      }

      // Get all task checkboxes
      const taskCheckboxes = milestone.querySelectorAll('.task-check');

      // Mark all tasks as complete
      taskCheckboxes.forEach((checkbox) => {
        const taskId = checkbox.id;
        checkbox.checked = true;
        customer.journeyData[phaseId][milestoneId][taskId] = true;
      });

      // Update customer's last modified timestamp
      customer.lastUpdated = new Date().toISOString();

      // Save changes
      window.NovAssistant.CustomerManagement.saveCustomers();

      // Update milestone checkbox and progress
      this.updateMilestoneCheckboxes();
      this.updateCustomerJourney(customer);

      // Show success message
      window.NovAssistant.ui.showToast("All tasks marked as complete", "success");

      return true;
    } catch (error) {
      AppLogger.error("Error marking all tasks complete", error, {
        phaseId: phaseId,
        milestoneId: milestoneId
      });
      return false;
    }
  },

  // Add a note to a task
  addTaskNote: function(phaseId, milestoneId) {
    try {
      // Get current customer
      const customerIndex = window.NovAssistant.state.currentCustomerIndex;
      if (customerIndex === null) return false;

      const customers = window.NovAssistant.CustomerManagement.getCustomers();
      const customer = customers[customerIndex];
      if (!customer) return false;

      // Get milestone name
      let milestoneName = "";
      this.journeyStructure.phases.forEach((phase) => {
        if (phase.id === phaseId) {
          phase.milestones.forEach((milestone) => {
            if (milestone.id === milestoneId) {
              milestoneName = milestone.name;
            }
          });
        }
      });

      // Prompt for note content
      const noteContent = prompt(`Add note for "${milestoneName}":`, "");
      if (!noteContent) return false;

      // Initialize notes array if needed
      if (!customer.notes) {
        customer.notes = [];
      }

      // Add note
      customer.notes.push({
        id: Date.now(),
        content: `[${milestoneName}] ${noteContent}`,
        timestamp: new Date().toISOString(),
        type: "journey",
        phaseId: phaseId,
        milestoneId: milestoneId
      });

      // Update customer data
      customer.lastUpdated = new Date().toISOString();
      window.NovAssistant.CustomerManagement.saveCustomers();

      // Refresh notes if available
      if (typeof window.refreshNotesUI === 'function') {
        window.refreshNotesUI(customer);
      }

      // Show success message
      window.NovAssistant.ui.showToast("Note added successfully", "success");

      return true;
    } catch (error) {
      AppLogger.error("Error adding task note", error, {
        phaseId: phaseId,
        milestoneId: milestoneId
      });
      return false;
    }
  },

  // Get the current active phase for a customer
  getActivePhase: function(customer) {
    try {
      if (!customer || !customer.journeyData) return null;

      // Check each phase in order
      for (const phase of this.journeyStructure.phases) {
        let isActive = false;
        let isCompleted = true;

        // Check milestones in this phase
        for (const milestone of phase.milestones) {
          let hasStarted = false;
          let allTasksComplete = true;

          // Check tasks in this milestone
          for (const task of milestone.tasks) {
            const isTaskComplete = !!(
              customer.journeyData[phase.id] &&
              customer.journeyData[phase.id][milestone.id] &&
              customer.journeyData[phase.id][milestone.id][task.id]
            );

            if (isTaskComplete) {
              hasStarted = true;
            } else {
              allTasksComplete = false;
            }
          }

          if (hasStarted && !allTasksComplete) {
            isActive = true;
          }

          if (!allTasksComplete) {
            isCompleted = false;
          }
        }

        // If phase has started but is not completed, it's the active phase
        if (isActive || !isCompleted) {
          return phase;
        }
      }

      // If all phases are complete or no phase is active, return the last phase
      if (this.isJourneyComplete(customer)) {
        return this.journeyStructure.phases[this.journeyStructure.phases.length - 1];
      }

      // If no active phase found, return the first phase
      return this.journeyStructure.phases[0];
    } catch (error) {
      AppLogger.error("Error getting active phase", error, {
        customerId: customer ? customer.id : 'unknown'
      });
      return null;
    }
  },

  // Check if the journey is complete
  isJourneyComplete: function(customer) {
    try {
      return this.calculateJourneyProgress(customer) === 100;
    } catch (error) {
      AppLogger.error("Error checking if journey is complete", error, {
        customerId: customer ? customer.id : 'unknown'
      });
      return false;
    }
  },

  // Generate next steps for customer journey
  generateNextSteps: function(customer) {
    try {
      if (!customer || !customer.journeyData) {
        return ["Begin by confirming customer details and explaining novated leasing"];
      }

      const nextSteps = [];

      // Get active phase
      const activePhase = this.getActivePhase(customer);
      if (!activePhase) {
        return ["Begin initial consultation phase"];
      }

      // Check each milestone in the active phase
      for (const milestone of activePhase.milestones) {
        let milestoneTasks = [];

        // Check for incomplete tasks
        for (const task of milestone.tasks) {
          const isTaskComplete = !!(
            customer.journeyData[activePhase.id] &&
            customer.journeyData[activePhase.id][milestone.id] &&
            customer.journeyData[activePhase.id][milestone.id][task.id]
          );

          if (!isTaskComplete) {
            milestoneTasks.push(`${task.name} - ${task.description}`);
          }
        }

        // If there are incomplete tasks, add them to next steps
        if (milestoneTasks.length > 0) {
          nextSteps.push(`Complete ${milestone.name}: ${milestoneTasks[0]}`);

          // Add up to 2 more tasks if available
          if (milestoneTasks.length > 1) {
            nextSteps.push(milestoneTasks[1]);
          }

          break; // Only show tasks for the first incomplete milestone
        }
      }

      // If active phase is complete, suggest starting the next phase
      if (nextSteps.length === 0) {
        const currentIndex = this.journeyStructure.phases.indexOf(activePhase);
        if (currentIndex < this.journeyStructure.phases.length - 1) {
          const nextPhase = this.journeyStructure.phases[currentIndex + 1];
          nextSteps.push(`Begin ${nextPhase.name} phase`);

          // Add first task of first milestone in next phase
          const firstMilestone = nextPhase.milestones[0];
          const firstTask = firstMilestone.tasks[0];
          nextSteps.push(`${firstTask.name} - ${firstTask.description}`);
        } else {
          nextSteps.push("Journey complete! Follow up with customer for feedback");
        }
      }

      return nextSteps;
    } catch (error) {
      AppLogger.error("Error generating next steps", error, {
        customerId: customer ? customer.id : 'unknown'
      });
      return ["Error generating next steps"];
    }
  },

  // Export journey data to CSV
  exportJourneyToCSV: function(customer) {
    try {
      if (!customer) {
        window.NovAssistant.ui.showToast("No customer selected", "error");
        return false;
      }

      // Build CSV content
      let csvContent = "Phase,Milestone,Task,Status,Completion Date\n";

      // Process each phase, milestone, and task
      this.journeyStructure.phases.forEach((phase) => {
        phase.milestones.forEach((milestone) => {
          milestone.tasks.forEach((task) => {
            // Determine task status
            let status = "Not Started";
            let completionDate = "";

            if (
              customer.journeyData &&
              customer.journeyData[phase.id] &&
              customer.journeyData[phase.id][milestone.id] &&
              customer.journeyData[phase.id][milestone.id][task.id]
            ) {
              status = "Completed";

              // If task completion date is available, use it
              if (
                customer.journeyData[phase.id] &&
                customer.journeyData[phase.id][milestone.id] &&
                customer.journeyData[phase.id][milestone.id][`${task.id}_date`]
              ) {
                completionDate = customer.journeyData[phase.id][milestone.id][`${task.id}_date`];
              } else {
                // Default to last updated date
                completionDate = customer.lastUpdated ? new Date(customer.lastUpdated).toLocaleDateString() : "";
              }
            }

            // Escape any commas in text fields
            const escapedPhase = phase.name.includes(',') ? `"${phase.name}"` : phase.name;
            const escapedMilestone = milestone.name.includes(',') ? `"${milestone.name}"` : milestone.name;
            const escapedTask = task.name.includes(',') ? `"${task.name}"` : task.name;

            // Add row to CSV
            csvContent += `${escapedPhase},${escapedMilestone},${escapedTask},${status},${completionDate}\n`;
          });
        });
      });

      // In a real implementation, this would trigger a download
      // For now, we'll just copy to clipboard
      navigator.clipboard.writeText(csvContent)
        .then(() => {
          window.NovAssistant.ui.showToast("Journey data copied to clipboard", "success");
        })
        .catch((error) => {
          throw error;
        });

      return true;
    } catch (error) {
      AppLogger.error("Error exporting journey to CSV", error, {
        customerId: customer ? customer.id : 'unknown'
      });
      window.NovAssistant.ui.showToast("Error exporting journey data", "error");
      return false;
    }
  }
};

/**
 * Enhanced Phone Script System
