  --border-color: #e0e0e0;
  --shadow-color: rgba(0, 0, 0, 0.1);
  --shadow-color-darker: rgba(0, 0, 0, 0.2);
  --error-color: #f44336;
  --warning-color: #ff9800;
  --success-color: #4caf50;
  --info-color: #2196f3;

  /* Status colors */
  --status-green: #5cd660;
  --status-orange: #ffa500;
  --status-red: #ff5252;

  /* Typography */
  --title-font: "Montserrat", sans-serif;
  --body-font: "Open Sans", Arial, sans-serif;

  /* Spacing */
  --spacing-xs: 0.25rem;
  --spacing-sm: 0.5rem;
  --spacing-md: 1rem;
  --spacing-lg: 1.5rem;
  --spacing-xl: 2rem;

  /* Border radius */
  --border-radius-sm: 4px;
  --border-radius-md: 8px;
  --border-radius-lg: 20px;
  --border-radius-circle: 50%;
}

body {
  font-family: var(--body-font);
  background-color: var(--background-color);
  color: var(--text-color);
  line-height: 1.6;
  transition: background-color 0.3s ease;
}

/* Accessibility focus outline */
:focus {
  outline: 2px solid var(--primary-color);
  outline-offset: 2px;
}

button:focus,
input:focus,
select:focus,
textarea:focus {
  box-shadow: 0 0 0 2px rgba(0, 66, 37, 0.3);
}

.sr-only {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

