import React, { useState } from 'react';

const ProductRecommendationTool = () => {
  const [customerData, setCustomerData] = useState({
    // Basic information
    vehicleValue: 50000,
    vehicleType: 'sedan',
    hasChildren: false,
    primaryUse: 'commuting',
    annualKilometers: 20000,
    parkingLocation: 'covered',
    employmentSector: 'private',
    riskAversion: 'moderate',
    hasTestDriven: 'unknown',
    employerHasSGFleet: 'unknown'
  });

  const [recommendations, setRecommendations] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [showScripts, setShowScripts] = useState(false);
  const [currentScript, setCurrentScript] = useState("");
  const [activeTab, setActiveTab] = useState("qualification");

  // Scripts for sales talking points
  const scripts = {
    motorInsurance: "Our Motor Insurance provides full lease payout coverage. With standard insurance, if your car is written off, they only pay market value, potentially leaving you thousands out of pocket if your lease payout is higher. Our policy covers the full lease amount, plus offers an open driver policy, one free windscreen replacement annually, and more.",

    leaseGuard: "LeaseGuard protects you against involuntary redundancy by covering your lease payments for up to 11 months (up to $15,000). Alternatively, after three monthly payments, you could choose to hand back the vehicle with no further obligation.",

    initialQualification: "Hi [Name], it's [Your Name] from SG Fleet. I understand you're interested in a novated lease. Have you had a novated lease before, or can I give you a quick overview of how they work?",

    taxAdvantages: "With novated leasing, running costs like fuel and maintenance come from your pre-tax salary, giving you a discount equal to your marginal tax rate. You also save the full 10% GST on both the purchase price and all running costs throughout the lease."
  };

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setCustomerData({
      ...customerData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  // Show script
  const showScript = (scriptName) => {
    setCurrentScript(scriptName);
    setShowScripts(true);
  };

  // Generate recommendations
  const generateRecommendations = () => {
    setIsAnalyzing(true);

    setTimeout(() => {
      const core = [];
      const recommended = [];
      const optional = [];

      // Motor Insurance is always core
      core.push({
        id: 'motorInsurance',
        name: "Motor Insurance",
        monthlyEst: Math.round(customerData.vehicleValue * 0.0035),
        reason: 'Essential protection with full lease payout coverage',
        scriptKey: 'motorInsurance',
        benefits: [
          "Full lease payout - no gap",
          "Open driver policy",
          "Free windscreen replacement"
        ],
        risks: [
          "Gap between insurance payout and lease value",
          "Higher excess with standard insurance",
          "Limited coverage for accessories"
        ]
      });

      // LeaseGuard based on employment sector
      if (customerData.employmentSector === 'private') {
        recommended.push({
          id: 'leaseGuard',
          name: "LeaseGuard",
          monthlyEst: 80,
          reason: 'Recommended for private sector employees',
          scriptKey: 'leaseGuard',
          benefits: [
            "Payments covered for up to 11 months",
            "Option to return vehicle if unemployed",
            "Peace of mind during uncertain times"
          ],
          risks: [
            "Full financial responsibility if made redundant",
            "Potential impact on credit rating if payments missed",
            "Stress during employment transition"
          ]
        });
      } else {
        optional.push({
          id: 'leaseGuard',
          name: "LeaseGuard",
          monthlyEst: 80,
          reason: 'Optional protection against employment changes',
          scriptKey: 'leaseGuard',
          benefits: [
            "Payments covered for up to 11 months",
            "Option to return vehicle if unemployed",
            "Peace of mind during uncertain times"
          ],
          risks: [
            "Full financial responsibility if made redundant",
            "Potential impact on credit rating if payments missed",
            "Stress during employment transition"
          ]
        });
      }

      // Window tint based on having children
      if (customerData.hasChildren) {
        recommended.push({
          id: 'windowTint',
          name: "Window Tint",
          oneTimeEst: 450,
          monthlyEst: Math.round(450 / 36),
          reason: 'Recommended for families with children',
          benefits: [
            "Blocks up to 99% of UV rays",
            "Reduces heat and glare by up to 60%",
            "Increased privacy"
          ],
          risks: [
            "Interior damage from UV exposure",
            "Uncomfortable cabin temperatures",
            "Higher air conditioning usage"
          ]
        });
      } else {
        optional.push({
          id: 'windowTint',
          name: "Window Tint",
          oneTimeEst: 450,
          monthlyEst: Math.round(450 / 36),
          reason: 'Optional UV and heat protection',
          benefits: [
            "Blocks up to 99% of UV rays",
            "Reduces heat and glare by up to 60%",
            "Increased privacy"
          ],
          risks: [
            "Interior damage from UV exposure",
            "Uncomfortable cabin temperatures",
            "Higher air conditioning usage"
          ]
        });
      }

      // Exterior protection based on parking location
      if (customerData.parkingLocation === 'street') {
        recommended.push({
          id: 'exteriorProtection',
          name: "Exterior Protection",
          oneTimeEst: 795,
          monthlyEst: Math.round(795 / 36),
          reason: 'Recommended for street parking exposure',
          benefits: [
            "Protection against environmental damage",
            "Super gloss finish",
            "Easier cleaning"
          ],
          risks: [
            "Paint damage and fading",
            "Permanent staining",
            "Reduced resale value"
          ]
        });
      } else {
        optional.push({
          id: 'exteriorProtection',
          name: "Exterior Protection",
          oneTimeEst: 795,
          monthlyEst: Math.round(795 / 36),
          reason: 'Optional paint protection',
          benefits: [
            "Protection against environmental damage",
            "Super gloss finish",
            "Easier cleaning"
          ],
          risks: [
            "Paint damage and fading",
            "Permanent staining",
            "Reduced resale value"
          ]
        });
      }

      setRecommendations({
        core,
        recommended,
        optional
      });

      setIsAnalyzing(false);
    }, 1000);
  };

  // Calculate total costs
  const calculateTotals = (products) => {
    if (!products) return { monthly: 0, oneTime: 0 };

    return products.reduce(
      (acc, product) => ({
        monthly: acc.monthly + product.monthlyEst,
        oneTime: acc.oneTime + (product.oneTimeEst || 0)
      }),
      { monthly: 0, oneTime: 0 }
    );
  };

  return (
    <div className="max-w-6xl mx-auto p-4 bg-gray-50 rounded-lg shadow">
      <h1 className="text-2xl font-bold mb-4 text-center">Novated Lease Consultant Tool</h1>

      {/* Tabs */}
      <div className="mb-6 border-b">
        <div className="flex">
          <button 
            className={`py-2 px-4 ${activeTab === 'qualification' ? 'bg-blue-100 border-b-2 border-blue-500' : ''}`}
            onClick={() => setActiveTab('qualification')}
          >
            Qualification
          </button>
          <button 
            className={`py-2 px-4 ${activeTab === 'recommendations' ? 'bg-blue-100 border-b-2 border-blue-500' : ''}`}
            onClick={() => {
              if (!recommendations) {
                generateRecommendations();
              }
              setActiveTab('recommendations');
            }}
          >
            Recommendations
          </button>
          <button 
            className={`py-2 px-4 ${activeTab === 'scripts' ? 'bg-blue-100 border-b-2 border-blue-500' : ''}`}
            onClick={() => setActiveTab('scripts')}
          >
            Sales Scripts
          </button>
        </div>
      </div>

      {/* Qualification Form */}
      {activeTab === 'qualification' && (
        <div className="bg-white p-6 rounded-lg shadow mb-6">
          <h2 className="text-xl font-semibold mb-4">Customer Qualification</h2>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium mb-1">Is SG Fleet the employer's preferred provider?</label>
              <select
                name="employerHasSGFleet"
                value={customerData.employerHasSGFleet}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
              >
                <option value="unknown">Unknown</option>
                <option value="yes">Yes</option>
                <option value="no">No</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">Has customer test driven?</label>
              <select
                name="hasTestDriven"
                value={customerData.hasTestDriven}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
              >
                <option value="unknown">Unknown</option>
                <option value="yes">Yes</option>
                <option value="no">No</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">Vehicle Value: ${customerData.vehicleValue.toLocaleString()}</label>
              <input
                type="range"
                name="vehicleValue"
                min="20000"
                max="150000"
                step="5000"
                value={customerData.vehicleValue}
                onChange={handleInputChange}
                className="w-full"
              />
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">Vehicle Type</label>
              <select
                name="vehicleType"
                value={customerData.vehicleType}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
              >
                <option value="sedan">Sedan</option>
                <option value="suv">SUV</option>
                <option value="wagon">Wagon</option>
                <option value="utility">Utility</option>
                <option value="hybrid">Hybrid</option>
                <option value="electric">Electric</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">Annual Kilometers</label>
              <select
                name="annualKilometers"
                value={customerData.annualKilometers}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
              >
                <option value="10000">Up to 10,000</option>
                <option value="15000">10,001 - 15,000</option>
                <option value="20000">15,001 - 20,000</option>
                <option value="25000">20,001 - 25,000</option>
                <option value="30000">Over 25,000</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">Primary Use</label>
              <select
                name="primaryUse"
                value={customerData.primaryUse}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
              >
                <option value="commuting">Commuting</option>
                <option value="family">Family</option>
                <option value="business">Business</option>
                <option value="weekend">Weekend/Recreational</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">Parking Location</label>
              <select
                name="parkingLocation"
                value={customerData.parkingLocation}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
              >
                <option value="garage">Garage</option>
                <option value="covered">Covered Carport</option>
                <option value="street">Street Parking</option>
                <option value="outdoor">Outdoor Uncovered</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">Employment Sector</label>
              <select
                name="employmentSector"
                value={customerData.employmentSector}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
              >
                <option value="private">Private</option>
                <option value="government">Government</option>
                <option value="nonprofit">Non-profit</option>
                <option value="contract">Contract/Temporary</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">Risk Aversion</label>
              <select
                name="riskAversion"
                value={customerData.riskAversion}
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
              >
                <option value="low">Low (Comfortable with risk)</option>
                <option value="moderate">Moderate</option>
                <option value="high">High (Risk averse)</option>
              </select>
            </div>

            <div className="flex items-center">
              <input
                type="checkbox"
                id="hasChildren"
                name="hasChildren"
                checked={customerData.hasChildren}
                onChange={handleInputChange}
                className="h-4 w-4 text-blue-600"
              />
              <label htmlFor="hasChildren" className="ml-2 block text-sm">Has Children</label>
            </div>
          </div>

          <div className="mt-6">
            <button
              onClick={() => {
                generateRecommendations();
                setActiveTab('recommendations');
              }}
              disabled={isAnalyzing}
              className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 disabled:bg-blue-300"
            >
              {isAnalyzing ? "Analyzing..." : "Generate Recommendations"}
            </button>
          </div>
        </div>
      )}

      {/* Recommendations Display */}
      {activeTab === 'recommendations' && recommendations && (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Core Products */}
          <div className="bg-white p-6 rounded-lg shadow">
            <h3 className="text-lg font-semibold mb-4 text-blue-800">Essential Products</h3>
            {recommendations.core.map(product => (
              <div key={product.id} className="mb-4 pb-3 border-b">
                <div className="flex justify-between items-start">
                  <div>
                    <h4 className="font-semibold">{product.name}</h4>
                    <p className="text-xs text-gray-600">{product.reason}</p>
                  </div>
                  <p className="font-medium">${product.monthlyEst}/month</p>
                </div>
                <div className="mt-2">
                  <p className="text-xs font-medium text-blue-600 mb-1">Key Benefits:</p>
                  <ul className="text-xs list-disc pl-4">
                    {product.benefits.map((benefit, i) => (
                      <li key={i}>{benefit}</li>
                    ))}
                  </ul>
                </div>
                {product.scriptKey && (
                  <button 
                    onClick={() => showScript(product.scriptKey)}
                    className="mt-2 text-xs text-blue-600 hover:underline"
                  >
                    View Sales Script
                  </button>
                )}
              </div>
            ))}
            <div className="mt-3 pt-2 border-t">
              <div className="flex justify-between items-center">
                <span className="font-medium">Monthly Total:</span>
                <span className="font-bold">${calculateTotals(recommendations.core).monthly}/month</span>
              </div>
            </div>
          </div>

          {/* Recommended Products */}
          <div className="bg-white p-6 rounded-lg shadow">
            <h3 className="text-lg font-semibold mb-4 text-green-700">Recommended Add-ons</h3>
            {recommendations.recommended.map(product => (
              <div key={product.id} className="mb-4 pb-3 border-b">
                <div className="flex justify-between items-start">
                  <div>
                    <h4 className="font-semibold">{product.name}</h4>
                    <p className="text-xs text-gray-600">{product.reason}</p>
                  </div>
                  <div className="text-right">
                    {product.oneTimeEst && (
                      <p className="text-xs text-gray-500">One-time: ${product.oneTimeEst}</p>
                    )}
                    <p className="font-medium">${product.monthlyEst}/month</p>
                  </div>
                </div>
                <div className="mt-2">
                  <p className="text-xs font-medium text-green-600 mb-1">Key Benefits:</p>
                  <ul className="text-xs list-disc pl-4">
                    {product.benefits.map((benefit, i) => (
                      <li key={i}>{benefit}</li>
                    ))}
                  </ul>
                </div>
                {product.scriptKey && (
                  <button 
                    onClick={() => showScript(product.scriptKey)}
                    className="mt-2 text-xs text-blue-600 hover:underline"
                  >
                    View Sales Script
                  </button>
                )}
              </div>
            ))}
            <div className="mt-3 pt-2 border-t">
              <div className="flex justify-between items-center">
                <span className="font-medium">Monthly Total:</span>
                <span className="font-bold">${calculateTotals(recommendations.recommended).monthly}/month</span>
              </div>
              {calculateTotals(recommendations.recommended).oneTime > 0 && (
                <div className="flex justify-between items-center text-sm text-gray-600">
                  <span>One-time costs:</span>
                  <span>${calculateTotals(recommendations.recommended).oneTime}</span>
                </div>
              )}
            </div>
          </div>

          {/* Optional Products */}
          <div className="bg-white p-6 rounded-lg shadow">
            <h3 className="text-lg font-semibold mb-4 text-gray-700">Optional Extras</h3>
            {recommendations.optional.map(product => (
              <div key={product.id} className="mb-4 pb-3 border-b">
                <div className="flex justify-between items-start">
                  <div>
                    <h4 className="font-semibold">{product.name}</h4>
                    <p className="text-xs text-gray-600">{product.reason}</p>
                  </div>
                  <div className="text-right">
                    {product.oneTimeEst && (
                      <p className="text-xs text-gray-500">One-time: ${product.oneTimeEst}</p>
                    )}
                    <p className="font-medium">${product.monthlyEst}/month</p>
                  </div>
                </div>
                <div className="mt-2">
                  <div className="flex justify-between">
                    <p className="text-xs font-medium text-red-600 mb-1">Without this protection:</p>
                  </div>
                  <ul className="text-xs list-disc pl-4 text-red-600">
                    {product.risks.map((risk, i) => (
                      <li key={i}>{risk}</li>
                    ))}
                  </ul>
                </div>
                {product.scriptKey && (
                  <button 
                    onClick={() => showScript(product.scriptKey)}
                    className="mt-2 text-xs text-blue-600 hover:underline"
                  >
                    View Sales Script
                  </button>
                )}
              </div>
            ))}
          </div>

          {/* Summary Section */}
          <div className="lg:col-span-3 bg-blue-50 p-6 rounded-lg shadow">
            <h3 className="text-xl font-semibold mb-4">Package Summary</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="bg-white p-4 rounded shadow">
                <h4 className="font-medium text-blue-800">Essential Package</h4>
                <p className="text-2xl font-bold mt-2">${calculateTotals(recommendations.core).monthly}/month</p>
                <p className="text-sm text-gray-600 mt-1">Basic protection for your lease</p>
              </div>

              <div className="bg-white p-4 rounded shadow">
                <h4 className="font-medium text-green-700">Enhanced Package</h4>
                <p className="text-2xl font-bold mt-2">
                  ${calculateTotals(recommendations.core).monthly + calculateTotals(recommendations.recommended).monthly}/month
                </p>
                <p className="text-sm text-gray-600 mt-1">Recommended for your specific needs</p>
              </div>

              <div className="bg-white p-4 rounded shadow">
                <h4 className="font-medium text-purple-700">Complete Package</h4>
                <p className="text-2xl font-bold mt-2">
                  ${calculateTotals(recommendations.core).monthly + 
                    calculateTotals(recommendations.recommended).monthly + 
                    calculateTotals(recommendations.optional).monthly}/month
                </p>
                <p className="text-sm text-gray-600 mt-1">Comprehensive protection</p>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Sales Scripts Tab */}
