// services/calculationService.js

/**
 * Calculate the novated lease details and savings
 * @param {Object} data - The form data
 * @returns {Object} The calculation results
 */
export const calculateLease = (data) => {
  const {
    vehiclePrice,
    term,
    interestRate,
    salary,
    taxRate,
    fbtRate,
    annualKilometers
  } = data;

  // Step 1: Calculate monthly loan payment using amortization formula
  const monthlyInterestRate = interestRate / 100 / 12;
  const loanPayment = vehiclePrice * monthlyInterestRate * Math.pow(1 + monthlyInterestRate, term) / 
                     (Math.pow(1 + monthlyInterestRate, term) - 1);

  // Step 2: Calculate running costs (simplified estimation)
  const fuelCostPerKm = 0.15; // Estimated fuel cost per kilometer
  const serviceCostPerYear = 800; // Estimated annual service cost
  const registrationPerYear = 900; // Estimated annual registration cost
  const insurancePerYear = 1200; // Estimated annual insurance cost

  const monthlyFuelCost = (annualKilometers * fuelCostPerKm) / 12;
  const monthlyServiceCost = serviceCostPerYear / 12;
  const monthlyRegistrationCost = registrationPerYear / 12;
  const monthlyInsuranceCost = insurancePerYear / 12;

  // Step 3: Calculate total monthly lease cost
  const totalMonthlyCost = loanPayment + monthlyFuelCost + monthlyServiceCost + 
                          monthlyRegistrationCost + monthlyInsuranceCost;

  // Step 4: Calculate FBT (Fringe Benefits Tax)
  // Simplified FBT calculation for novated lease
  const statutoryPercentage = calculateStatutoryPercentage(annualKilometers);
  const fbtTaxableValue = vehiclePrice * statutoryPercentage / 100;
  const annualFBT = fbtTaxableValue * (fbtRate / 100);
  const monthlyFBT = annualFBT / 12;

  // Step 5: Calculate tax savings
  const monthlySalaryBeforeTax = salary / 12;
  const monthlyTaxWithoutLease = calculateMonthlyTax(monthlySalaryBeforeTax, taxRate);

  const monthlySalaryAfterLease = monthlySalaryBeforeTax - totalMonthlyCost;
  const monthlyTaxWithLease = calculateMonthlyTax(monthlySalaryAfterLease, taxRate);

  const monthlyTaxSavings = monthlyTaxWithoutLease - monthlyTaxWithLease;

  // Step 6: Calculate net cost after tax
  const netMonthlyCost = totalMonthlyCost - monthlyTaxSavings + monthlyFBT;

  // Calculate traditional purchase costs for comparison
  const traditionalMonthlyCost = calculateTraditionalCost(data);

  // Calculate total savings over lease term
  const totalSavingsOverTerm = (traditionalMonthlyCost - netMonthlyCost) * term;

  return {
    vehiclePrice,
    term,
    interestRate,
    salary,
    taxRate,

    // Detailed costs
    monthlyLoanPayment: loanPayment,
    monthlyRunningCosts: {
      fuel: monthlyFuelCost,
      service: monthlyServiceCost,
      registration: monthlyRegistrationCost,
      insurance: monthlyInsuranceCost
    },
    totalMonthlyCost,
    monthlyFBT,
    monthlyTaxSavings,
    netMonthlyCost,

    // Comparison
    traditionalMonthlyCost,
    monthlySavings: traditionalMonthlyCost - netMonthlyCost,
    totalSavingsOverTerm,

    // Additional info
    statutoryPercentage,
    takeHomePay: {
      beforeLease: monthlySalaryBeforeTax - monthlyTaxWithoutLease,
      afterLease: monthlySalaryAfterLease - monthlyTaxWithLease
    }
  };
};

/**
 * Calculate the statutory percentage based on annual kilometers
 * @param {number} annualKm - Annual kilometers
 * @returns {number} The statutory percentage
 */
const calculateStatutoryPercentage = (annualKm) => {
  if (annualKm < 15000) return 26;
  if (annualKm < 25000) return 20;
  if (annualKm < 40000) return 11;
  return 7;
};

/**
 * Calculate monthly tax based on monthly income and tax rate
 * @param {number} monthlyIncome - Monthly income
 * @param {number} taxRate - Tax rate percentage
 * @returns {number} Monthly tax amount
 */
const calculateMonthlyTax = (monthlyIncome, taxRate) => {
  return monthlyIncome * (taxRate / 100);
};

/**
 * Calculate traditional purchase cost for comparison
 * @param {Object} data - The form data
 * @returns {number} Monthly cost with traditional purchase
 */
const calculateTraditionalCost = (data) => {
  const {
    vehiclePrice,
    term,
    interestRate,
    annualKilometers
  } = data;

  // Calculate car loan with higher interest rate (typically 1-2% higher)
  const traditionalInterestRate = interestRate + 1.5;
  const monthlyInterestRate = traditionalInterestRate / 100 / 12;
  const loanPayment = vehiclePrice * monthlyInterestRate * Math.pow(1 + monthlyInterestRate, term) / 
                     (Math.pow(1 + monthlyInterestRate, term) - 1);

  // Running costs (same as novated lease calculation)
  const fuelCostPerKm = 0.15;
  const serviceCostPerYear = 800;
  const registrationPerYear = 900;
  const insurancePerYear = 1200;

  const monthlyFuelCost = (annualKilometers * fuelCostPerKm) / 12;
  const monthlyServiceCost = serviceCostPerYear / 12;
  const monthlyRegistrationCost = registrationPerYear / 12;
  const monthlyInsuranceCost = insurancePerYear / 12;

  // With traditional purchase, you pay with after-tax dollars
  return loanPayment + monthlyFuelCost + monthlyServiceCost + 
         monthlyRegistrationCost + monthlyInsuranceCost;
};

