// components/Calculator/Calculator.js
import React, { useState } from 'react';
import { calculateLease } from '../../services/calculationService';
import './Calculator.css';

const Calculator = ({ onCalculate }) => {
  const [formData, setFormData] = useState({
    vehiclePrice: '',
    term: 60, // Default 5 years (60 months)
    interestRate: 7.5,
    salary: '',
    taxRate: 32.5, // Default tax rate
    fbtRate: 47, // Default FBT rate
    annualKilometers: 15000,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: name === 'term' || name === 'interestRate' || name === 'taxRate' || 
              name === 'fbtRate' || name === 'annualKilometers' ? 
              parseFloat(value) : value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Convert string inputs to numbers
    const numericFormData = {
      ...formData,
      vehiclePrice: parseFloat(formData.vehiclePrice),
      salary: parseFloat(formData.salary),
    };

    const results = calculateLease(numericFormData);
    onCalculate(results);
  };

  return (
    <div className="calculator-container">
      <h2>Novated Lease Calculator</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="vehiclePrice">Vehicle Price ($)</label>
          <input
            type="number"
            id="vehiclePrice"
            name="vehiclePrice"
            value={formData.vehiclePrice}
            onChange={handleChange}
            required
            min="0"
            step="100"
          />
        </div>

        <div className="form-group">
          <label htmlFor="term">Lease Term (months)</label>
          <select
            id="term"
            name="term"
            value={formData.term}
            onChange={handleChange}
            required
          >
            <option value="12">12 months (1 year)</option>
            <option value="24">24 months (2 years)</option>
            <option value="36">36 months (3 years)</option>
            <option value="48">48 months (4 years)</option>
            <option value="60">60 months (5 years)</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="interestRate">Interest Rate (%)</label>
          <input
            type="number"
            id="interestRate"
            name="interestRate"
            value={formData.interestRate}
            onChange={handleChange}
            required
            min="0"
            max="20"
            step="0.01"
          />
        </div>

        <div className="form-group">
          <label htmlFor="salary">Annual Salary ($)</label>
          <input
            type="number"
            id="salary"
            name="salary"
            value={formData.salary}
            onChange={handleChange}
            required
            min="0"
            step="1000"
          />
        </div>

        <div className="form-group">
          <label htmlFor="taxRate">Marginal Tax Rate (%)</label>
          <select
            id="taxRate"
            name="taxRate"
            value={formData.taxRate}
            onChange={handleChange}
            required
          >
            <option value="0">0% ($0 - $18,200)</option>
            <option value="19">19% ($18,201 - $45,000)</option>
            <option value="32.5">32.5% ($45,001 - $120,000)</option>
            <option value="37">37% ($120,001 - $180,000)</option>
            <option value="45">45% ($180,001+)</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="annualKilometers">Annual Kilometers</label>
          <input
            type="number"
            id="annualKilometers"
            name="annualKilometers"
            value={formData.annualKilometers}
            onChange={handleChange}
            required
            min="0"
            step="1000"
          />
        </div>

        <button type="submit" className="calculate-btn">Calculate Savings</button>
      </form>
    </div>
  );
};

