      notes: "Customer confirmed interest in proceeding with finance pre-approval"
    }
  ]);

  // Current date for dashboard
  const today = new Date();
  const dateString = today.toLocaleDateString('en-AU', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  // PHEV deadline calculation
  const deadline = new Date('2025-03-31');
  const daysToDeadline = Math.ceil((deadline - today) / (1000 * 60 * 60 * 24));

  // Call duration timer
  useEffect(() => {
    let timer;
    if (isCallActive) {
      timer = setInterval(() => {
        setCallDuration(prev => prev + 1);
      }, 1000);
    }

    return () => {
      if (timer) clearInterval(timer);
    };
  }, [isCallActive]);

  // Format call duration
  const formatCallDuration = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs < 10 ? '0' : ''}${secs}`;
  };

  // Format currency
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-AU', {
      style: 'currency',
      currency: 'AUD',
      minimumFractionDigits: 2
    }).format(amount);
  };

  // Format percentage
  const formatPercentage = (value) => {
    return `${value.toFixed(2)}%`;
  };

  // HEEMS Guidelines (NSW)
  const hemsGuidelines = {
    1: 2200, // Single adult, monthly
    2: 3100, // Couple, monthly
    3: 3800, // Couple with 1 child, monthly
    4: 4500, // Couple with 2 children, monthly
    5: 5200, // Couple with 3 children, monthly
    6: 5900  // Couple with 4 children, monthly
  };

  // Calculate household size
  const getHouseholdSize = () => {
    return (serviceabilityData.hasSpouse ? 2 : 1) + serviceabilityData.dependents;
  };

  // Get applicable HEMS guideline
  const getHemsGuideline = () => {
    const householdSize = getHouseholdSize();
    // Cap at 6 for larger families
    const applicableSize = Math.min(householdSize, 6);
    return hemsGuidelines[applicableSize] || hemsGuidelines[6];
  };

  // Handle customer input changes
  const handleCustomerInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    const newValue = type === 'checkbox' ? checked : 
                  type === 'number' ? Number(value) : value;

    setCustomerData({
      ...customerData,
      [name]: newValue
    });

    // Also update calculator form if relevant fields changed
    if (['vehicleValue', 'salary', 'annualKilometers'].includes(name)) {
      const calculatorFieldName = name === 'vehicleValue' ? 'vehiclePrice' : name;
      setFormData(prev => ({
        ...prev,
        [calculatorFieldName]: newValue
      }));
    }

    // Also update serviceability form if relevant fields changed
    if (['salary', 'vehicleValue'].includes(name)) {
      const serviceabilityFieldName = name === 'salary' ? 'grossIncome' : 
                                    name === 'vehicleValue' ? 'loanAmount' : name;
      setServiceabilityData(prev => ({
        ...prev,
        [serviceabilityFieldName]: newValue
      }));
    }
  };

  // Handle calculator form changes
  const handleCalculatorChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: name === 'term' || name === 'interestRate' || name === 'taxRate' || 
              name === 'fbtRate' || name === 'annualKilometers' ? 
              parseFloat(value) : value
    });
  };

  // Handle serviceability calculator changes
  const handleServiceabilityChange = (e) => {
    const { name, value, type, checked } = e.target;
    setServiceabilityData({
      ...serviceabilityData,
      [name]: type === 'checkbox' ? checked : parseFloat(value)
    });
  };

  // Handle calculator submission
  const handleCalculate = (e) => {
    if (e) e.preventDefault();

    // Convert string inputs to numbers
    const numericFormData = {
      ...formData,
      vehiclePrice: parseFloat(formData.vehiclePrice),
      salary: parseFloat(formData.salary),
    };

    const results = calculateLease(numericFormData);
    setCalculationResults(results);

    // Switch to results tab
    if (selectedTab === "calculator") {
      setSelectedTab("results");
    }
  };

  // Handle serviceability calculation
  const handleCalculateServiceability = () => {
    // Enhanced calculator with family size and investment property income
    const primaryMonthlyIncome = serviceabilityData.grossIncome / 12;
    const spouseMonthlyIncome = serviceabilityData.hasSpouse ? serviceabilityData.spouseIncome / 12 : 0;
    const otherMonthlyIncome = serviceabilityData.otherIncome / 12;

    // Investment property income (70% rule)
    const netInvestmentIncome = Math.max(0, serviceabilityData.investmentPropertyIncome - serviceabilityData.investmentPropertyExpenses);
    const serviceableInvestmentIncome = (netInvestmentIncome * 0.7) / 12;

    // Total monthly income
    const totalMonthlyIncome = primaryMonthlyIncome + spouseMonthlyIncome + otherMonthlyIncome + serviceableInvestmentIncome;

    // Use HEM benchmark based on family size
    const hemsBenchmark = getHemsGuideline();
    const totalDeclaredExpenses = serviceabilityData.livingExpenses;
    const assessableExpenses = Math.max(totalDeclaredExpenses, hemsBenchmark);

    // Calculate loan repayment
    const monthlyInterestRate = serviceabilityData.interestRate / 100 / 12;
    const monthlyRepayment = serviceabilityData.loanAmount * monthlyInterestRate * 
      Math.pow(1 + monthlyInterestRate, serviceabilityData.term) / 
      (Math.pow(1 + monthlyInterestRate, serviceabilityData.term) - 1);

    // Credit card commitment
    const creditCardCommitment = serviceabilityData.creditCardLimit * 0.03;

    // Total debt commitments
    const totalDebtCommitments = serviceabilityData.otherLoanRepayments + creditCardCommitment + monthlyRepayment;

    // Debt service ratio
    const debtServiceRatio = (totalDebtCommitments / totalMonthlyIncome) * 100;

    // Residual income
    const disposableIncome = totalMonthlyIncome - assessableExpenses - serviceabilityData.otherLoanRepayments - creditCardCommitment;
    const residualAfterNewLoan = disposableIncome - monthlyRepayment;

    // Stress test
    const stressInterestRate = serviceabilityData.interestRate + 3;
    const stressMonthlyRate = stressInterestRate / 100 / 12;
    const stressMonthlyRepayment = serviceabilityData.loanAmount * stressMonthlyRate * 
      Math.pow(1 + stressMonthlyRate, serviceabilityData.term) / 
      (Math.pow(1 + stressMonthlyRate, serviceabilityData.term) - 1);

    const stressResidualIncome = disposableIncome - stressMonthlyRepayment;

    // LTV
    const ltv = (serviceabilityData.loanAmount / formData.vehiclePrice) * 100;

    // Approval decision
    const approved = debtServiceRatio <= 50 && residualAfterNewLoan > 0 && stressResidualIncome > 0;

    // Set results
    setServiceabilityResults({
      approved,
      monthlyRepayment,
      totalMonthlyIncome,
      assessableExpenses,
      hemsBenchmark,
      totalDeclaredExpenses,
      disposableIncome,
      residualAfterNewLoan,
      debtServiceRatio,
      stressMonthlyRepayment,
      stressResidualIncome,
      ltv,
      assessmentPoints: [
        // Income assessment
        {
          type: 'positive',
          text: `Total assessed monthly income: ${formatCurrency(totalMonthlyIncome)}`
        },
        // Living expenses assessment
        {
          type: totalDeclaredExpenses < hemsBenchmark ? 'warning' : 'positive',
          text: `Living expenses ${totalDeclaredExpenses < hemsBenchmark ? 'adjusted to' : 'assessed at'} ${formatCurrency(assessableExpenses)}`
        },
        // Debt service ratio assessment
        {
          type: debtServiceRatio <= 30 ? 'positive' : debtServiceRatio <= 40 ? 'warning' : 'negative',
          text: `Debt service ratio of ${formatPercentage(debtServiceRatio)} is ${debtServiceRatio <= 30 ? 'excellent' : debtServiceRatio <= 40 ? 'moderate' : 'high'}`
        },
        // Residual income assessment
        {
          type: residualAfterNewLoan > 500 ? 'positive' : residualAfterNewLoan > 0 ? 'warning' : 'negative',
          text: `Residual income of ${formatCurrency(residualAfterNewLoan)} after loan repayment`
        },
        // Stress test assessment
        {
          type: stressResidualIncome > 0 ? 'positive' : 'negative',
          text: `${stressResidualIncome > 0 ? 'Passes' : 'Fails'} stress test with ${formatCurrency(stressResidualIncome)} residual`
        }
      ],
      suggestions: approved ? 
        ["All serviceability criteria met. Proceed with finance application."] : 
        [
          "Consider a longer loan term to reduce monthly repayments.",
          "Review living expenses to identify potential savings.",
          "Consider a lower loan amount to improve serviceability."
        ]
    });

    // Update customer journey if approved
    if (approved && journeyStage.stageNumber === 2) {
      advanceJourney();
    }
  };

  // Handle customer journey advancement
  const advanceJourney = () => {
    const newStageNumber = journeyStage.stageNumber + 1;
    setJourneyStage({
      stageNumber: newStageNumber,
      stageName: newStageNumber === 3 ? "Finance Approval" : 
                 newStageNumber === 4 ? "Vehicle Selection" :
                 newStageNumber === 5 ? "Documentation" :
                 newStageNumber === 6 ? "Settlement" : "Delivery",
      progress: Math.min(100, (newStageNumber / 7) * 100),
      lastActivity: "Just now",
      nextAction: newStageNumber === 3 ? "Submit finance application" :
                 newStageNumber === 4 ? "Finalize vehicle selection" :
                 newStageNumber === 5 ? "Collect and verify all documents" :
                 newStageNumber === 6 ? "Process settlement payment" : "Coordinate delivery",
      riskLevel: newStageNumber === 3 ? "high" : "low"
    });

    // Add a journey progression interaction
    const newInteraction = {
      id: interactions.length + 1,
      type: "system",
      date: new Date().toLocaleDateString('en-AU', {
        day: 'numeric',
        month: 'short',
        year: 'numeric'
      }),
      description: `Advanced to ${newStageNumber === 3 ? "Finance Approval" : 
                    newStageNumber === 4 ? "Vehicle Selection" :
                    newStageNumber === 5 ? "Documentation" :
                    newStageNumber === 6 ? "Settlement" : "Delivery"} stage`,
      sentiment: "positive",
      notes: "Customer journey progressed to next stage."
    };

    setInteractions([newInteraction, ...interactions]);
  };

  // File handling functions
  const handleDragEnter = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (!isDragging) setIsDragging(true);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);

    const droppedFiles = [...e.dataTransfer.files];
    if (droppedFiles.length > 0) {
      processFiles(droppedFiles);
    }
  };

  const handleFileInputChange = (e) => {
    const selectedFiles = [...e.target.files];
    if (selectedFiles.length > 0) {
      processFiles(selectedFiles);
    }
  };

  // Process files with AI
  const processFiles = (newFiles) => {
    setIsProcessing(true);

    // Simulate AI processing
    const processedFiles = [...newFiles].map(file => {
      // Create a unique ID for each file
      const fileId = `file-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

      // Determine file type
      let fileType = 'other';
      const fileName = file.name.toLowerCase();

      if (fileName.includes('quote') || fileName.includes('vsss') || fileName.includes('price')) {
        fileType = 'quotes';
      } else if (fileName.includes('application') || fileName.includes('form')) {
        fileType = 'applications';
      } else if (fileName.includes('license') || fileName.includes('licence') || fileName.includes('id') || fileName.includes('passport')) {
        fileType = 'identification';
      } else if (fileName.includes('payslip') || fileName.includes('bank') || fileName.includes('financial')) {
        fileType = 'financial';
      } else if (fileName.includes('email') || fileName.includes('communication') || fileName.includes('msg')) {
        fileType = 'communication';
      }

      // Create AI analysis for the file
      const analysis = {
        summary: `This appears to be a ${fileType.slice(0, -1)} document for ${customerData.fullName}`,
        classification: fileType,
        extractedData: {
          dates: file.lastModified ? [new Date(file.lastModified).toISOString().split('T')[0]] : [],
          entities: [customerData.fullName, customerData.email],
          keyPoints: []
        },
        confidenceScore: 0.87 + Math.random() * 0.12
      };

      // Add key points based on file type
      if (fileType === 'quotes') {
        analysis.extractedData.keyPoints = ['Quoted vehicle price', 'Monthly payment details', 'Term information'];
      } else if (fileType === 'applications') {
        analysis.extractedData.keyPoints = ['Application status', 'Reference number', 'Submission date'];
      } else if (fileType === 'identification') {
        analysis.extractedData.keyPoints = ['ID verification', 'Expiry date', 'License number'];
      }

      // Update AI analysis state
      setAiAnalysis(prev => ({
        ...prev,
        [fileId]: analysis
      }));

      return {
        id: fileId,
        file,
        type: fileType,
        uploadDate: new Date().toISOString(),
        analyzed: true
      };
    });

    // Simulate processing delay
    setTimeout(() => {
      setFiles(prev => [...prev, ...processedFiles]);

      // Organize files by type
      const newOrganizedFiles = { ...organizedFiles };
      processedFiles.forEach(file => {
        newOrganizedFiles[file.type] = [...newOrganizedFiles[file.type], file];
      });
      setOrganizedFiles(newOrganizedFiles);

      setIsProcessing(false);
    }, 1500);
  };

  // Delete file
  const handleDeleteFile = (fileId) => {
    setFiles(prev => prev.filter(file => file.id !== fileId));

    // Remove from organized files
    const fileToDelete = files.find(file => file.id === fileId);
    if (fileToDelete) {
      setOrganizedFiles(prev => ({
        ...prev,
        [fileToDelete.type]: prev[fileToDelete.type].filter(file => file.id !== fileId)
      }));
    }

    // Remove from AI analysis
    setAiAnalysis(prev => {
      const newAnalysis = { ...prev };
      delete newAnalysis[fileId];
      return newAnalysis;
    });
  };

  // Toggle call status
  const toggleCallStatus = () => {
    if (!isCallActive) {
      setIsCallActive(true);
      setIsRecording(true);
      setCallDuration(0);
    } else {
      setIsCallActive(false);
      setIsRecording(false);
      setCallDuration(0);
    }
  };

  // Get file type count
  const getFileTypeCount = (type) => {
    return organizedFiles[type]?.length || 0;
  };

  // Filtered files based on search
  const filteredFiles = searchQuery
    ? files.filter(file => 
        file.file.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (aiAnalysis[file.id]?.extractedData.keyPoints.some(point => 
          point.toLowerCase().includes(searchQuery.toLowerCase()))
        )
      )
    : files;

  return (
