                Dashboard
              </TabsTrigger>
              <TabsTrigger value="customerProfile" className="data-[state=active]:bg-blue-50 flex items-center">
                <User size={16} className="mr-2" />
                Customer Profile
              </TabsTrigger>
              <TabsTrigger value="journey" className="data-[state=active]:bg-blue-50 flex items-center">
                <Users size={16} className="mr-2" />
                Customer Journey
              </TabsTrigger>
              <TabsTrigger value="serviceability" className="data-[state=active]:bg-blue-50 flex items-center">
                <CreditCard size={16} className="mr-2" />
                Serviceability
              </TabsTrigger>
            </TabsList>

            {/* Second row of tabs */}
            <TabsList className="bg-white rounded-b-lg p-1 border">
              <TabsTrigger value="calculator" className="data-[state=active]:bg-blue-50 flex items-center">
                <Calculator size={16} className="mr-2" />
