                    </button>
                    <button className="text-xs px-2 py-0.5 rounded bg-gray-100 text-gray-800">
                      ID ({getFileTypeCount('identification')})
                    </button>
                    <button className="text-xs px-2 py-0.5 rounded bg-gray-100 text-gray-800">
                      Financial ({getFileTypeCount('financial')})
                    </button>
                    <button className="text-xs px-2 py-0.5 rounded bg-gray-100 text-gray-800">
                      Communication ({getFileTypeCount('communication')})
                    </button>
                    <button className="text-xs px-2 py-0.5 rounded bg-gray-100 text-gray-800">
                      Other ({getFileTypeCount('other')})
