// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';

// Main Components
import Navbar from './components/Navbar/Navbar';
import Dashboard from './pages/Dashboard';
import Calculator from './components/Calculator/Calculator';
import Results from './components/Results/Results';
import CustomerProfile from './pages/CustomerProfile';
import DocumentManager from './components/DocumentManager/DocumentManager';
import ProductRecommendations from './pages/ProductRecommendations';
import SalesScripts from './pages/SalesScripts';
import ProcessGuide from './pages/ProcessGuide';
import CustomerJourney from './pages/CustomerJourney';
import ServiceabilityCalculator from './pages/ServiceabilityCalculator';

// Services
import { calculateLease } from './services/calculationService';

// Styles
import './App.css';

function App() {
  // State for calculator
  const [calculationResults, setCalculationResults] = useState(null);

  // Customer data state
  const [customerData, setCustomerData] = useState({
    fullName: '',
    email: '',
    mobile: '',
    employer: '',
    vehicleValue: 50000,
    vehicleType: 'sedan',
    hasChildren: false,
    primaryUse: 'commuting',
    annualKilometers: 15000,
    employmentSector: 'private',
    salary: 85000,
    taxRate: 32.5
  });

  // Customer journey state
  const [journeyStage, setJourneyStage] = useState({
    stageNumber: 1,
    stageName: "Initial Contact",
    progress: 10,
    lastActivity: "New lead",
    nextAction: "Schedule initial consultation",
    riskLevel: "low"
  });

  // Handle calculation
  const handleCalculation = (results) => {
    setCalculationResults(results);
  };

  // Update customer data
  const updateCustomerData = (data) => {
    setCustomerData({...customerData, ...data});
  };

  // Advance customer journey
  const advanceJourney = () => {
    const newStageNumber = journeyStage.stageNumber + 1;
    setJourneyStage({
      stageNumber: newStageNumber,
      stageName: newStageNumber === 2 ? "Finance Consideration" : 
                 newStageNumber === 3 ? "Finance Approval" :
                 newStageNumber === 4 ? "Vehicle Selection" :
                 newStageNumber === 5 ? "Documentation" :
                 newStageNumber === 6 ? "Settlement" : "Delivery",
      progress: Math.min(100, (newStageNumber / 7) * 100),
      lastActivity: "Stage advanced",
      nextAction: newStageNumber === 2 ? "Complete serviceability assessment" :
                 newStageNumber === 3 ? "Submit finance application" :
                 newStageNumber === 4 ? "Finalize vehicle selection" :
                 newStageNumber === 5 ? "Collect and verify all documents" :
                 newStageNumber === 6 ? "Process settlement payment" : "Coordinate delivery",
      riskLevel: newStageNumber === 3 ? "high" : "low"
    });
  };

  return (
    <Router>
      <div className="App">
        <Navbar />
        <main className="container">
          <h1>Novated Lease Assistant</h1>

          <Routes>
            <Route path="/" element={
              <Dashboard 
                customerData={customerData} 
                journeyStage={journeyStage}
                calculationResults={calculationResults}
              />
            } />

            <Route path="/calculator" element={
              <>
                <Calculator onCalculate={handleCalculation} />
                {calculationResults && <Results results={calculationResults} />}
              </>
            } />

            <Route path="/customer" element={
              <CustomerProfile 
                customerData={customerData} 
                updateCustomerData={updateCustomerData} 
              />
            } />

            <Route path="/documents" element={
              <DocumentManager customerData={customerData} />
            } />

            <Route path="/recommendations" element={
              <ProductRecommendations customerData={customerData} />
            } />

            <Route path="/scripts" element={
              <SalesScripts customerData={customerData} />
            } />

            <Route path="/process" element={
              <ProcessGuide />
            } />

            <Route path="/journey" element={
              <CustomerJourney 
                customerData={customerData}
                journeyStage={journeyStage}
                advanceJourney={advanceJourney}
              />
            } />

            <Route path="/serviceability" element={
              <ServiceabilityCalculator 
                customerData={customerData}
                advanceJourney={advanceJourney}
              />
            } />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;

// src/pages/Dashboard.js
import React from 'react';
import { Link } from 'react-router-dom';
import { Card, CardHeader, CardContent, CardTitle, CardDescription } from '../components/ui/card';
import { Clock, Activity, FileText, AlertTriangle, MessageSquare, Car, User, CheckSquare, Bell } from 'lucide-react';

const Dashboard = ({ customerData, journeyStage, calculationResults }) => {
  // Format currency
  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-AU', {
      style: 'currency',
      currency: 'AUD',
      minimumFractionDigits: 2
    }).format(amount);
  };

  // Format percentage
  const formatPercentage = (value) => {
    return `${value.toFixed(2)}%`;
  };

  // HEEMS Guidelines (NSW)
  const hemsGuidelines = {
    1: 2200, // Single adult, monthly
    2: 3100, // Couple, monthly
    3: 3800, // Couple with 1 child, monthly
    4: 4500, // Couple with 2 children, monthly
    5: 5200, // Couple with 3 children, monthly
    6: 5900  // Couple with 4 children, monthly
  };

  // Calculate household size
  const getHouseholdSize = () => {
    return (serviceabilityData.hasSpouse ? 2 : 1) + serviceabilityData.dependents;
  };

  // Get applicable HEMS guideline
  const getHemsGuideline = () => {
    const householdSize = getHouseholdSize();
    // Cap at 6 for larger families
    const applicableSize = Math.min(householdSize, 6);
    return hemsGuidelines[applicableSize] || hemsGuidelines[6];
  };

  // Handle serviceability calculator changes
  const handleServiceabilityChange = (e) => {
    const { name, value, type, checked } = e.target;
    setServiceabilityData({
      ...serviceabilityData,
      [name]: type === 'checkbox' ? checked : parseFloat(value)
    });
  };

  // Handle serviceability calculation
  const handleCalculateServiceability = () => {
    // Enhanced calculator with family size and investment property income
    const primaryMonthlyIncome = serviceabilityData.grossIncome / 12;
    const spouseMonthlyIncome = serviceabilityData.hasSpouse ? serviceabilityData.spouseIncome / 12 : 0;
    const otherMonthlyIncome = serviceabilityData.otherIncome / 12;

    // Investment property income (70% rule)
    const netInvestmentIncome = Math.max(0, serviceabilityData.investmentPropertyIncome - serviceabilityData.investmentPropertyExpenses);
    const serviceableInvestmentIncome = (netInvestmentIncome * 0.7) / 12;

    // Total monthly income
    const totalMonthlyIncome = primaryMonthlyIncome + spouseMonthlyIncome + otherMonthlyIncome + serviceableInvestmentIncome;

    // Use HEM benchmark based on family size
    const hemsBenchmark = getHemsGuideline();
    const totalDeclaredExpenses = serviceabilityData.livingExpenses;
    const assessableExpenses = Math.max(totalDeclaredExpenses, hemsBenchmark);

    // Calculate loan repayment
    const monthlyInterestRate = serviceabilityData.interestRate / 100 / 12;
    const monthlyRepayment = serviceabilityData.loanAmount * monthlyInterestRate * 
      Math.pow(1 + monthlyInterestRate, serviceabilityData.term) / 
      (Math.pow(1 + monthlyInterestRate, serviceabilityData.term) - 1);

    // Credit card commitment
    const creditCardCommitment = serviceabilityData.creditCardLimit * 0.03;

    // Total debt commitments
    const totalDebtCommitments = serviceabilityData.otherLoanRepayments + creditCardCommitment + monthlyRepayment;

    // Debt service ratio
    const debtServiceRatio = (totalDebtCommitments / totalMonthlyIncome) * 100;

    // Residual income
    const disposableIncome = totalMonthlyIncome - assessableExpenses - serviceabilityData.otherLoanRepayments - creditCardCommitment;
    const residualAfterNewLoan = disposableIncome - monthlyRepayment;

    // Stress test
    const stressInterestRate = serviceabilityData.interestRate + 3;
    const stressMonthlyRate = stressInterestRate / 100 / 12;
    const stressMonthlyRepayment = serviceabilityData.loanAmount * stressMonthlyRate * 
      Math.pow(1 + stressMonthlyRate, serviceabilityData.term) / 
      (Math.pow(1 + stressMonthlyRate, serviceabilityData.term) - 1);

    const stressResidualIncome = disposableIncome - stressMonthlyRepayment;

    // LTV
    const ltv = (serviceabilityData.loanAmount / customerData.vehicleValue) * 100;

    // Approval decision
    const approved = debtServiceRatio <= 50 && residualAfterNewLoan > 0 && stressResidualIncome > 0;

    // Set results
    setServiceabilityResults({
      approved,
      monthlyRepayment,
      totalMonthlyIncome,
      assessableExpenses,
      hemsBenchmark,
      totalDeclaredExpenses,
      disposableIncome,
      residualAfterNewLoan,
      debtServiceRatio,
      stressMonthlyRepayment,
      stressResidualIncome,
      ltv,
      assessmentPoints: [
        // Income assessment
        {
          type: 'positive',
          text: `Total assessed monthly income: ${formatCurrency(totalMonthlyIncome)}`
        },
        // Living expenses assessment
        {
          type: totalDeclaredExpenses < hemsBenchmark ? 'warning' : 'positive',
          text: `Living expenses ${totalDeclaredExpenses < hemsBenchmark ? 'adjusted to' : 'assessed at'} ${formatCurrency(assessableExpenses)}`
        },
        // Debt service ratio assessment
        {
          type: debtServiceRatio <= 30 ? 'positive' : debtServiceRatio <= 40 ? 'warning' : 'negative',
          text: `Debt service ratio of ${formatPercentage(debtServiceRatio)} is ${debtServiceRatio <= 30 ? 'excellent' : debtServiceRatio <= 40 ? 'moderate' : 'high'}`
        },
        // Residual income assessment
        {
          type: residualAfterNewLoan > 500 ? 'positive' : residualAfterNewLoan > 0 ? 'warning' : 'negative',
          text: `Residual income of ${formatCurrency(residualAfterNewLoan)} after loan repayment`
        },
        // Stress test assessment
        {
          type: stressResidualIncome > 0 ? 'positive' : 'negative',
          text: `${stressResidualIncome > 0 ? 'Passes' : 'Fails'} stress test with ${formatCurrency(stressResidualIncome)} residual`
        }
      ],
      suggestions: approved ? 
        ["All serviceability criteria met. Proceed with finance application."] : 
        [
          "Consider a longer loan term to reduce monthly repayments.",
          "Review living expenses to identify potential savings.",
          "Consider a lower loan amount to improve serviceability."
        ]
    });

    // Update customer journey if approved
    if (approved && advanceJourney) {
      advanceJourney();
    }
  };

  // If no customer data, show message
  if (!customerData.fullName) {
    return (
      <div className="bg-white p-6 rounded-lg shadow text-center">
        <p className="text-gray-500 mb-4">Please create or select a customer to perform serviceability assessment.</p>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
      <div className="lg:col-span-2">
        <Card>
          <CardHeader className="pb-2">
            <CardTitle className="text-lg">Finance Application Serviceability Calculator</CardTitle>
            <CardDescription>
              Assess customer's ability to service the loan
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-6">
              {/* Personal details section */}
              <div>
                <h3 className="font-medium text-sm mb-3">Household Details</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium mb-1">Has Spouse/Partner</label>
                    <div className="flex items-center">
                      <input
                        type="checkbox"
                        id="hasSpouse"
                        name="hasSpouse"
                        checked={serviceabilityData.hasSpouse}
                        onChange={handleServiceabilityChange}
                        className="mr-2"
                      />
                      <label htmlFor="hasSpouse" className="text-sm">Include spouse/partner income</label>
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium mb-1">Number of Dependents</label>
                    <input
                      type="number"
                      name="dependents"
                      value={serviceabilityData.dependents}
                      onChange={handleServiceabilityChange}
                      className="w-full p-2 border rounded"
                      min="0"
                      max="10"
                    />
                  </div>
                </div>
              </div>

              {/* Income section */}
              <div>
                <h3 className="font-medium text-sm mb-3">Income</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium mb-1">Gross Annual Income ($)</label>
                    <input
                      type="number"
                      name="grossIncome"
                      value={serviceabilityData.grossIncome}
                      onChange={handleServiceabilityChange}
                      className="w-full p-2 border rounded"
                      required
                    />
                  </div>

                  {serviceabilityData.hasSpouse && (
                    <div>
                      <label className="block text-sm font-medium mb-1">Spouse Annual Income ($)</label>
                      <input
                        type="number"
                        name="spouseIncome"
                        value={serviceabilityData.spouseIncome || 0}
                        onChange={handleServiceabilityChange}
                        className="w-full p-2 border rounded"
                      />
                    </div>
                  )}

                  <div>
                    <label className="block text-sm font-medium mb-1">Other Income ($)</label>
                    <input
                      type="number"
                      name="otherIncome"
                      value={serviceabilityData.otherIncome}
                      onChange={handleServiceabilityChange}
                      className="w-full p-2 border rounded"
                    />
                  </div>
                </div>
              </div>

              {/* Living expenses section */}
              <div>
                <h3 className="font-medium text-sm mb-3">Living Expenses</h3>
                <p className="text-xs text-gray-600 mb-2">
                  HEM benchmark for a household of {getHouseholdSize()} people: 
                  <span className="font-medium"> {formatCurrency(getHemsGuideline())}/month</span>
                </p>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
