// src/components/DocumentManager/DocumentManager.js
import React, { useState, useCallback } from 'react';
import { Upload, X, FileText, AlertCircle, Check, Search, UploadCloud, File, Loader2 } from 'lucide-react';

const DocumentManager = ({ customerEmail, customerName }) => {
  const [files, setFiles] = useState([]);
  const [isDragging, setIsDragging] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [aiAnalysis, setAiAnalysis] = useState({});
  const [organizedFiles, setOrganizedFiles] = useState({
    quotes: [],
    applications: [],
    identification: [],
    financial: [],
    communication: [],
    other: []
  });

  // Handle drag events
  const handleDragEnter = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(true);
  }, []);

  const handleDragLeave = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
  }, []);

  const handleDragOver = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    if (!isDragging) setIsDragging(true);
  }, [isDragging]);

  // Process files with AI
  const processFiles = useCallback(async (newFiles) => {
    setIsProcessing(true);

    // Simulate AI processing
    const processedFiles = [...newFiles].map(file => {
      // Create a unique ID for each file
      const fileId = `file-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

      // Determine file type
      let fileType = 'other';
      const fileName = file.name.toLowerCase();

      if (fileName.includes('quote') || fileName.includes('vsss') || fileName.includes('price')) {
        fileType = 'quotes';
      } else if (fileName.includes('application') || fileName.includes('form')) {
        fileType = 'applications';
      } else if (fileName.includes('license') || fileName.includes('licence') || fileName.includes('id') || fileName.includes('passport')) {
        fileType = 'identification';
      } else if (fileName.includes('payslip') || fileName.includes('bank') || fileName.includes('financial')) {
        fileType = 'financial';
      } else if (fileName.includes('email') || fileName.includes('communication') || fileName.includes('msg')) {
        fileType = 'communication';
      }

      // Create AI analysis for the file
      const analysis = {
        summary: `This appears to be a ${fileType.slice(0, -1)} document for ${customerName || 'Customer'}`,
        classification: fileType,
        extractedData: {
          dates: file.lastModified ? [new Date(file.lastModified).toISOString().split('T')[0]] : [],
          entities: [customerName, customerEmail].filter(Boolean),
          keyPoints: []
        },
        confidenceScore: 0.87 + Math.random() * 0.12
      };

      // Add key points based on file type
      if (fileType === 'quotes') {
        analysis.extractedData.keyPoints = ['Quoted vehicle price', 'Monthly payment details', 'Term information'];
      } else if (fileType === 'applications') {
        analysis.extractedData.keyPoints = ['Application status', 'Reference number', 'Submission date'];
      } else if (fileType === 'identification') {
        analysis.extractedData.keyPoints = ['ID verification', 'Expiry date', 'License number'];
      }

      // Update AI analysis state
      setAiAnalysis(prev => ({
        ...prev,
        [fileId]: analysis
      }));

      return {
        id: fileId,
        file,
        type: fileType,
        uploadDate: new Date().toISOString(),
        analyzed: true
      };
    });

    // Simulate processing delay
    await new Promise(resolve => setTimeout(resolve, 1500));

    setFiles(prev => [...prev, ...processedFiles]);

    // Organize files by type
    const newOrganizedFiles = { ...organizedFiles };
    processedFiles.forEach(file => {
      newOrganizedFiles[file.type] = [...(newOrganizedFiles[file.type] || []), file];
    });
    setOrganizedFiles(newOrganizedFiles);
    setIsProcessing(false);
  }, [customerEmail, customerName, organizedFiles]);

  // Handle drop event
  const handleDrop = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);

    const droppedFiles = [...e.dataTransfer.files];
    if (droppedFiles.length > 0) {
      processFiles(droppedFiles);
    }
  }, [processFiles]);

  // Handle file input change
  const handleFileInputChange = useCallback((e) => {
    const selectedFiles = [...e.target.files];
    if (selectedFiles.length > 0) {
      processFiles(selectedFiles);
    }
  }, [processFiles]);

  // Handle file deletion
  const handleDeleteFile = useCallback((fileId) => {
    setFiles(prev => prev.filter(file => file.id !== fileId));

    // Remove from organized files
    const fileToDelete = files.find(file => file.id === fileId);
    if (fileToDelete) {
      setOrganizedFiles(prev => ({
        ...prev,
        [fileToDelete.type]: prev[fileToDelete.type].filter(file => file.id !== fileId)
      }));
    }

    // Remove from AI analysis
    setAiAnalysis(prev => {
      const newAnalysis = { ...prev };
      delete newAnalysis[fileId];
      return newAnalysis;
    });
  }, [files]);

  // Handle paste from clipboard
  const handlePaste = useCallback(async () => {
    try {
      const clipboardItems = await navigator.clipboard.read();
      for (const clipboardItem of clipboardItems) {
        for (const type of clipboardItem.types) {
          if (type.startsWith('image/')) {
            const blob = await clipboardItem.getType(type);
            const file = new File([blob], `Pasted Image ${new Date().toLocaleString()}.png`, { type });
            processFiles([file]);
          } else if (type === 'text/plain') {
            const blob = await clipboardItem.getType(type);
            const text = await blob.text();
            const file = new File([text], `Pasted Text ${new Date().toLocaleString()}.txt`, { type: 'text/plain' });
            processFiles([file]);
          }
        }
      }
    } catch (error) {
      console.error('Error accessing clipboard:', error);
      alert('Unable to access clipboard. Please check browser permissions.');
    }
  }, [processFiles]);

  // Filtered files based on search
  const filteredFiles = searchQuery
    ? files.filter(file => 
        file.file.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (aiAnalysis[file.id]?.extractedData.keyPoints.some(point => 
          point.toLowerCase().includes(searchQuery.toLowerCase()))
        )
      )
    : files;

  // Document type counter
  const getFileTypeCount = (type) => {
    return organizedFiles[type]?.length || 0;
  };

  return (
    <div className="w-full">
      <div className="flex justify-between items-center mb-3">
        <div className="relative flex-grow mr-2">
          <input
            type="text"
            placeholder="Search files..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="w-full pl-8 pr-2 py-1 text-sm border rounded focus:outline-none focus:ring-1 focus:ring-blue-500"
          />
          <Search size={14} className="absolute left-2.5 top-2 text-gray-400" />
        </div>
        <div className="flex gap-2">
          <button 
            onClick={handlePaste}
            className="text-xs bg-blue-50 text-blue-600 px-2 py-1 rounded flex items-center"
          >
            <Upload size={14} className="mr-1" />
            Paste from Clipboard
          </button>
        </div>
      </div>

      {/* Category tabs */}
      <div className="flex flex-wrap gap-1 mb-3">
        <button className="text-xs px-2 py-0.5 rounded bg-blue-100 text-blue-800">
          All ({files.length})
        </button>
        <button className="text-xs px-2 py-0.5 rounded bg-gray-100 text-gray-800">
          Quotes ({getFileTypeCount('quotes')})
        </button>
        <button className="text-xs px-2 py-0.5 rounded bg-gray-100 text-gray-800">
          Applications ({getFileTypeCount('applications')})
        </button>
        <button className="text-xs px-2 py-0.5 rounded bg-gray-100 text-gray-800">
          ID ({getFileTypeCount('identification')})
        </button>
        <button className="text-xs px-2 py-0.5 rounded bg-gray-100 text-gray-800">
          Financial ({getFileTypeCount('financial')})
        </button>
        <button className="text-xs px-2 py-0.5 rounded bg-gray-100 text-gray-800">
          Other ({getFileTypeCount('other')})
        </button>
      </div>

      {/* Drag and Drop Zone */}
      <div 
        className={`border-2 ${isDragging ? 'border-blue-400 bg-blue-50' : 'border-dashed border-gray-300 bg-gray-50'} rounded-lg p-6 mb-3 text-center hover:bg-gray-100 transition-colors`}
        onDragEnter={handleDragEnter}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
        onClick={() => document.getElementById('file-input').click()}
      >
        <input 
          id="file-input" 
          type="file" 
          multiple
          onChange={handleFileInputChange}
          className="hidden" 
        />
        <div className="flex flex-col items-center">
          <UploadCloud size={36} className={isDragging ? 'text-blue-500' : 'text-gray-400'} />
          <p className="text-sm font-medium text-gray-700 mb-1 mt-2">
            {isDragging ? 'Drop files here' : 'Drag and drop files here'}
          </p>
          <p className="text-xs text-gray-500">or click to select files</p>
        </div>
        <div className="mt-2 text-xs text-gray-500">
          Supports all common document formats including emails, PDFs, images, and more
        </div>
      </div>

      {/* Processing indicator */}
      {isProcessing && (
        <div className="mb-3 p-2 bg-blue-50 rounded border border-blue-200 flex items-center justify-center">
          <Loader2 size={16} className="animate-spin text-blue-500 mr-2" />
          <span className="text-sm text-blue-700">Processing files with AI analysis...</span>
        </div>
      )}

      {/* Files list */}
      <div className="border rounded max-h-64 overflow-y-auto">
        {filteredFiles.length > 0 ? (
          <ul className="divide-y">
            {filteredFiles.map((fileItem) => (
              <li key={fileItem.id} className="hover:bg-gray-50 p-3">
                <div className="flex justify-between">
                  <div className="flex items-start gap-3">
                    <div className="p-2 bg-blue-50 rounded">
                      <FileText size={24} className="text-blue-500" />
                    </div>
                    <div>
                      <div className="flex items-center">
                        <p className="font-medium text-sm">{fileItem.file.name}</p>
                        <span className={`ml-2 text-xs px-2 py-0.5 rounded-full ${
                          fileItem.type === 'quotes' ? 'bg-purple-100 text-purple-800' :
                          fileItem.type === 'applications' ? 'bg-green-100 text-green-800' :
                          fileItem.type === 'identification' ? 'bg-blue-100 text-blue-800' :
                          fileItem.type === 'financial' ? 'bg-yellow-100 text-yellow-800' :
                          fileItem.type === 'communication' ? 'bg-pink-100 text-pink-800' :
                          'bg-gray-100 text-gray-800'
                        }`}>
                          {fileItem.type}
                        </span>
                      </div>
                      <p className="text-xs text-gray-500 mt-1">
                        {new Date(fileItem.uploadDate).toLocaleString()} • {(fileItem.file.size / 1024).toFixed(0)} KB
                      </p>

                      {/* AI Analysis Display */}
                      {aiAnalysis[fileItem.id] && (
                        <div className="mt-1 text-xs text-gray-600">
                          <div className="flex items-center text-blue-600">
                            <Check size={12} className="mr-1" />
                            AI-analyzed ({(aiAnalysis[fileItem.id].confidenceScore * 100).toFixed(0)}% confidence)
                          </div>
                          {aiAnalysis[fileItem.id].extractedData.keyPoints.length > 0 && (
                            <div className="mt-1">
                              <span className="font-medium">Key points: </span>
                              {aiAnalysis[fileItem.id].extractedData.keyPoints.join(', ')}
                            </div>
                          )}
                        </div>
                      )}
                    </div>
                  </div>

                  <div className="flex gap-2">
                    <button className="text-xs bg-gray-100 hover:bg-gray-200 text-gray-700 px-2 py-1 rounded">
                      View
                    </button>
                    <button 
                      onClick={() => handleDeleteFile(fileItem.id)}
                      className="text-xs bg-red-50 hover:bg-red-100 text-red-600 px-2 py-1 rounded"
                    >
                      <X size={14} />
                    </button>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <div className="p-8 text-center">
            <File size={32} className="text-gray-300 mx-auto mb-2" />
            <p className="text-sm text-gray-500">
              {files.length > 0 
                ? "No files match your search criteria" 
                : "No files uploaded for this customer yet"}
            </p>
          </div>
        )}
      </div>

      <div className="mt-3 flex justify-between">
        <div className="flex gap-2">
          <button 
            onClick={() => document.getElementById('file-input').click()}
            className="text-xs bg-blue-600 text-white px-2 py-1 rounded flex items-center"
          >
            <Upload size={14} className="mr-1" />
            Upload Files
          </button>
          <button className="text-xs bg-gray-100 text-gray-700 px-2 py-1 rounded flex items-center">
            <FileText size={14} className="mr-1" />
            Create Folder
          </button>
        </div>
        <div>
          <button className="text-xs text-blue-600 hover:underline flex items-center">
            Analyze All Documents
          </button>
        </div>
      </div>
    </div>
  );
};

export default DocumentManager;

// src/pages/ProductRecommendations.js
import React, { useState, useEffect } from 'react';
import { Card, CardHeader, CardContent, CardTitle, CardDescription } from '../components/ui/card';

const ProductRecommendations = ({ customerData }) => {
  const [recommendations, setRecommendations] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  // Generate recommendations based on customer data
  useEffect(() => {
    if (customerData.fullName) {
      generateRecommendations();
    }
  }, [customerData]);

  // Calculate total costs
  const calculateTotals = (products) => {
    if (!products) return { monthly: 0, oneTime: 0 };

    return products.reduce(
      (acc, product) => ({
        monthly: acc.monthly + product.monthlyEst,
        oneTime: acc.oneTime + (product.oneTimeEst || 0)
      }),
      { monthly: 0, oneTime: 0 }
    );
  };

  // Generate recommendations
  const generateRecommendations = () => {
    setIsAnalyzing(true);

    setTimeout(() => {
      const core = [];
      const recommended = [];
      const optional = [];

      // Motor Insurance is always core
      core.push({
        id: 'motorInsurance',
        name: "Motor Insurance",
        monthlyEst: Math.round(customerData.vehicleValue * 0.0035),
        reason: 'Essential protection with full lease payout coverage',
        scriptKey: 'motorInsurance',
        benefits: [
          "Full lease payout - no gap",
          "Open driver policy",
          "Free windscreen replacement"
        ],
        risks: [
          "Gap between insurance payout and lease value",
          "Higher excess with standard insurance",
          "Limited coverage for accessories"
        ]
      });

      // LeaseGuard based on employment sector
      if (customerData.employmentSector === 'private') {
        recommended.push({
          id: 'leaseGuard',
          name: "LeaseGuard",
          monthlyEst: 80,
          reason: 'Recommended for private sector employees',
          scriptKey: 'leaseGuard',
          benefits: [
            "Payments covered for up to 11 months",
            "Option to return vehicle if unemployed",
            "Peace of mind during uncertain times"
          ],
          risks: [
            "Full financial responsibility if made redundant",
            "Potential impact on credit rating if payments missed",
            "Stress during employment transition"
          ]
        });
      } else {
        optional.push({
          id: 'leaseGuard',
          name: "LeaseGuard",
          monthlyEst: 80,
          reason: 'Optional protection against employment changes',
          scriptKey: 'leaseGuard',
          benefits: [
            "Payments covered for up to 11 months",
            "Option to return vehicle if unemployed",
            "Peace of mind during uncertain times"
          ],
          risks: [
            "Full financial responsibility if made redundant",
            "Potential impact on credit rating if payments missed",
            "Stress during employment transition"
          ]
        });
      }

      // Window tint based on having children
      if (customerData.hasChildren) {
        recommended.push({
          id: 'windowTint',
          name: "Window Tint",
          oneTimeEst: 450,
          monthlyEst: Math.round(450 / 36),
          reason: 'Recommended for families with children',
          benefits: [
            "Blocks up to 99% of UV rays",
            "Reduces heat and glare by up to 60%",
            "Increased privacy"
          ],
          risks: [
            "Interior damage from UV exposure",
            "Uncomfortable cabin temperatures",
            "Higher air conditioning usage"
          ]
        });
      } else {
        optional.push({
          id: 'windowTint',
          name: "Window Tint",
          oneTimeEst: 450,
          monthlyEst: Math.round(450 / 36),
          reason: 'Optional UV and heat protection',
          benefits: [
            "Blocks up to 99% of UV rays",
            "Reduces heat and glare by up to 60%",
            "Increased privacy"
          ],
          risks: [
            "Interior damage from UV exposure",
            "Uncomfortable cabin temperatures",
            "Higher air conditioning usage"
          ]
        });
      }

      // Exterior protection based on parking location
      if (customerData.parkingLocation === 'street') {
        recommended.push({
          id: 'exteriorProtection',
          name: "Exterior Protection",
          oneTimeEst: 795,
          monthlyEst: Math.round(795 / 36),
          reason: 'Recommended for street parking exposure',
          benefits: [
            "Protection against environmental damage",
            "Super gloss finish",
            "Easier cleaning"
          ],
          risks: [
            "Paint damage and fading",
            "Permanent staining",
            "Reduced resale value"
          ]
        });
      } else {
        optional.push({
          id: 'exteriorProtection',
          name: "Exterior Protection",
          oneTimeEst: 795,
          monthlyEst: Math.round(795 / 36),
          reason: 'Optional paint protection',
          benefits: [
            "Protection against environmental damage",
            "Super gloss finish",
            "Easier cleaning"
          ],
          risks: [
            "Paint damage and fading",
            "Permanent staining",
            "Reduced resale value"
          ]
        });
      }

      setRecommendations({
        core,
        recommended,
        optional
      });

      setIsAnalyzing(false);
    }, 1000);
  };

  // If no customer data, show message
  if (!customerData.fullName) {
    return (
      <div className="bg-white p-6 rounded-lg shadow text-center">
        <p className="text-gray-500 mb-4">Please create or select a customer to view recommendations.</p>
      </div>
    );
  }

  return (
    <div className="recommendations-container">
      <Card className="mb-6">
        <CardHeader className="pb-2">
          <CardTitle className="text-lg">Product Recommendations for {customerData.fullName}</CardTitle>
          <CardDescription>Personalized recommendations based on customer profile</CardDescription>
        </CardHeader>
        <CardContent>
          <p className="text-sm text-gray-600 mb-4">
            Based on the customer's profile, including their {customerData.vehicleType} valued at ${customerData.vehicleValue.toLocaleString()},
            {customerData.hasChildren ? ' family needs,' : ''} employment in the {customerData.employmentSector} sector,
            and annual usage of {customerData.annualKilometers} kilometers, we recommend the following products:
          </p>

          <button 
            onClick={generateRecommendations}
            disabled={isAnalyzing}
            className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 disabled:bg-blue-300 mb-4"
          >
            {isAnalyzing ? "Analyzing Customer Profile..." : "Refresh Recommendations"}
          </button>
        </CardContent>
      </Card>

      {recommendations && (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Core Products */}
          <div className="bg-white p-6 rounded-lg shadow">
            <h3 className="text-lg font-semibold mb-4 text-blue-800">Essential Products</h3>
            {recommendations.core.map(product => (
              <div key={product.id} className="mb-4 pb-3 border-b">
                <div className="flex justify-between items-start">
                  <div>
                    <h4 className="font-semibold">{product.name}</h4>
                    <p className="text-xs text-gray-600">{product.reason}</p>
                  </div>
                  <p className="font-medium">${product.monthlyEst}/month</p>
                </div>
                <div className="mt-2">
                  <p className="text-xs font-medium text-blue-600 mb-1">Key Benefits:</p>
                  <ul className="text-xs list-disc pl-4">
                    {product.benefits.map((benefit, i) => (
                      <li key={i}>{benefit}</li>
                    ))}
                  </ul>
                </div>
              </div>
            ))}
            <div className="mt-3 pt-2 border-t">
              <div className="flex justify-between items-center">
                <span className="font-medium">Monthly Total:</span>
                <span className="font-bold">${calculateTotals(recommendations.core).monthly}/month</span>
              </div>
            </div>
          </div>

          {/* Recommended Products */}
          <div className="bg-white p-6 rounded-lg shadow">
            <h3 className="text-lg font-semibold mb-4 text-green-700">Recommended Add-ons</h3>
            {recommendations.recommended.map(product => (
              <div key={product.id} className="mb-4 pb-3 border-b">
                <div className="flex justify-between items-start">
                  <div>
                    <h4 className="font-semibold">{product.name}</h4>
                    <p className="text-xs text-gray-600">{product.reason}</p>
                  </div>
                  <div className="text-right">
                    {product.oneTimeEst && (
                      <p className="text-xs text-gray-500">One-time: ${product.oneTimeEst}</p>
                    )}
                    <p className="font-medium">${product.monthlyEst}/month</p>
                  </div>
                </div>
                <div className="mt-2">
                  <p className="text-xs font-medium text-green-600 mb-1">Key Benefits:</p>
                  <ul className="text-xs list-disc pl-4">
                    {product.benefits.map((benefit, i) => (
                      <li key={i}>{benefit}</li>
                    ))}
                  </ul>
                </div>
              </div>
            ))}
            <div className="mt-3 pt-2 border-t">
              <div className="flex justify-between items-center">
                <span className="font-medium">Monthly Total:</span>
                <span className="font-bold">${calculateTotals(recommendations.recommended).monthly}/month</span>
              </div>
              {calculateTotals(recommendations.recommended).oneTime > 0 && (
                <div className="flex justify-between items-center text-sm text-gray-600">
                  <span>One-time costs:</span>
                  <span>${calculateTotals(recommendations.recommended).oneTime}</span>
                </div>
              )}
            </div>
          </div>

          {/* Optional Products */}
          <div className="bg-white p-6 rounded-lg shadow">
            <h3 className="text-lg font-semibold mb-4 text-gray-700">Optional Extras</h3>
            {recommendations.optional.map(product => (
              <div key={product.id} className="mb-4 pb-3 border-b">
                <div className="flex justify-between items-start">
                  <div>
                    <h4 className="font-semibold">{product.name}</h4>
                    <p className="text-xs text-gray-600">{product.reason}</p>
                  </div>
                  <div className="text-right">
                    {product.oneTimeEst && (
                      <p className="text-xs text-gray-500">One-time: ${product.oneTimeEst}</p>
                    )}
                    <p className="font-medium">${product.monthlyEst}/month</p>
                  </div>
                </div>
                <div className="mt-2">
                  <div className="flex justify-between">
                    <p className="text-xs font-medium text-red-600 mb-1">Without this protection:</p>
                  </div>
                  <ul className="text-xs list-disc pl-4 text-red-600">
                    {product.risks.map((risk, i) => (
                      <li key={i}>{risk}</li>
                    ))}
                  </ul>
                </div>
              </div>
            ))}
            <div className="mt-3 pt-2 border-t">
              <div className="flex justify-between items-center">
                <span className="font-medium">Monthly Total:</span>
                <span className="font-bold">${calculateTotals(recommendations.optional).monthly}/month</span>
              </div>
              {calculateTotals(recommendations.optional).oneTime > 0 && (
                <div className="flex justify-between items-center text-sm text-gray-600">
                  <span>One-time costs:</span>
                  <span>${calculateTotals(recommendations.optional).oneTime}</span>
                </div>
              )}
            </div>
          </div>

          {/* Summary Section */}
          <div className="lg:col-span-3 bg-blue-50 p-6 rounded-lg shadow">
            <h3 className="text-xl font-semibold mb-4">Package Summary</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="bg-white p-4 rounded shadow">
                <h4 className="font-medium text-blue-800">Essential Package</h4>
                <p className="text-2xl font-bold mt-2">${calculateTotals(recommendations.core).monthly}/month</p>
                <p className="text-sm text-gray-600 mt-1">Basic protection for your lease</p>
              </div>

              <div className="bg-white p-4 rounded shadow">
                <h4 className="font-medium text-green-700">Enhanced Package</h4>
                <p className="text-2xl font-bold mt-2">
                  ${calculateTotals(recommendations.core).monthly + calculateTotals(recommendations.recommended).monthly}/month
                </p>
                <p className="text-sm text-gray-600 mt-1">Recommended for your specific needs</p>
              </div>

              <div className="bg-white p-4 rounded shadow">
                <h4 className="font-medium text-purple-700">Complete Package</h4>
                <p className="text-2xl font-bold mt-2">
                  ${calculateTotals(recommendations.core).monthly + 
                    calculateTotals(recommendations.recommended).monthly + 
                    calculateTotals(recommendations.optional).monthly}/month
                </p>
                <p className="text-sm text-gray-600 mt-1">Comprehensive protection</p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductRecommendations;

