const CustomerJourney = ({ customerData, journeyStage, advanceJourney }) => {
  // Interactions history for customer journey
  const [interactions, setInteractions] = useState([
    { 
      id: 1, 
      type: "email", 
      date: "15 Feb 2025", 
      description: "Initial inquiry about novated lease options", 
      sentiment: "positive",
      notes: "Customer expressed interest in SUV models, has a budget range of $45,000-60,000" 
    },
    { 
      id: 2, 
      type: "phone", 
      date: "16 Feb 2025", 
      description: "Introductory call to explain novated lease benefits", 
      sentiment: "positive",
      notes: "Discussed tax savings, customer was particularly interested in the GST savings aspect" 
    }
  ]);

  // Form state for new interaction
  const [newInteraction, setNewInteraction] = useState({
    type: "phone",
    description: "",
    sentiment: "positive",
    notes: ""
  });

  // Handle form change
  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setNewInteraction({
      ...newInteraction,
      [name]: value
    });
  };

  // Add new interaction
  const addInteraction = (e) => {
    e.preventDefault();

    if (!newInteraction.description) {
      alert("Please provide a description");
      return;
    }

    const interaction = {
      id: interactions.length + 1,
      ...newInteraction,
      date: new Date().toLocaleDateString('en-AU', {
        day: 'numeric',
        month: 'short',
        year: 'numeric'
      })
    };

    setInteractions([interaction, ...interactions]);

    // Reset form
    setNewInteraction({
      type: "phone",
      description: "",
      sentiment: "positive",
      notes: ""
    });
  };

  if (!customerData.fullName) {
    return (
      <div className="bg-white p-6 rounded-lg shadow text-center">
        <p className="text-gray-500 mb-4">Please create or select a customer to view their journey.</p>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
      {/* Current Stage Card */}
      <div className="bg-white rounded-lg shadow p-4">
        <div className="flex justify-between items-start">
          <h3 className="text-lg font-medium">Current Stage</h3>
          <span className={`text-xs px-2 py-1 rounded-full ${
            journeyStage.riskLevel === "low" ? "bg-green-100 text-green-800" :
            journeyStage.riskLevel === "medium" ? "bg-yellow-100 text-yellow-800" :
            "bg-red-100 text-red-800"
          }`}>
            {journeyStage.riskLevel === "low" ? "Low Risk" :
             journeyStage.riskLevel === "medium" ? "Medium Risk" :
             "High Risk"}
          </span>
        </div>

        <div className="mt-4 flex space-x-4 items-center">
          <div className="p-3 bg-blue-100 rounded-full">
            {journeyStage.stageNumber === 1 ? <Phone size={24} className="text-blue-600" /> :
             journeyStage.stageNumber === 2 ? <DollarSign size={24} className="text-blue-600" /> :
             journeyStage.stageNumber === 3 ? <CheckCircle size={24} className="text-blue-600" /> :
             journeyStage.stageNumber === 4 ? <Car size={24} className="text-blue-600" /> :
             journeyStage.stageNumber === 5 ? <FileText size={24} className="text-blue-600" /> :
             journeyStage.stageNumber === 6 ? <CreditCard size={24} className="text-blue-600" /> :
             <Check size={24} className="text-blue-600" />}
          </div>

          <div>
            <h4 className="font-semibold text-lg">{journeyStage.stageName}</h4>
            <p className="text-sm text-gray-500">Last activity: {journeyStage.lastActivity}</p>
          </div>
        </div>

        <div className="mt-4">
          <div className="text-sm font-medium">Progress</div>
          <div className="h-2 w-full bg-gray-200 rounded-full overflow-hidden mt-1">
            <div 
              className="h-full bg-blue-600 rounded-full"
