          </div>
        </div>

        <div className="mt-4 p-3 bg-blue-50 rounded-lg border border-blue-100">
          <div className="text-sm font-medium mb-1">Next Action</div>
          <div className="text-sm">{journeyStage.nextAction}</div>
        </div>

        <button 
          onClick={advanceJourney}
          className="mt-4 w-full py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
          disabled={journeyStage.stageNumber === 7}
        >
          Advance to Next Stage
        </button>
      </div>

      {/* Timeline Card */}
      <div className="bg-white rounded-lg shadow p-4 md:col-span-2">
        <h3 className="text-lg font-medium mb-4">Journey Timeline</h3>

        <div className="space-y-4 max-h-96 overflow-y-auto pr-2">
          {interactions.map((interaction, index) => (
            <div key={interaction.id} className="relative">
              {/* Connecting line */}
              {index < interactions.length - 1 && (
                <div className="absolute left-3.5 top-8 h-full w-0.5 bg-gray-200"></div>
              )}

              <div className="flex space-x-3">
                {/* Icon */}
                <div className={`z-10 flex-shrink-0 w-7 h-7 rounded-full flex items-center justify-center ${
                  interaction.type === "system" ? "bg-blue-100" :
                  interaction.sentiment === "positive" ? "bg-green-100" :
                  interaction.sentiment === "negative" ? "bg-red-100" :
                  "bg-gray-100"
                }`}>
                  {interaction.type === "email" ? 
                    <Mail size={16} className="text-blue-600" /> :
                   interaction.type === "phone" ? 
                    <Phone size={16} className="text-green-600" /> :
                   interaction.type === "system" ?
                    <Check size={16} className="text-blue-600" /> :
                    <MessageCircle size={16} className="text-gray-600" />
                  }
                </div>

                {/* Content */}
                <div className="flex-1">
                  <div className="flex justify-between items-start">
                    <h4 className="font-medium">
                      {interaction.type === "email" ? "Email Communication" :
                       interaction.type === "phone" ? "Phone Conversation" :
                       interaction.type === "system" ? "System Update" :
                       "Interaction"}
                    </h4>
                    <span className="text-xs text-gray-500">{interaction.date}</span>
                  </div>
                  <p className="text-sm text-gray-600 mt-1">{interaction.description}</p>

                  {interaction.notes && (
                    <div className="mt-1 text-xs text-gray-500 bg-gray-50 p-2 rounded">
                      {interaction.notes}
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Add Interaction Form */}
      <div className="bg-white rounded-lg shadow p-4 col-span-3">
        <h3 className="text-lg font-medium mb-4">Record New Interaction</h3>

        <form onSubmit={addInteraction} className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium mb-1">Interaction Type</label>
              <select 
                name="type"
                value={newInteraction.type}
                onChange={handleFormChange}
                className="w-full p-2 border rounded"
              >
                <option value="email">Email</option>
                <option value="phone">Phone Call</option>
                <option value="meeting">In-person Meeting</option>
                <option value="other">Other</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium mb-1">Sentiment</label>
              <select 
                name="sentiment"
                value={newInteraction.sentiment}
                onChange={handleFormChange}
                className="w-full p-2 border rounded"
              >
                <option value="positive">Positive</option>
                <option value="neutral">Neutral</option>
                <option value="negative">Negative</option>
              </select>
            </div>

            <div className="md:col-span-2">
              <label className="block text-sm font-medium mb-1">Description</label>
              <input 
                type="text" 
                name="description"
                value={newInteraction.description}
                onChange={handleFormChange}
                className="w-full p-2 border rounded" 
                placeholder="Brief description of the interaction" 
              />
            </div>

            <div className="md:col-span-2">
              <label className="block text-sm font-medium mb-1">Notes</label>
              <textarea 
                name="notes"
                value={newInteraction.notes}
                onChange={handleFormChange}
                className="w-full p-2 border rounded" 
                rows="3" 
                placeholder="Detailed notes about the interaction"
              ></textarea>
            </div>
          </div>

          <div className="flex justify-end">
            <button 
              type="submit"
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              Save Interaction
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

