    { id: 2, text: "3 quotes awaiting follow-up (7+ days)", type: "warning" },
    { id: 3, text: "2 finance applications need documentation", type: "task" }
  ]);

  // AI Call Assistant state
  const [isCallActive, setIsCallActive] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [callDuration, setCallDuration] = useState(0);
  const [callAssistantExpanded, setCallAssistantExpanded] = useState(true);

  // File handling state
  const [files, setFiles] = useState([]);
  const [isDragging, setIsDragging] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);

  // Current date for dashboard
  const today = new Date();
  const dateString = today.toLocaleDateString('en-AU', {
    weekday: 'long',
    year: 'numeric',
    month: 'long',
    day: 'numeric'
  });

  // PHEV deadline calculation
  const deadline = new Date('2025-03-31');
  const daysToDeadline = Math.ceil((deadline - today) / (1000 * 60 * 60 * 24));

  // Call duration timer
  useEffect(() => {
    let timer;
    if (isCallActive) {
      timer = setInterval(() => {
        setCallDuration(prev => prev + 1);
      }, 1000);
    }

    return () => {
      if (timer) clearInterval(timer);
    };
  }, [isCallActive]);

  // Format call duration
  const formatCallDuration = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs < 10 ? '0' : ''}${secs}`;
  };

  // Handle customer input changes
  const handleCustomerInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setCustomerData({
      ...customerData,
      [name]: type === 'checkbox' ? checked : value === '' ? '' : 
              type === 'number' ? Number(value) : value
    });
  };

  // Handle file drag and drop
  const handleDragEnter = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (!isDragging) setIsDragging(true);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);

    const droppedFiles = [...e.dataTransfer.files];
    if (droppedFiles.length > 0) {
      processFiles(droppedFiles);
    }
