// OutlookService.js - Microsoft Graph API Email Integration Service

import { Client } from "@microsoft/microsoft-graph-client";
import { AuthProvider } from "@microsoft/microsoft-graph-client/authProviders/authProvider";

// This service handles the integration with Microsoft Graph API
// to sync emails between Outlook and our CRM system

class OutlookService {
  constructor() {
    this.graphClient = null;
    this.isAuthenticated = false;
    this.email = null;
  }

  // Initialize the Microsoft Graph client
  async initialize(authProvider) {
    try {
      // In a real implementation, we would use the provided authProvider
      // to authenticate with Microsoft Graph API
      this.graphClient = Client.init({
        authProvider: authProvider,
      });

      const user = await this.graphClient.api("/me").get();
      this.email = user.mail;
      this.isAuthenticated = true;
      return true;
    } catch (error) {
      console.error("Error initializing Microsoft Graph client:", error);
      this.isAuthenticated = false;
      return false;
    }
  }

  // Check if the service is authenticated
  getIsAuthenticated() {
    return this.isAuthenticated;
  }

  // Get the current user's email address
  getUserEmail() {
    return this.email;
  }

  // Fetch emails related to a specific customer
  async getCustomerEmails(customerEmail) {
    if (!this.isAuthenticated || !this.graphClient) {
      throw new Error("Not authenticated with Microsoft Graph");
    }

    try {
      // Use Microsoft Graph API to search for emails to/from the customer
      // Filtering for emails where the customer is either a sender or recipient
      const query = `recipients/any(r:r/emailAddress/address eq '${customerEmail}') or from/emailAddress/address eq '${customerEmail}'`;

      const response = await this.graphClient
        .api("/me/messages")
        .filter(query)
        .orderby("receivedDateTime desc")
        .top(50) // Limit to most recent 50 emails
        .select("id,subject,bodyPreview,from,toRecipients,receivedDateTime,isRead,hasAttachments,importance,flag")
        .get();

      // Transform the response into our application's email format
      return this.transformEmailsFromGraph(response.value, customerEmail);
    } catch (error) {
      console.error("Error fetching customer emails:", error);
      throw error;
    }
  }

  // Send an email to the customer directly from the CRM
  async sendEmail(to, subject, body, attachments = []) {
    if (!this.isAuthenticated || !this.graphClient) {
      throw new Error("Not authenticated with Microsoft Graph");
    }

    try {
      // Create the email message object
      const message = {
        subject: subject,
        body: {
          contentType: "HTML",
          content: body
        },
        toRecipients: [
          {
            emailAddress: {
              address: to
            }
          }
        ]
      };

      // Add attachments if any
      if (attachments.length > 0) {
        message.attachments = attachments.map(attachment => ({
          "@odata.type": "#microsoft.graph.fileAttachment",
          name: attachment.name,
          contentType: attachment.contentType,
          contentBytes: attachment.contentBytes // Base64 encoded content
        }));
      }

      // Send the email
      await this.graphClient
        .api("/me/sendMail")
        .post({
          message: message,
          saveToSentItems: true
        });

      return true;
    } catch (error) {
      console.error("Error sending email:", error);
      throw error;
    }
  }

  // Flag an email for follow-up
  async flagEmail(emailId, flagStatus = true) {
    if (!this.isAuthenticated || !this.graphClient) {
      throw new Error("Not authenticated with Microsoft Graph");
    }

    try {
      const flaggedValue = flagStatus ? "flagged" : "notFlagged";

      await this.graphClient
        .api(`/me/messages/${emailId}`)
        .update({
          flag: {
            flagStatus: flaggedValue
          }
        });

      return true;
    } catch (error) {
      console.error("Error flagging email:", error);
      throw error;
    }
  }

  // Transform the Graph API response into our application format
  transformEmailsFromGraph(graphEmails, customerEmail) {
    return graphEmails.map(email => {
      // Determine if this is a sent or received email
      const isReceived = email.from.emailAddress.address.toLowerCase() === customerEmail.toLowerCase();

      return {
        id: email.id,
        subject: email.subject || "(No Subject)",
        preview: email.bodyPreview || "",
        sender: email.from.emailAddress.address,
        senderName: email.from.emailAddress.name,
        recipient: email.toRecipients[0]?.emailAddress.address || "",
        recipientName: email.toRecipients[0]?.emailAddress.name || "",
        date: email.receivedDateTime,
        isRead: email.isRead,
        hasAttachments: email.hasAttachments,
        isFlagged: email.flag?.flagStatus === "flagged",
        importance: email.importance,
        direction: isReceived ? "received" : "sent"
      };
    });
  }

  // Schedule automatic email sync for a customer
  scheduleCustomerEmailSync(customerEmail, intervalMinutes = 30) {
    // In a real implementation, this would set up a recurring task
    // to sync emails for this customer at regular intervals
    console.log(`Scheduled email sync for ${customerEmail} every ${intervalMinutes} minutes`);

    // For demo purposes, let's return a mock timer ID
    return 12345;
  }

  // Cancel scheduled email sync
  cancelScheduledSync(syncId) {
    // In a real implementation, this would cancel the scheduled task
    console.log(`Cancelled scheduled sync with ID: ${syncId}`);
    return true;
  }
}

