// Utility function to attach the OutlookService to the main app
export const setupOutlookIntegration = async (authProvider) => {
  try {
    // This would be called during application initialization
    // It sets up the service with the appropriate authentication
    const initialized = await outlookService.initialize(authProvider);

    if (initialized) {
      console.log("Successfully connected to Outlook");

      // Set up event listeners for email events
      window.addEventListener('outlook:newemail', async (event) => {
        const { customerEmail } = event.detail;
        // Trigger a sync for this customer
        await syncEmailsForCustomer(customerEmail);
      });
    }

    return initialized;
  } catch (error) {
    console.error("Failed to initialize Outlook integration:", error);
    return false;
  }
};

// Sync emails for a specific customer
export const syncEmailsForCustomer = async (customerEmail) => {
  try {
    if (!outlookService.getIsAuthenticated()) {
      console.warn("Cannot sync emails: Not authenticated with Outlook");
      return null;
    }

    // Get emails for this customer
    const emails = await outlookService.getCustomerEmails(customerEmail);

    // In a real implementation, we would store these in the CRM database
    // For now, we'll just return them
    return emails;
  } catch (error) {
    console.error("Error syncing emails for customer:", error);
    return null;
  }
};

// Send an email to a customer and automatically sync it
export const sendEmailToCustomer = async (customerEmail, subject, body, attachments = []) => {
  try {
    if (!outlookService.getIsAuthenticated()) {
      console.warn("Cannot send email: Not authenticated with Outlook");
      return false;
    }

    // Send the email via Outlook
    await outlookService.sendEmail(customerEmail, subject, body, attachments);

    // Trigger a sync to update our local copy
    await syncEmailsForCustomer(customerEmail);

    return true;
  } catch (error) {
    console.error("Error sending email to customer:", error);
    return false;
  }
};

// Enable automatic email syncing for a customer
export const enableEmailAutoSync = (customerEmail, intervalMinutes = 30) => {
  if (!outlookService.getIsAuthenticated()) {
    console.warn("Cannot set up auto-sync: Not authenticated with Outlook");
    return null;
  }

  return outlookService.scheduleCustomerEmailSync(customerEmail, intervalMinutes);
};

