// Dynamic checklist system that adapts based on transaction type
class NovLeaseChecklist {
  constructor(transactionType, vehicleType, employerType) {
    this.transactionType = transactionType;
    this.vehicleType = vehicleType;
    this.employerType = employerType;
    this.checklistItems = [];
    this.generateChecklist();
  }

  generateChecklist() {
    // Add base requirements for all transaction types
    this.addBaseRequirements();

    // Add transaction-specific requirements
    switch(this.transactionType) {
      case 'dealerPurchase':
        this.addDealerPurchaseRequirements();
        break;
      case 'privateSale':
        this.addPrivateSaleRequirements();
        break;
      case 'saleAndLeaseback':
        this.addSaleAndLeasebackRequirements();
        break;
      case 'companySale':
        this.addCompanySaleRequirements();
        break;
      case 'refinance':
        this.addRefinanceRequirements();
        break;
    }

    // Add vehicle-specific requirements
    if (this.vehicleType === 'phev' || this.vehicleType === 'ev') {
      this.addElectricVehicleRequirements();
    }

    // Add employer-specific requirements
    this.addEmployerSpecificRequirements();
  }

  addBaseRequirements() {
    this.checklistItems.push(
      { id: 'driverLicense', title: 'Driver\'s License', required: true, category: 'identity', status: 'pending' },
      { id: 'payslips', title: 'Recent Payslips (2)', required: true, category: 'income', status: 'pending' },
      { id: 'directDebit', title: 'Direct Debit Request', required: true, category: 'finance', status: 'pending' },
      { id: 'novationAgreement', title: 'Novation Agreement', required: true, category: 'legal', status: 'pending' }
    );
  }

  addDealerPurchaseRequirements() {
    this.checklistItems.push(
      { id: 'dealerQuote', title: 'Dealer Quote on Letterhead', required: true, category: 'vehicle', status: 'pending' },
      { id: 'orderConfirmation', title: 'Order Confirmation', required: true, category: 'vehicle', status: 'pending' }
    );
  }

  addPrivateSaleRequirements() {
    this.checklistItems.push(
      { id: 'registrationPapers', title: 'Registration Papers', required: true, category: 'vehicle', status: 'pending' },
      { id: 'ppsr', title: 'PPSR Check', required: true, category: 'legal', status: 'pending' },
      { id: 'vendorInvoice', title: 'Invoice Signed by Vendor', required: true, category: 'finance', status: 'pending' },
      { id: 'statementBySupplier', title: 'Statement by Supplier', required: true, category: 'finance', status: 'pending' },
      { id: 'vendorBankDetails', title: 'Vendor Bank Details', required: true, category: 'finance', status: 'pending' },
      { id: 'odometerReading', title: 'Current Odometer Reading', required: true, category: 'vehicle', status: 'pending' }
    );

    // Add conditional requirements
    this.checklistItems.push(
      { 
        id: 'payoutLetter', 
        title: 'Payout Letter (if financed)', 
        required: false, 
        conditional: true,
        conditionField: 'isFinanced',
        category: 'finance', 
        status: 'pending' 
      },
      { 
        id: 'inspectionReport', 
        title: 'Inspection Report (if >$60K)', 
        required: false, 
        conditional: true,
        conditionField: 'isOver60K',
        category: 'vehicle', 
        status: 'pending' 
      }
    );
  }

  addSaleAndLeasebackRequirements() {
    this.checklistItems.push(
      { id: 'registrationPapers', title: 'Registration Papers in Driver\'s Name', required: true, category: 'vehicle', status: 'pending' },
      { id: 'ppsrClear', title: 'PPSR Showing Clear Title', required: true, category: 'legal', status: 'pending' },
      { id: 'proofOfPurchase', title: 'Proof of Purchase OR Statutory Declaration', required: true, category: 'finance', status: 'pending' },
      { id: 'statementBySupplier', title: 'Statement by Supplier', required: true, category: 'finance', status: 'pending' },
      { id: 'driverBankDetails', title: 'Driver Bank Details', required: true, category: 'finance', status: 'pending' },
      { id: 'odometerReading', title: 'Current Odometer Reading', required: true, category: 'vehicle', status: 'pending' }
    );
  }

  addCompanySaleRequirements() {
    this.checklistItems.push(
      { id: 'abnVerification', title: 'ABN Verification', required: true, category: 'legal', status: 'pending' },
      { id: 'gstStatus', title: 'GST Registration Status Check', required: true, category: 'finance', status: 'pending' },
      { id: 'registrationPapers', title: 'Registration Papers', required: true, category: 'vehicle', status: 'pending' },
      { id: 'ppsr', title: 'PPSR Check', required: true, category: 'legal', status: 'pending' },
      { id: 'authorizedSignatory', title: 'Authorized Signatory Verification', required: true, category: 'legal', status: 'pending' },
      { id: 'vendorBankDetails', title: 'Vendor Bank Details', required: true, category: 'finance', status: 'pending' },
      { id: 'odometerReading', title: 'Current Odometer Reading', required: true, category: 'vehicle', status: 'pending' }
    );

    // Add conditional requirements based on GST status
    this.checklistItems.push(
      { 
        id: 'taxInvoice', 
        title: 'Tax Invoice (if GST registered)', 
        required: false, 
        conditional: true,
        conditionField: 'isGstRegistered',
        category: 'finance', 
        status: 'pending' 
      },
      { 
        id: 'nonGstInvoice', 
        title: 'Invoice with no GST + Statement by Supplier', 
        required: false, 
        conditional: true,
        conditionField: 'isNotGstRegistered',
        category: 'finance', 
        status: 'pending' 
      }
    );
  }

  addRefinanceRequirements() {
    this.checklistItems.push(
      { id: 'residualValue', title: 'Residual Value Verification', required: true, category: 'finance', status: 'pending' },
      { id: 'existingLeaseDetails', title: 'Existing Lease Analysis', required: true, category: 'finance', status: 'pending' },
      { id: 'payoutConfirmation', title: 'Payout Confirmation', required: true, category: 'finance', status: 'pending' }
    );
  }

  addElectricVehicleRequirements() {
    this.checklistItems.push(
      { id: 'fbtExemption', title: 'FBT Exemption Documentation', required: true, category: 'tax', status: 'pending' },
      { id: 'deliveryDeadline', title: 'March 31, 2025 Delivery Verification', required: true, category: 'vehicle', status: 'pending' }
    );
  }

  addEmployerSpecificRequirements() {
    switch(this.employerType) {
      case 'standard':
        // No additional requirements
        break;
      case 'government':
        this.checklistItems.push(
          { id: 'fbtExemptionCap', title: 'FBT Exemption Cap Documentation', required: true, category: 'tax', status: 'pending' }
        );
        break;
      case 'act-gov':
        this.checklistItems.push(
          { id: 'deedOfNovation', title: 'Deed of Novation (wet signatures)', required: true, category: 'legal', status: 'pending' }
        );
        break;
      case 'nsw-police':
        this.checklistItems.push(
          { id: 'witnessedNovation', title: 'Witnessed Novation Agreement', required: true, category: 'legal', status: 'pending' },
          { id: 'authorizedSignatory', title: 'Authorized Personnel Signature', required: true, category: 'legal', status: 'pending' }
        );
        break;
      case 'defence':
        this.checklistItems.push(
          { id: 'commandingOfficer', title: 'Commanding Officer Signature', required: true, category: 'legal', status: 'pending' }
        );
        break;
      case 'asc':
        this.checklistItems.push(
          { id: 'ascIndemnity', title: 'ASC Indemnity Document', required: true, category: 'legal', status: 'pending' }
        );
        break;
      case 'westpac':
        this.checklistItems.push(
          { id: 'smartSalary', title: 'Smart Salary Account Setup', required: true, category: 'finance', status: 'pending' }
        );
        break;
    }
  }

  updateItemStatus(itemId, newStatus) {
    const item = this.checklistItems.find(item => item.id === itemId);
    if (item) {
      item.status = newStatus;
      return true;
    }
    return false;
  }

  getIncompleteItems() {
    return this.checklistItems.filter(item => 
      item.required && item.status !== 'complete'
    );
  }

  getChecklist() {
    return this.checklistItems;
  }

  getChecklistByCategory() {
    const categories = {};
    this.checklistItems.forEach(item => {
      if (!categories[item.category]) {
        categories[item.category] = [];
      }
      categories[item.category].push(item);
    });
    return categories;
  }

  isComplete() {
    return this.checklistItems
      .filter(item => item.required)
      .every(item => item.status === 'complete');
  }
}

