import React, { createContext, useContext, useReducer, useEffect } from "react";

// Define initial states
const initialCustomerState = {
  personalInfo: {
    name: "",
    email: "",
    phone: "",
    address: "",
  },
  vehicleInfo: {
    currentVehicle: "",
    makeOfInterest: "",
    modelOfInterest: "",
    colorPreference: "",
    budgetRange: "",
  },
  employmentInfo: {
    employer: "",
    position: "",
    annualSalary: "",
    payCycle: "fortnightly", // default
    employmentLength: "",
  },
  leaseDetails: {
    term: 60, // 5 years default
    annualKm: 15000,
    includeFuel: true,
    includeInsurance: true,
    includeServicing: true,
    includeRegistration: true,
  },
  leadStatus: {
    type: "", // 'fresh-incoming', 'fresh-outbound', 'existing-incoming', etc.
    stage: "new", // 'new', 'contacted', 'quote-sent', 'follow-up', 'closed'
    priority: "medium", // 'low', 'medium', 'high'
    knowledgeLevel: "", // 'clueless', 'basic', 'informed'
    notes: [],
  },
  timestamp: null
};

const initialUIState = {
  sidePanelVisible: true,
  sidePanelWidth: 350, // default width in pixels
  activeTab: "dashboardTab",
  activeScriptFlow: null,
  scriptHistory: [],
  showNewLeadModal: false,
  isEditingCustomer: false,
  lastAction: null,
  notifications: []
};

// Define reducers
const customerReducer = (state, action) => {
  switch (action.type) {
    case "UPDATE_PERSONAL_INFO":
      return {
        ...state,
        personalInfo: { ...state.personalInfo, ...action.payload },
        timestamp: Date.now()
      };
    case "UPDATE_VEHICLE_INFO":
      return {
        ...state,
        vehicleInfo: { ...state.vehicleInfo, ...action.payload },
        timestamp: Date.now()
      };
    case "UPDATE_EMPLOYMENT_INFO":
      return {
        ...state,
        employmentInfo: { ...state.employmentInfo, ...action.payload },
        timestamp: Date.now()
      };
    case "UPDATE_LEASE_DETAILS":
      return {
        ...state,
        leaseDetails: { ...state.leaseDetails, ...action.payload },
        timestamp: Date.now()
      };
    case "UPDATE_LEAD_STATUS":
      return {
        ...state,
        leadStatus: { ...state.leadStatus, ...action.payload },
        timestamp: Date.now()
      };
    case "ADD_NOTE":
      return {
        ...state,
        leadStatus: {
          ...state.leadStatus,
          notes: [
            ...state.leadStatus.notes,
            { id: Date.now(), text: action.payload, timestamp: new Date().toISOString() }
          ]
        },
        timestamp: Date.now()
      };
    case "RESET_CUSTOMER":
      return { ...initialCustomerState, timestamp: Date.now() };
    case "LOAD_CUSTOMER":
      return { ...action.payload, timestamp: Date.now() };
    default:
      return state;
  }
};

const uiReducer = (state, action) => {
  switch (action.type) {
    case "SET_ACTIVE_TAB":
      return { ...state, activeTab: action.payload };
    case "TOGGLE_SIDE_PANEL":
      return { ...state, sidePanelVisible: !state.sidePanelVisible };
    case "SET_SIDE_PANEL":
      return { ...state, sidePanelVisible: action.payload };
    case "RESIZE_SIDE_PANEL":
      return { ...state, sidePanelWidth: action.payload };
    case "SET_SCRIPT_FLOW":
      return {
        ...state,
        activeScriptFlow: action.payload,
        scriptHistory: [...state.scriptHistory, state.activeScriptFlow].filter(Boolean)
      };
    case "SCRIPT_BACK":
      const previousScript = state.scriptHistory.pop() || null;
      return {
        ...state,
        activeScriptFlow: previousScript,
        scriptHistory: [...state.scriptHistory]
      };
    case "TOGGLE_NEW_LEAD_MODAL":
      return { ...state, showNewLeadModal: !state.showNewLeadModal };
    case "SET_EDITING_CUSTOMER":
      return { ...state, isEditingCustomer: action.payload };
    case "LOG_ACTION":
      return {
        ...state,
        lastAction: { type: action.payload, timestamp: Date.now() }
      };
    case "ADD_NOTIFICATION":
      return {
        ...state,
        notifications: [...state.notifications, { id: Date.now(), ...action.payload }]
      };
    case "REMOVE_NOTIFICATION":
      return {
        ...state,
        notifications: state.notifications.filter(n => n.id !== action.payload)
      };
    default:
      return state;
  }
};

// Create contexts
const CustomerContext = createContext();
const UIContext = createContext();

// Context providers
export const CustomerProvider = ({ children }) => {
  const [customerState, customerDispatch] = useReducer(customerReducer, initialCustomerState);

  // Load customer data from localStorage on initial render
  useEffect(() => {
    const savedCustomer = localStorage.getItem("currentCustomer");
    if (savedCustomer) {
      try {
        const parsedData = JSON.parse(savedCustomer);
        customerDispatch({ type: "LOAD_CUSTOMER", payload: parsedData });
      } catch (error) {
        console.error("Failed to parse saved customer data:", error);
      }
    }
  }, []);

  // Save customer data to localStorage whenever it changes
  useEffect(() => {
    if (customerState.timestamp) {
      localStorage.setItem("currentCustomer", JSON.stringify(customerState));
    }
  }, [customerState]);

  return (
    <CustomerContext.Provider value={{ customerState, customerDispatch }}>
      {children}
    </CustomerContext.Provider>
  );
};

export const UIProvider = ({ children }) => {
  const [uiState, uiDispatch] = useReducer(uiReducer, initialUIState);

  return (
    <UIContext.Provider value={{ uiState, uiDispatch }}>
      {children}
    </UIContext.Provider>
  );
};

// Custom hooks for using the contexts
export const useCustomer = () => {
  const context = useContext(CustomerContext);
  if (!context) {
    throw new Error("useCustomer must be used within a CustomerProvider");
  }
  return context;
};

export const useUI = () => {
  const context = useContext(UIContext);
  if (!context) {
    throw new Error("useUI must be used within a UIProvider");
  }
  return context;
};

// Combined provider for convenience
export const AppProvider = ({ children }) => {
  return (
    <CustomerProvider>
      <UIProvider>{children}</UIProvider>
    </CustomerProvider>
  );
};

