import React, { useState } from "react";
import "/src/styles/Calculator.css";

const Calculator = () => {
  const [formValues, setFormValues] = useState({
    vehiclePrice: 40000,
    leaseTerm: 60, // 5 years in months
    annualSalary: 80000,
    annualKm: 15000,
  });

  const [calculationResults, setCalculationResults] = useState({
    monthlyPayment: 754.25,
    carLoanPayment: 892.5,
    cashPurchasePayment: 910.75,
    monthlySavings: 138.25,
    termSavings: 8295,
  });

  const handleInputChange = (e) => {
    const { id, value } = e.target;
    setFormValues({
      ...formValues,
      [id]: value,
    });
  };

  const calculateQuote = () => {
    // In a real app, this would do actual calculations
    // For this example, we'll just update with some fixed values

    // Small random adjustment to make it look like it's calculating
    const adjustment = Math.random() * 20 - 10; // Random number between -10 and 10

    setCalculationResults({
      monthlyPayment: (754.25 + adjustment).toFixed(2),
      carLoanPayment: (892.5 + adjustment).toFixed(2),
      cashPurchasePayment: (910.75 + adjustment).toFixed(2),
      monthlySavings: (138.25 + adjustment / 2).toFixed(2),
      termSavings: (8295 + (adjustment * 60) / 2).toFixed(0),
    });
  };

  return (
    <div className="card">
      <h2>Novated Lease Calculator</h2>

      <div className="calculator-form">
        <div className="form-group">
          <label htmlFor="vehiclePrice">Vehicle Price ($)</label>
          <input
            type="number"
            id="vehiclePrice"
            value={formValues.vehiclePrice}
            onChange={handleInputChange}
            min="0"
            step="1000"
          />
        </div>

        <div className="form-group">
          <label htmlFor="leaseTerm">Lease Term</label>
          <select
            id="leaseTerm"
            value={formValues.leaseTerm}
            onChange={handleInputChange}
          >
            <option value="12">1 Year</option>
            <option value="24">2 Years</option>
            <option value="36">3 Years</option>
            <option value="48">4 Years</option>
            <option value="60">5 Years</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="annualSalary">Annual Salary ($)</label>
          <input
            type="number"
            id="annualSalary"
            value={formValues.annualSalary}
            onChange={handleInputChange}
            min="0"
            step="5000"
          />
        </div>

        <div className="form-group">
          <label htmlFor="annualKm">Annual Kilometers</label>
          <input
            type="number"
            id="annualKm"
            value={formValues.annualKm}
            onChange={handleInputChange}
            min="0"
            step="1000"
          />
        </div>
      </div>

      <button onClick={calculateQuote} className="primary-btn">
        Calculate Quote
      </button>

      <div className="calculator-result">
        <h3>Estimated Monthly Payment</h3>
        <div className="amount">${calculationResults.monthlyPayment}</div>
        <p>This includes all running costs and tax benefits</p>
      </div>

      <div className="comparison-results">
        <h3>Comparison with Other Options</h3>
        <p>
          <strong>Car Loan:</strong> ${calculationResults.carLoanPayment}/month
        </p>
        <p>
          <strong>Cash Purchase:</strong> $
          {calculationResults.cashPurchasePayment}/month
        </p>
        <p>
          <strong>Savings vs Car Loan:</strong> $
          {calculationResults.monthlySavings}/month ($
          {calculationResults.termSavings} over term)
        </p>
      </div>
    </div>
  );
};

export default Calculator;

import React, { useState, useEffect } from "react";

function CustomerManagement() {
  const [customers, setCustomers] = useState([]);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");

  useEffect(() => {
    const savedCustomers = localStorage.getItem("customers");
    if (savedCustomers) {
      setCustomers(JSON.parse(savedCustomers));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("customers", JSON.stringify(customers));
  }, [customers]);

  const handleSubmit = (event) => {
    event.preventDefault();

    const newCustomer = {
      id: Date.now(),
      name,
      email,
      phone,
      status: "green",
    };

    setCustomers([...customers, newCustomer]);
    setName("");
    setEmail("");
    setPhone("");
  };

  return (
    <div className="card">
      <h2>Add New Customer</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-field">
          <label htmlFor="name">Customer Name</label>
          <input id="name" type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        </div>

        <div className="form-field">
          <label htmlFor="email">Email</label>
          <input id="email" type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>

        <div className="form-field">
          <label htmlFor="phone">Phone</label>
          <input id="phone" type="tel" value={phone} onChange={(e) => setPhone(e.target.value)} />
        </div>

        <button type="submit" className="primary-btn">Add Customer</button>
      </form>

      <div className="customer-count">Total customers: {customers.length}</div>
    </div>
  );
}

export default CustomerManagement;

import React, { useState } from "react";
import "../styles/CustomerManagement.css";

const CustomersList = () => {
  const [customers, setCustomers] = useState([
    { id: 1, name: "John Smith", vehicleInterest: "Toyota RAV4", phone: "(04) 1234 5678", email: "john.smith@example.com", status: "hot" },
    { id: 2, name: "Emily Johnson", vehicleInterest: "Mazda CX-5", phone: "(04) 8765 4321", email: "emily.j@example.com", status: "active" },
    { id: 3, name: "Michael Williams", vehicleInterest: "Ford Ranger", phone: "(04) 2468 1357", email: "m.williams@example.com", status: "new" },
    { id: 4, name: "Sarah Brown", vehicleInterest: "Hyundai Tucson", phone: "(04) 1357 2468", email: "sarah.b@example.com", status: "active" },
  ]);

  const [selectedCustomer, setSelectedCustomer] = useState(null);

  const handleCustomerClick = (customer) => {
    setSelectedCustomer(customer);
  };

  return (
    <div className="card">
      <h2>Your Customers</h2>
      <p>Manage your customer leads and active deals.</p>

      <div className="customer-list">
        {customers.map((customer) => (
          <div key={customer.id} className="customer-card" onClick={() => handleCustomerClick(customer)}>
            <div className="customer-name">{customer.name}</div>
            <div className="customer-details">
              <div>Vehicle Interest: {customer.vehicleInterest}</div>
              <div>Phone: {customer.phone}</div>
              <div>Email: {customer.email}</div>
            </div>
            <span className={`customer-status status-${customer.status}`}>
              {customer.status === "hot" && "Hot Lead"}
              {customer.status === "active" && "Active Deal"}
              {customer.status === "new" && "New Inquiry"}
            </span>
          </div>
        ))}
      </div>

      {selectedCustomer && (
        <div className="customer-details-modal">
          <div className="modal-content">
            <h3>{selectedCustomer.name}</h3>
            {/* Additional customer details would go here */}
            <button onClick={() => setSelectedCustomer(null)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CustomersList;

