  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  background: var(--background-color);
  border-radius: 50%;
  margin-right: var(--spacing-md);
  color: var(--text-light);
}

.phase-title {
  margin: 0;
  font-family: var(--title-font);
  flex: 1;
}

.phase-status {
  font-size: 0.8rem;
  padding: 0.25rem 0.5rem;
  border-radius: 4px;
  font-weight: 600;
}

.phase-status.not-started {
