}

.template-selection label {
  display: block;
  margin-bottom: var(--spacing-xs);
  font-weight: 600;
}

.template-selection select {
  width: 100%;
  padding: 0.8rem;
  border: 1px solid var(--border-color);
  border-radius: var(--border-radius-sm);
  margin-bottom: var(--spacing-md);
}

.template-subject {
  margin-bottom: var(--spacing-md);
  display: flex;
