  font-weight: bold;
  margin-bottom: var(--spacing-xs);
}

.kpi-target {
  font-size: 0.9rem;
  color: var(--text-light);
  margin-bottom: var(--spacing-sm);
}

.progress-bar {
  height: 6px;
  background: var(--border-color);
  border-radius: 3px;
  overflow: hidden;
}

.progress-fill {
  height: 100%;
  background: var(--success-color);
}

/* =========================================================
