  margin-bottom: var(--spacing-sm);
  color: var(--primary-color);
  border-bottom: 1px solid var(--border-color);
  padding-bottom: var(--spacing-xs);
}

.checklist-item {
  display: flex;
  align-items: center;
  padding: var(--spacing-sm) 0;
  border-bottom: 1px dashed var(--border-color);
}

.checklist-item:last-child {
  border-bottom: none;
}

.checklist-item input[type="checkbox"] {
  margin-right: var(--spacing-sm);
}

.required-tag {
  background: #f8d7da;
  color: #721c24;
  padding: 0.1rem 0.3rem;
  border-radius: 3px;
  font-size: 0.7rem;
  margin-left: var(--spacing-sm);
}

.drop-zone {
  border: 2px dashed var(--border-color);
  border-radius: var(--border-radius-md);
  padding: var(--spacing-xl);
  text-align: center;
  transition: all 0.3s ease;
  cursor: pointer;
  margin-bottom: var(--spacing-lg);
}

.drop-zone:hover,
.drop-zone.drag-over {
  border-color: var(--primary-color);
  background: var(--primary-light);
}

.drop-zone i {
  font-size: 2.5rem;
  color: var(--primary-color);
  margin-bottom: var(--spacing-md);
}

.upload-btn {
  display: inline-block;
  padding: 0.6rem 1.2rem;
  background: var(--primary-color);
  color: white;
  border-radius: var(--border-radius-sm);
  margin-top: var(--spacing-md);
  cursor: pointer;
  transition: all 0.3s ease;
}

.upload-btn:hover {
  background: var(--primary-dark);
}

.document-items {
  margin-top: var(--spacing-md);
}

.document-item {
  display: flex;
  align-items: center;
  padding: 0.8rem;
  border: 1px solid var(--border-color);
  border-radius: var(--border-radius-sm);
  margin-bottom: var(--spacing-sm);
  transition: background 0.2s;
}

.document-item:hover {
  background: var(--background-color);
}

.document-icon {
  width: 40px;
  height: 40px;
  border-radius: var(--border-radius-sm);
  background: #f0f0f0;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: var(--spacing-md);
  color: var(--primary-color);
}

.document-info {
  flex: 1;
}

.document-name {
  font-weight: 600;
  margin-bottom: 0.25rem;
}

.document-meta {
  font-size: 0.8rem;
  color: var(--text-light);
  display: flex;
  gap: var(--spacing-md);
}

.document-actions {
  display: flex;
  gap: var(--spacing-sm);
}

.document-action-btn {
  background: none;
  border: none;
  color: var(--text-light);
  cursor: pointer;
  font-size: 1rem;
  transition: color 0.2s;
}

.document-action-btn:hover {
  color: var(--primary-color);
}

/* =========================================================
       12. Modal Styles
       ========================================================= */
.modal {
  position: fixed;
  z-index: 100;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  overflow: auto;
  background: rgba(0, 0, 0, 0.6);
  padding-top: 60px;
  display: none;
}

.modal-content {
  background: linear-gradient(135deg, #ffffff, #f0f0f0);
  margin: 5% auto;
  padding: var(--spacing-lg);
  border: 1px solid #888;
  width: 90%;
  max-width: 600px;
  border-radius: var(--border-radius-md);
  position: relative;
  box-shadow: 0 6px 12px rgba(0, 0, 0, 0.5);
}

.modal-content h2 {
  margin-bottom: var(--spacing-md);
  font-family: var(--title-font);
  color: var(--primary-color);
}

.close {
  color: #aaa;
  position: absolute;
  right: var(--spacing-md);
  top: var(--spacing-md);
  font-size: 1.8rem;
  cursor: pointer;
  transition: color 0.2s;
}

.close:focus,
.close:hover {
  color: #000;
  outline: none;
}

.form-actions {
  display: flex;
  justify-content: flex-end;
  gap: var(--spacing-md);
  margin-top: var(--spacing-lg);
}

/* Client Data Collection Form */
.client-form-container {
  max-height: 70vh;
  overflow-y: auto;
  padding-right: var(--spacing-sm);
}

.form-section {
  margin-bottom: var(--spacing-lg);
  padding-bottom: var(--spacing-md);
  border-bottom: 1px solid var(--border-color);
}

.form-section h3 {
  font-family: var(--title-font);
  margin-bottom: var(--spacing-md);
  color: var(--primary-color);
}

.form-row {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: var(--spacing-md);
  margin-bottom: var(--spacing-md);
}

.form-field {
  display: flex;
  flex-direction: column;
}

.form-field.full-width {
  grid-column: 1 / -1;
}

.form-field label {
  margin-bottom: var(--spacing-xs);
  font-weight: 600;
}

.form-field input,
.form-field select,
.form-field textarea {
  padding: 0.7rem;
  border: 1px solid var(--border-color);
  border-radius: var(--border-radius-sm);
  font-family: var(--body-font);
}

.form-field textarea {
  resize: vertical;
  min-height: 100px;
}

.form-fields-multiple {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(180px, 1fr));
  gap: var(--spacing-sm);
}

.checkbox-field {
  display: flex;
  align-items: center;
  gap: var(--spacing-xs);
  margin-bottom: var(--spacing-sm);
}

/* =========================================================
       13. Responsive Adjustments
       ========================================================= */
@media (max-width: 768px) {
  .dashboard-header {
    flex-direction: column;
    align-items: flex-start;
    gap: var(--spacing-md);
  }

  .dashboard-actions {
    width: 100%;
  }

  .metric-card p {
    font-size: 1.5rem;
  }

  .main-tab-nav {
    flex-wrap: wrap;
  }

  .main-tab-btn {
    flex: 1 0 33.33%;
  }

  .template-buttons {
    flex-direction: column;
  }

  .profile-grid {
    grid-template-columns: 1fr;
  }

  .journey-phase::before {
    left: -10px; /* Adjust for mobile view */
  }

  .form-row {
    grid-template-columns: 1fr;
  }
}

@media (max-width: 480px) {
  .customer-list,
  .hot-leads-container {
    grid-template-columns: 1fr;
  }

  .phase-header {
    flex-wrap: wrap;
  }

  .phase-status {
    margin-top: var(--spacing-sm);
    width: 100%;
    text-align: center;
  }

  .modal-content {
    width: 95%;
    margin-top: 10%;
  }

  .form-fields-multiple {
    grid-template-columns: 1fr;
  }
}

/* =========================================================
       14. Animations
       ========================================================= */
@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

.fade-in {
  animation: fadeIn 0.3s ease-in-out;
}

@keyframes slideIn {
  from {
    transform: translateX(50px);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

.slide-in {
  animation: slideIn 0.3s ease;
}

@keyframes pulse {
  0% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.05);
  }
  100% {
    transform: scale(1);
  }
}

.pulse {
  animation: pulse 1.5s infinite;
}
