import React, { useState } from "react";
import "./styles/AI-Response-Generator.css";

function AIResponseGenerator() {
  const [customerQuery, setCustomerQuery] = useState("");
  const [response, setResponse] = useState("");

  // List of common questions users might ask
  const commonPrompts = [
    "Explain a Novated Lease",
    "How does Balance Management work?",
    "What are the tax benefits?",
    "End of lease options",
    "Employer requirements",
    "Comparing to car loan",
  ];

  // Apply a prompt when clicked
  function usePrompt(prompt) {
    setCustomerQuery(prompt);
  }

  function generateResponse() {
    if (!customerQuery.trim()) {
      setResponse("<p class='error-message'>Please enter a question first</p>");
      return;
    }

    setResponse(
      "<p><i class='fas fa-spinner fa-spin'></i> Generating response...</p>"
    );

    setTimeout(() => {
      const query = customerQuery.toLowerCase();

      if (query.includes("explain") && query.includes("novated lease")) {
        setResponse(getNovatedLeaseExplanation());
      } else if (query.includes("balance") && query.includes("management")) {
        setResponse(getBalanceManagementExplanation());
      } else if (query.includes("tax") || query.includes("benefit")) {
        setResponse(getTaxBenefitResponse());
      } else if (query.includes("expensive") || query.includes("cost")) {
        setResponse(getCostResponse());
      } else if (query.includes("loan") || query.includes("comparison")) {
        setResponse(getLoanComparisonResponse());
      } else if (query.includes("end of lease") || query.includes("residual")) {
        setResponse(getEndOfLeaseResponse());
      } else if (query.includes("employer") || query.includes("company")) {
        setResponse(getEmployerResponse());
      } else {
        setResponse(getGenericResponse(customerQuery));
      }
    }, 800);
  }

  // New response for explaining novated leases
  function getNovatedLeaseExplanation() {
    return `
      <h4>Novated Lease Explanation:</h4>
      <div class="response-section">
        <p class="response-intro">A novated lease is a three-way agreement between you, your employer, and a finance company:</p>
        <ul class="response-points">
          <li><strong>How it works:</strong> "Your employer makes lease payments on your behalf directly from your salary."</li>
          <li><strong>Pre-tax benefits:</strong> "Part of the payment comes from your pre-tax salary, reducing your taxable income."</li>
          <li><strong>Complete package:</strong> "Unlike regular car financing, a novated lease can include all running costs like fuel, insurance, and maintenance."</li>
