/**
 * Template processing function to replace placeholders with data
 * @param {string} template - String with placeholders in {{key}} format
 * @param {object} data - Data object to pull values from
 * @returns {string} - Processed string with placeholders replaced
 */
export const processTemplate = (template, data) => {
  if (!template) return "";

  return template.replace(/\{\{([^}]+)\}\}/g, (match, key) => {
    // Split the key by dots to handle nested properties
    const keys = key.trim().split('.');
    let value = data;

    // Traverse the object using the key path
    for (const k of keys) {
      if (!value || value[k] === undefined) return match;
      value = value[k];
    }

    return value || match;
  });
};

/**
 * Format a lead type string into a readable format
 * @param {string} type - Lead type identifier
 * @returns {string} - Human-readable lead type description
 */
export const formatLeadType = (type) => {
  switch (type) {
    case "fresh-incoming":
      return "Fresh Incoming Lead";
    case "fresh-outbound":
      return "Fresh Outbound Lead";
    case "existing-incoming":
      return "Existing Incoming Lead";
    case "existing-outbound":
      return "Existing Outbound Lead";
    default:
      return type || "New Lead";
  }
};

/**
 * Format currency values consistently
 * @param {number} value - Numeric value to format as currency
 * @param {boolean} showCents - Whether to show cents
 * @returns {string} - Formatted currency string
 */
export const formatCurrency = (value, showCents = true) => {
  if (!value && value !== 0) return "$0";

  const formatter = new Intl.NumberFormat('en-AU', {
    style: 'currency',
    currency: 'AUD',
    minimumFractionDigits: showCents ? 2 : 0,
    maximumFractionDigits: showCents ? 2 : 0
  });

  return formatter.format(value);
};

/**
 * Format a date consistently
 * @param {string|Date} date - Date to format
 * @param {boolean} includeTime - Whether to include time
 * @returns {string} - Formatted date string
 */
export const formatDate = (date, includeTime = false) => {
  if (!date) return "";

  const dateObj = typeof date === 'string' ? new Date(date) : date;

  if (includeTime) {
    return dateObj.toLocaleString('en-AU', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  }

  return dateObj.toLocaleDateString('en-AU', {
    day: '2-digit',
    month: '2-digit',
    year: 'numeric'
  });
};

/**
 * Calculate residual value based on ATO guidelines
 * @param {number} vehiclePrice - Original vehicle price
 * @param {number} termMonths - Lease term in months
 * @returns {number} - Calculated residual value
 */
export const calculateResidualValue = (vehiclePrice, termMonths) => {
  const termYears = termMonths / 12;
  let residualPercentage;

  // ATO guidelines for residual value percentage
  switch (termYears) {
    case 1:
      residualPercentage = 0.65625; // 65.625%
      break;
    case 2:
      residualPercentage = 0.4375; // 43.75%
      break;
    case 3:
      residualPercentage = 0.28125; // 28.125%
      break;
    case 4:
      residualPercentage = 0.1875; // 18.75%
      break;
    case 5:
      residualPercentage = 0.15625; // 15.625%
      break;
    default:
      residualPercentage = 0.15625; // Default to 5 years
  }

  return vehiclePrice * residualPercentage;
};

/**
 * Get tax rate based on income bracket
 * @param {number} annualSalary - Annual salary
 * @returns {number} - Applicable tax rate (0-1)
 */
export const getTaxRate = (annualSalary) => {
  // Australian tax brackets (simplified)
  if (annualSalary <= 18200) return 0;
  if (annualSalary <= 45000) return 0.19;
  if (annualSalary <= 120000) return 0.325;
  if (annualSalary <= 180000) return 0.37;
  return 0.45;
};

/**
 * Check if a value is empty (null, undefined, empty string, or empty array/object)
 * @param {*} value - Value to check
 * @returns {boolean} - True if empty, false otherwise
 */
export const isEmpty = (value) => {
  if (value === null || value === undefined) return true;
  if (typeof value === 'string') return value.trim() === '';
  if (Array.isArray(value)) return value.length === 0;
  if (typeof value === 'object') return Object.keys(value).length === 0;
  return false;
};

/**
 * Persist data to local storage with error handling
 * @param {string} key - Storage key
 * @param {*} data - Data to store (will be JSON stringified)
 * @returns {boolean} - Success state
 */
export const saveToLocalStorage = (key, data) => {
  try {
    const serializedData = JSON.stringify(data);
    localStorage.setItem(key, serializedData);
    return true;
  } catch (error) {
    console.error(`Error saving to localStorage (${key}):`, error);
    return false;
  }
};

/**
 * Retrieve data from local storage with error handling
 * @param {string} key - Storage key
 * @param {*} defaultValue - Default value if key doesn't exist or error occurs
 * @returns {*} - Retrieved data or default value
 */
export const getFromLocalStorage = (key, defaultValue = null) => {
  try {
    const serializedData = localStorage.getItem(key);
    if (serializedData === null) return defaultValue;
    return JSON.parse(serializedData);
  } catch (error) {
    console.error(`Error retrieving from localStorage (${key}):`, error);
    return defaultValue;
  }
};

/**
 * Debounce function to limit how often a function can be called
 * @param {Function} func - Function to debounce
 * @param {number} wait - Wait time in milliseconds
 * @returns {Function} - Debounced function
 */
export const debounce = (func, wait = 300) => {
  let timeout;

  return function executedFunction(...args) {
    const later = () => {
      clearTimeout(timeout);
      func(...args);
    };

    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
};

