import React, { useState } from "react";
import { useCustomer, useUI } from "./AppContext";
import "./styles/LeadActions.css";

const LeadActions = () => {
  const { customerDispatch } = useCustomer();
  const { uiDispatch } = useUI();
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleNewLead = (leadType) => {
    // Reset customer data
    customerDispatch({ type: "RESET_CUSTOMER" });

    // Set the lead type
    customerDispatch({
      type: "UPDATE_LEAD_STATUS",
      payload: { type: leadType }
    });

    // Set UI state to show the phone script tab
    uiDispatch({ type: "SET_ACTIVE_TAB", payload: "phoneScriptTab" });

    // Start appropriate script flow based on lead type
    let initialScriptFlow = "initial";
    if (leadType === "existing-incoming" || leadType === "existing-outbound") {
      initialScriptFlow = "existing-customer";
    }

    uiDispatch({ type: "SET_SCRIPT_FLOW", payload: initialScriptFlow });

    // Make sure side panel is visible
    uiDispatch({ type: "SET_SIDE_PANEL", payload: true });

    // Close the menu
    setMenuOpen(false);

    // Log action for tracking
    uiDispatch({ 
      type: "LOG_ACTION", 
      payload: `new-lead-${leadType}` 
    });

    // Add notification
    uiDispatch({
      type: "ADD_NOTIFICATION",
      payload: {
        message: `New ${leadTypeToString(leadType)} created`,
        type: "success",
        duration: 3000
      }
    });
  };

  // Helper function to convert lead type to readable string
  const leadTypeToString = (type) => {
    switch (type) {
      case "fresh-incoming":
        return "Fresh Incoming Lead";
      case "fresh-outbound":
        return "Fresh Outbound Lead";
      case "existing-incoming":
        return "Existing Incoming Lead";
      case "existing-outbound":
        return "Existing Outbound Lead";
      default:
        return "Lead";
    }
  };

  return (
    <div className="lead-actions-container">
      {menuOpen && (
        <div className="lead-action-menu">
          <button
            className="lead-action-item"
            onClick={() => handleNewLead("fresh-incoming")}
          >
            <i className="fas fa-phone-alt"></i>
            <span>Fresh Incoming Lead</span>
          </button>
          <button
            className="lead-action-item"
            onClick={() => handleNewLead("fresh-outbound")}
          >
            <i className="fas fa-phone-volume"></i>
            <span>Fresh Outbound Lead</span>
          </button>
          <button
            className="lead-action-item"
            onClick={() => handleNewLead("existing-incoming")}
          >
            <i className="fas fa-user-clock"></i>
            <span>Existing Incoming Lead</span>
          </button>
          <button
            className="lead-action-item"
            onClick={() => handleNewLead("existing-outbound")}
          >
            <i className="fas fa-user-check"></i>
            <span>Existing Outbound Lead</span>
          </button>
        </div>
      )}

      <button 
        className={`lead-actions-button ${menuOpen ? 'active' : ''}`}
        onClick={toggleMenu}
        title="Create new lead"
      >
        {menuOpen ? (
          <i className="fas fa-times"></i>
        ) : (
          <i className="fas fa-plus"></i>
        )}
      </button>
    </div>
  );
};

export default LeadActions;

