  const { customerState } = useCustomer();
  const { uiState, uiDispatch } = useUI();

  // Set active tab handler
  const setActiveTab = (tab) => {
    uiDispatch({ type: "SET_ACTIVE_TAB", payload: tab });
  };

  // Effect to adjust main content padding based on side panel visibility
  useEffect(() => {
    const mainContent = document.querySelector('.main-content');
    if (mainContent) {
      if (uiState.sidePanelVisible) {
        mainContent.style.paddingLeft = `${uiState.sidePanelWidth}px`;
      } else {
        mainContent.style.paddingLeft = '0';
      }
    }
  }, [uiState.sidePanelVisible, uiState.sidePanelWidth]);

  return (
    <div className="novassistant-container">
      {/* Side Panel for Customer Notes */}
      <SidePanel />

      {/* Main Content Area */}
      <div className="main-content">
        <header>
          <div className="header-content">
            <h1>NovAssistant</h1>
            <div className="customer-indicator">
              {customerState.personalInfo.name && (
                <div className="active-customer">
                  <i className="fas fa-user-circle"></i>
                  <span>{customerState.personalInfo.name}</span>
                  {customerState.leadStatus.type && (
                    <span className="lead-type-badge">
                      {formatLeadType(customerState.leadStatus.type)}
                    </span>
                  )}
                </div>
              )}
            </div>
          </div>
        </header>

        <div className="tabs">
          <button
            className={`tab-btn ${uiState.activeTab === "dashboardTab" ? "active" : ""}`}
            onClick={() => setActiveTab("dashboardTab")}
          >
            <i className="fas fa-tachometer-alt"></i> Dashboard
          </button>
          <button
            className={`tab-btn ${uiState.activeTab === "phoneScriptTab" ? "active" : ""}`}
            onClick={() => setActiveTab("phoneScriptTab")}
          >
            <i className="fas fa-phone-alt"></i> Phone Scripts
          </button>
          <button
            className={`tab-btn ${uiState.activeTab === "calculatorTab" ? "active" : ""}`}
            onClick={() => setActiveTab("calculatorTab")}
          >
            <i className="fas fa-calculator"></i> Calculator
          </button>
          <button
            className={`tab-btn ${uiState.activeTab === "customersTab" ? "active" : ""}`}
            onClick={() => setActiveTab("customersTab")}
          >
            <i className="fas fa-users"></i> Customers
          </button>
          <button
            className={`tab-btn ${uiState.activeTab === "aiResponseTab" ? "active" : ""}`}
            onClick={() => setActiveTab("aiResponseTab")}
          >
            <i className="fas fa-robot"></i> AI Response Generator
          </button>
        </div>

        <div className="tab-content-container">
          {/* Dashboard Tab */}
          <div
            className={uiState.activeTab === "dashboardTab" ? "tab-content active" : "tab-content"}
          >
            <div className="dashboard-metrics">
              <div className="metric-card">
                <h3>Active Leads</h3>
                <p>24</p>
              </div>
              <div className="metric-card">
                <h3>Proposals Sent</h3>
                <p>18</p>
              </div>
              <div className="metric-card">
                <h3>Deals Closed</h3>
                <p>7</p>
              </div>
              <div className="metric-card">
                <h3>Conversion Rate</h3>
                <p>38.9%</p>
              </div>
            </div>

            <RecentActivity />
          </div>

          {/* Phone Script Tab */}
          <div
            className={uiState.activeTab === "phoneScriptTab" ? "tab-content active" : "tab-content"}
          >
            <EnhancedPhoneScript />
          </div>

          {/* Calculator Tab */}
          <div
            className={uiState.activeTab === "calculatorTab" ? "tab-content active" : "tab-content"}
          >
            <EnhancedCalculator />
          </div>

          {/* Customers Tab */}
          <div
            className={uiState.activeTab === "customersTab" ? "tab-content active" : "tab-content"}
          >
            <CustomersList />
          </div>

          {/* AI Response Generator Tab */}
          <div
            className={uiState.activeTab === "aiResponseTab" ? "tab-content active" : "tab-content"}
          >
            <AIResponseGenerator />
          </div>
        </div>
      </div>

      {/* Floating Action Button for New Leads */}
      <LeadActions />

      {/* System Notifications */}
      <Notifications />
    </div>
  );
};

// Helper function to format lead type into readable text
const formatLeadType = (type) => {
  switch (type) {
    case "fresh-incoming":
      return "Fresh Incoming Lead";
    case "fresh-outbound":
      return "Fresh Outbound Lead";
    case "existing-incoming":
      return "Existing Incoming Lead";
    case "existing-outbound":
      return "Existing Outbound Lead";
    default:
      return type;
  }
};

export default NovAssistant;

