// Function to process template strings with customer data
const processTemplate = (template, customerData) => {
  if (!template) return "";

  return template.replace(/\{\{([^}]+)\}\}/g, (match, key) => {
    // Split the key by dots to handle nested properties
    const keys = key.trim().split('.');
    let value = customerData;

    // Traverse the object using the key path
    for (const k of keys) {
      if (!value || value[k] === undefined) return match;
      value = value[k];
    }

    return value || match;
  });
};

// Main ScriptEngine component
const ScriptEngine = () => {
  const { customerState, customerDispatch } = useCustomer();
  const { uiState, uiDispatch } = useUI();

  const activeFlow = uiState.activeScriptFlow || "initial";
  const currentScript = scriptTemplates[activeFlow] || scriptTemplates.default;

  // Navigate to a different script flow
  const navigateToFlow = (flow) => {
    uiDispatch({ type: "SET_SCRIPT_FLOW", payload: flow });
  };

  // Go back to previous script
  const goBack = () => {
    uiDispatch({ type: "SCRIPT_BACK" });
  };

  // Handle navigation to calculator
  const navigateToCalculator = () => {
    uiDispatch({ type: "SET_ACTIVE_TAB", payload: "calculatorTab" });

    // Add notification
    uiDispatch({
      type: "ADD_NOTIFICATION",
      payload: {
        message: "Calculating potential savings based on your information",
        type: "info",
        duration: 3000
      }
    });
  };

  // Handle script-specific actions
  const handleScriptAction = (action) => {
    if (action === "navigateToCalculator") {
      navigateToCalculator();
    } else if (action === "sendQuoteRequest") {
      // Code to handle quote request submission
      uiDispatch({
        type: "ADD_NOTIFICATION",
        payload: {
          message: "Quote request submitted successfully!",
          type: "success",
          duration: 3000
        }
      });
    }
  };

  // Handle data collection inputs
  const handleDataInput = (field, value, type = "text") => {
    const [section, key] = field.split('.');

    // Determine which reducer action to dispatch based on the section
    let actionType;
    switch (section) {
      case "personalInfo":
        actionType = "UPDATE_PERSONAL_INFO";
        break;
      case "vehicleInfo":
        actionType = "UPDATE_VEHICLE_INFO";
        break;
      case "employmentInfo":
        actionType = "UPDATE_EMPLOYMENT_INFO";
        break;
      case "leaseDetails":
        actionType = "UPDATE_LEASE_DETAILS";
        break;
      case "leadStatus":
        actionType = "UPDATE_LEAD_STATUS";
        break;
      default:
        console.warn(`Unknown section: ${section}`);
        return;
    }

    // Handle checkbox inputs differently
    const processedValue = type === "checkbox" ? value === "true" : value;

    customerDispatch({
      type: actionType,
      payload: { [key]: processedValue }
    });
  };

  // Process all templated text in the current script
  const processAllTemplates = (script) => {
    if (!script) return script;

    const result = { ...script };

    // Process title and intro
    if (result.title) result.title = processTemplate(result.title, customerState);
    if (result.intro) result.intro = processTemplate(result.intro, customerState);
    if (result.question) result.question = processTemplate(result.question, customerState);

    // Process sections
    if (result.sections) {
      result.sections = result.sections.map(section => ({
        ...section,
        title: processTemplate(section.title, customerState),
        script: section.script.map(line => processTemplate(line, customerState))
      }));
    }

    // Process general script
    if (result.generalScript) {
      result.generalScript = result.generalScript.map(line => processTemplate(line, customerState));
    }

    return result;
  };

  // Process the current script with customer data
  const processedScript = processAllTemplates(currentScript);

  // Determine if there are missing fields that should be collected
  const getMissingFields = () => {
    if (!currentScript.dataCollection) return [];

    return currentScript.dataCollection.filter(field => {
      const [section, key] = field.field.split('.');
      let value;

      switch (section) {
        case "personalInfo":
          value = customerState.personalInfo[key];
          break;
        case "vehicleInfo":
          value = customerState.vehicleInfo[key];
          break;
        case "employmentInfo":
          value = customerState.employmentInfo[key];
          break;
        case "leaseDetails":
          value = customerState.leaseDetails[key];
          break;
        case "leadStatus":
          value = customerState.leadStatus[key];
          break;
        default:
          return true; // Consider unknown fields as missing
      }

      return !value || value === "";
    });
  };

  const missingFields = getMissingFields();

  // Side effect for script specific actions
  useEffect(() => {
    if (currentScript.calculationRequired && currentScript.calculationScript) {
      handleScriptAction(currentScript.calculationScript);
    }
  }, [activeFlow]);

  return (
    <div className="script-engine">
      <div className="script-header">
        <h3>{processedScript.title}</h3>
        {uiState.scriptHistory.length > 0 && (
          <button className="script-back-btn" onClick={goBack}>
            <i className="fas fa-arrow-left"></i> Back
          </button>
        )}
      </div>

      <div className="script-intro">
        <p>{processedScript.intro}</p>
      </div>

      {/* Script sections with word tracks */}
      {processedScript.sections && processedScript.sections.map((section, index) => (
        <div key={index} className="script-section">
          <h4>{section.title}</h4>
          <ul className="word-track-list">
            {section.script.map((line, lineIndex) => (
              <li key={lineIndex}>{line}</li>
            ))}
          </ul>
        </div>
      ))}

      {/* General script for opening */}
      {processedScript.generalScript && (
        <div className="script-section">
          <h4>General Script</h4>
          <ul className="word-track-list">
            {processedScript.generalScript.map((line, lineIndex) => (
              <li key={lineIndex}>{line}</li>
            ))}
          </ul>
        </div>
      )}

      {/* Data collection section */}
      {currentScript.dataCollection && missingFields.length > 0 && (
        <div className="data-collection-section">
          <h4>Information Collection</h4>
          <p>Collect the following information from the customer:</p>

          <div className="data-fields">
            {missingFields.map((field, index) => (
              <div key={index} className="data-field">
                <label>{field.prompt}</label>
                {field.type === "select" ? (
                  <select
                    onChange={(e) => handleDataInput(field.field, e.target.value)}
                    className="data-input"
                  >
                    <option value="">-- Select --</option>
                    {field.options.map((option, optIndex) => (
                      <option key={optIndex} value={option}>
                        {option} {field.field.includes("term") ? "Year" + (option > 1 ? "s" : "") : ""}
                      </option>
                    ))}
                  </select>
                ) : field.type === "checkbox" ? (
                  <div className="checkbox-field">
                    <input
                      type="checkbox"
                      id={`field-${index}`}
                      onChange={(e) => handleDataInput(field.field, e.target.checked.toString(), "checkbox")}
                    />
                    <label htmlFor={`field-${index}`}>{field.label || "Yes"}</label>
                  </div>
                ) : (
                  <input
                    type="text"
                    placeholder={`Enter ${field.field.split('.')[1]}`}
                    onChange={(e) => handleDataInput(field.field, e.target.value)}
                    className="data-input"
                  />
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Next step question and options */}
      {processedScript.question && (
        <div className="script-question">
          <h4>{processedScript.question}</h4>

          {processedScript.options && (
            <div className="script-options">
              {processedScript.options.map((option, index) => (
                <button
                  key={index}
                  className="script-option-btn"
                  onClick={() => navigateToFlow(option.flow)}
                >
                  <i className={`fas fa-${option.icon || 'arrow-right'}`}></i>
                  <span>{option.label}</span>
                </button>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Submit action button */}
      {currentScript.submitAction && (
        <div className="script-submit">
          <button
            className="primary-btn submit-action-btn"
            onClick={() => handleScriptAction(currentScript.submitAction)}
          >
            <i className="fas fa-paper-plane"></i>
            <span>Submit Quote Request</span>
          </button>
        </div>
      )}
    </div>
  );
};

export default ScriptEngine;

import React, { useEffect } from "react";
import { useUI } from "./AppContext";

const Notifications = () => {
  const { uiState, uiDispatch } = useUI();
  const { notifications } = uiState;

  // Remove notification after specified duration
  useEffect(() => {
    if (notifications.length === 0) return;

    const timeouts = notifications.map(notification => {
      if (!notification.duration) return null;

      return setTimeout(() => {
        uiDispatch({ 
          type: "REMOVE_NOTIFICATION",
          payload: notification.id
        });
      }, notification.duration);
    });

    return () => {
      timeouts.forEach(timeout => {
        if (timeout) clearTimeout(timeout);
      });
    };
  }, [notifications, uiDispatch]);

  const handleClose = (id) => {
    uiDispatch({ 
      type: "REMOVE_NOTIFICATION",
      payload: id
    });
  };

  if (notifications.length === 0) return null;

  return (
    <div className="notifications-container">
      {notifications.map(notification => (
        <div 
          key={notification.id} 
          className={`notification ${notification.type || 'info'}`}
          onClick={() => handleClose(notification.id)}
        >
          <div className="notification-content">
            <div className="notification-icon">
              {notification.type === 'success' && <i className="fas fa-check-circle"></i>}
              {notification.type === 'error' && <i className="fas fa-exclamation-circle"></i>}
              {notification.type === 'warning' && <i className="fas fa-exclamation-triangle"></i>}
              {notification.type === 'info' && <i className="fas fa-info-circle"></i>}
            </div>
            <div className="notification-message">{notification.message}</div>
          </div>
          <button 
            className="notification-close"
            onClick={(e) => {
              e.stopPropagation();
              handleClose(notification.id);
            }}
          >
            <i className="fas fa-times"></i>
          </button>
        </div>
      ))}
    </div>
  );
};

export default Notifications;

