  const { customerState, customerDispatch } = useCustomer();
  const { uiDispatch } = useUI();
  const [showUtility, setShowUtility] = useState(false);
  const [importData, setImportData] = useState('');
  const [error, setError] = useState('');

  const toggleUtility = () => {
    setShowUtility(!showUtility);
    setError('');
  };

  // Generate export data
  const generateExportData = () => {
    const exportData = {
      metadata: {
        version: CONFIG.app.version,
        exportDate: new Date().toISOString(),
        type: 'novassistant-customer-data'
      },
      customerData: customerState
    };

    return JSON.stringify(exportData, null, 2);
  };

  // Handle export to file
  const handleExport = () => {
    try {
      const exportData = generateExportData();
      const blob = new Blob([exportData], { type: 'application/json' });
      const url = URL.createObjectURL(blob);

      // Create and click a download link
      const a = document.createElement('a');
      a.href = url;
      a.download = `novassistant-export-${new Date().toISOString().split('T')[0]}.json`;
      document.body.appendChild(a);
      a.click();

      // Clean up
      document.body.removeChild(a);
      URL.revokeObjectURL(url);

      uiDispatch({
        type: 'ADD_NOTIFICATION',
        payload: {
          message: 'Customer data exported successfully',
          type: 'success',
          duration: 3000
        }
      });
    } catch (err) {
      setError('Failed to export data: ' + err.message);
    }
  };

  // Handle export to clipboard
  const handleCopyToClipboard = () => {
    try {
      const exportData = generateExportData();
      navigator.clipboard.writeText(exportData);

      uiDispatch({
        type: 'ADD_NOTIFICATION',
        payload: {
          message: 'Customer data copied to clipboard',
          type: 'success',
          duration: 3000
        }
      });
    } catch (err) {
      setError('Failed to copy to clipboard: ' + err.message);
    }
  };

  // Handle import from text
  const handleImport = () => {
    try {
      if (!importData.trim()) {
        setError('Please enter data to import');
        return;
      }

      const parsedData = JSON.parse(importData);

      // Validate the imported data
      if (!parsedData.metadata || !parsedData.customerData) {
        setError('Invalid data format');
        return;
      }

      if (parsedData.metadata.type !== 'novassistant-customer-data') {
        setError('The imported data is not a valid NovAssistant export');
        return;
      }

      // Import the data
      customerDispatch({
        type: 'LOAD_CUSTOMER',
        payload: parsedData.customerData
      });

      // Save to local storage
      saveToLocalStorage(CONFIG.storage.customerDataKey, parsedData.customerData);

      uiDispatch({
        type: 'ADD_NOTIFICATION',
        payload: {
          message: 'Customer data imported successfully',
          type: 'success',
          duration: 3000
        }
      });

      // Reset form
      setImportData('');
      setError('');
      setShowUtility(false);
    } catch (err) {
      setError('Failed to import data: ' + err.message);
    }
  };

  // Handle file upload
  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        setImportData(e.target.result);
      } catch (err) {
        setError('Failed to read file: ' + err.message);
      }
    };
    reader.onerror = () => {
      setError('Failed to read file');
    };
    reader.readAsText(file);
  };

  return (
    <div className="data-utility">
      <button className="data-utility-toggle" onClick={toggleUtility}>
        <i className={`fas fa-${showUtility ? 'times' : 'database'}`}></i>
      </button>

      {showUtility && (
        <div className="data-utility-panel">
          <h3>Data Management</h3>

          <div className="utility-section">
            <h4>Export Data</h4>
            <p>Save your current customer data for backup or transfer</p>
            <div className="utility-actions">
              <button className="utility-btn" onClick={handleExport}>
                <i className="fas fa-file-download"></i> Export to File
              </button>
              <button className="utility-btn" onClick={handleCopyToClipboard}>
                <i className="fas fa-clipboard"></i> Copy to Clipboard
              </button>
            </div>
          </div>

          <div className="utility-section">
            <h4>Import Data</h4>
            <p>Restore customer data from a previous export</p>

            <div className="form-group">
              <label htmlFor="importDataText">Paste exported data:</label>
              <textarea
                id="importDataText"
                value={importData}
                onChange={(e) => setImportData(e.target.value)}
                placeholder="Paste JSON export data here"
                rows={5}
              />
            </div>

            <div className="form-group">
              <label className="file-upload-label">
                <span>Or upload from file:</span>
                <input
                  type="file"
                  accept=".json"
                  onChange={handleFileUpload}
                  className="file-upload-input"
                />
              </label>
            </div>

            <button 
              className="utility-btn import-btn" 
              onClick={handleImport}
              disabled={!importData.trim()}
            >
              <i className="fas fa-file-upload"></i> Import Data
            </button>

            {error && <div className="error-message">{error}</div>}
          </div>

          <div className="utility-section">
            <h4>Instructions</h4>
            <ul className="utility-instructions">
              <li>Use <strong>Export</strong> to save your current customer data</li>
              <li>Use <strong>Import</strong> to restore previously exported data</li>
              <li>Importing will replace the current customer data</li>
              <li>Keep exports secure as they may contain sensitive information</li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default DataUtility;

