  position: fixed;
  bottom: 20px;
  right: 20px;
  max-width: 300px;
  background-color: white;
  border-radius: var(--border-radius-md);
  box-shadow: 0 2px 10px var(--shadow-color-darker);
  overflow: hidden;
  z-index: 10000;
  animation: slideInRight 0.3s ease;
}

.nov-notification.info {
  border-left: 4px solid var(--info-color);
}

.nov-notification.success {
  border-left: 4px solid var(--success-color);
}

.nov-notification.warning {
  border-left: 4px solid var(--warning-color);
}

.nov-notification.error {
  border-left: 4px solid var(--error-color);
}

.nov-notification-content {
  display: flex;
  padding: var(--spacing-md);
}

.nov-notification-message {
  flex: 1;
  padding-right: var(--spacing-md);
}

.nov-notification-close {
  background: none;
  border: none;
  cursor: pointer;
  font-size: 1.2rem;
  color: var(--text-light);
  align-self: flex-start;
}

@keyframes slideInRight {
  from {
    transform: translateX(100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

