  // Initialize the module
  init: async function() {
    try {
      if (this.initialized) {
        return this;
      }
      
      AppLogger.log("Initializing Document Management module");
      
      // Set up sale type selector
      const saleTypeSelector = document.getElementById('saleTypeSelector');
      if (saleTypeSelector) {
        saleTypeSelector.addEventListener('change', this.updateDocumentChecklist.bind(this));
        
        // Initialize with the current selected value
        this.updateDocumentChecklist({ target: saleTypeSelector });
      }
      
      // Set up document upload functionality
      this.initDocumentUpload();
      
      this.initialized = true;
      AppLogger.log("Document Management module initialized successfully");
      
      return this;
    } catch (error) {
      AppLogger.error("Failed to initialize Document Management module", error);
      throw error;
    }
  },
  
  // Update document checklist based on selected sale type
  updateDocumentChecklist: function(event) {
    try {
      const saleType = event.target.value;
      const documentChecklist = document.getElementById('documentChecklist');
      
      if (!documentChecklist) return false;
      
      // Get checklist for selected sale type
      const checklist = this.documentChecklists[saleType];
      if (!checklist) {
        documentChecklist.innerHTML = '<p>No checklist available for this sale type.</p>';
        return false;
      }
      
      // Build checklist HTML
      let checklistHTML = '';
      
      // Process each section
      for (const [section, items] of Object.entries(checklist)) {
        checklistHTML += `
          <div class="checklist-group">
            <h4>${this.formatSectionName(section)}</h4>
        `;
        
        items.forEach(item => {
          checklistHTML += `
            <div class="checklist-item">
              <input type="checkbox" id="${item.id}" name="${item.id}" ${item.checked ? 'checked' : ''}>
              <label for="${item.id}">${item.name}${item.required ? ' <span class="required-tag">Required</span>' : ''}</label>
            </div>
          `;
        });
        
        checklistHTML += '</div>';
      }
      
      // Update checklist
      documentChecklist.innerHTML = checklistHTML;
      
      // Restore checkbox states if customer has document data
      this.restoreCheckboxStates(saleType);
      
      // Set up change listeners for checkboxes
      const checkboxes = documentChecklist.querySelectorAll('input[type="checkbox"]');
      checkboxes.forEach(checkbox => {
        checkbox.addEventListener('change', this.handleCheckboxChange.bind(this));
      });
      
      return true;
    } catch (error) {
      AppLogger.error("Error updating document checklist", error);
      return false;
    }
  },
  
  // Format section name for display
  formatSectionName: function(section) {
    // Capitalize first letter and add spaces before capital letters
    return section
      .replace(/([A-Z])/g, ' $1')
      .replace(/^./, str => str.toUpperCase());
  },
  
  // Restore checkbox states from customer data
  restoreCheckboxStates: function(saleType) {
    try {
      // Only proceed if we have a current customer
      if (window.NovAssistant.state.currentCustomerIndex === null) {
        return false;
      }
      
      // Get current customer
      const customers = window.NovAssistant.CustomerManagement.getCustomers();
      const customer = customers[window.NovAssistant.state.currentCustomerIndex];
      
      if (!customer || !customer.documents || !customer.documents[saleType]) {
        return false;
      }
      
      // Restore checkbox states
      const documentCheckboxes = customer.documents[saleType];
      
      for (const [id, checked] of Object.entries(documentCheckboxes)) {
        const checkbox = document.getElementById(id);
        if (checkbox) {
          checkbox.checked = checked;
        }
      }
      
      return true;
    } catch (error) {
      AppLogger.error("Error restoring checkbox states", error);
      return false;
    }
  },
  
  // Handle checkbox change
  handleCheckboxChange: function(event) {
    try {
      // Only proceed if we have a current customer
      if (window.NovAssistant.state.currentCustomerIndex === null) {
        return false;
      }
      
      const checkbox = event.target;
      const itemId = checkbox.id;
      const saleTypeSelector = document.getElementById('saleTypeSelector');
      
      if (!saleTypeSelector) return false;
      
      const saleType = saleTypeSelector.value;
      
      // Get current customer
      const customerManagement = window.NovAssistant.CustomerManagement;
      const customers = customerManagement.getCustomers();
      const customer = customers[window.NovAssistant.state.currentCustomerIndex];
      
      if (!customer) return false;
      
      // Initialize documents object if needed
      if (!customer.documents) {
        customer.documents = {};
      }
      
      if (!customer.documents[saleType]) {
        customer.documents[saleType] = {};
      }
      
      // Update document status
      customer.documents[saleType][itemId] = checkbox.checked;
      
      // Save customer changes
      customerManagement.saveCustomers();
      
      return true;
    } catch (error) {
      AppLogger.error("Error handling checkbox change", error);
      return false;
    }
  },
  
  // Initialize document upload functionality
  initDocumentUpload: function() {
    try {
      const dropZone = document.getElementById('dropZone');
      const fileUpload = document.getElementById('fileUpload');
      
      if (!dropZone || !fileUpload) return false;
      
      // Handle drag events
      ['dragenter', 'dragover', 'dragleave', 'drop'].forEach(eventName => {
        dropZone.addEventListener(eventName, preventDefaults, false);
      });
      
      function preventDefaults(e) {
        e.preventDefault();
        e.stopPropagation();
      }
      
      // Handle drop zone highlighting
      ['dragenter', 'dragover'].forEach(eventName => {
        dropZone.addEventListener(eventName, highlight, false);
      });
      
      ['dragleave', 'drop'].forEach(eventName => {
        dropZone.addEventListener(eventName, unhighlight, false);
      });
      
      function highlight() {
        dropZone.classList.add('drag-over');
      }
      
      function unhighlight() {
        dropZone.classList.remove('drag-over');
      }
      
      // Handle file drop
      dropZone.addEventListener('drop', handleDrop, false);
      
      function handleDrop(e) {
        const dt = e.dataTransfer;
        const files = dt.files;
        handleFiles(files);
      }
      
      // Handle file input change
      fileUpload.addEventListener('change', function() {
        handleFiles(this.files);
      });
      
      // Click on drop zone to trigger file input
      dropZone.addEventListener('click', function() {
        fileUpload.click();
      });
      
      // Process uploaded files
      function handleFiles(files) {
        // Convert FileList to Array for easier processing
        const filesArray = Array.from(files);
        
        // Process each file
        filesArray.forEach(file => {
          uploadFile(file);
        });
      }
      
      // Upload file and add to document list
      const uploadFile = (file) => {
        // In a real implementation, this would upload to a server
        // For this demo, we'll just simulate a successful upload
        
        // Create document item
        this.addDocumentToList(file);
        
        // Show success message
        window.NovAssistant.ui.showToast(`File "${file.name}" uploaded successfully`, "success");
      };
      
      return true;
    } catch (error) {
      AppLogger.error("Error initializing document upload", error);
      return false;
    }
  },
  
  // Add document to list
  addDocumentToList: function(file) {
    try {
      // Only proceed if we have a current customer
      if (window.NovAssistant.state.currentCustomerIndex === null) {
        window.NovAssistant.ui.showToast("No customer selected", "warning");
        return false;
      }
      
      const documentList = document.getElementById('documentList');
      if (!documentList) return false;
      
      // Get file details
      const fileName = file.name;
      const fileSize = this.formatFileSize(file.size);
      const fileType = file.type;
      const uploadDate = new Date().toLocaleDateString();
      
      // Determine icon based on file type
      let iconClass = "fa-file";
      if (fileType.includes("pdf")) {
        iconClass = "fa-file-pdf";
      } else if (fileType.includes("image")) {
        iconClass = "fa-file-image";
      } else if (fileType.includes("word") || fileName.endsWith(".doc") || fileName.endsWith(".docx")) {
        iconClass = "fa-file-word";
      } else if (fileType.includes("excel") || fileName.endsWith(".xls") || fileName.endsWith(".xlsx")) {
        iconClass = "fa-file-excel";
      }
      
      // Create document item
      const documentItem = document.createElement('div');
      documentItem.className = 'document-item';
      documentItem.innerHTML = `
        <div class="document-icon">
          <i class="fas ${iconClass}"></i>
        </div>
        <div class="document-info">
          <div class="document-name">${fileName}</div>
          <div class="document-meta">
            <span class="document-date">${uploadDate}</span>
            <span class="document-size">${fileSize}</span>
          </div>
        </div>
        <div class="document-actions">
          <button class="document-action-btn" data-action="download" title="Download">
            <i class="fas fa-download"></i>
          </button>
          <button class="document-action-btn" data-action="delete" title="Delete">
            <i class="fas fa-trash-alt"></i>
          </button>
        </div>
      `;
      
      // Add event listeners for document actions
      const downloadBtn = documentItem.querySelector('[data-action="download"]');
      const deleteBtn = documentItem.querySelector('[data-action="delete"]');
      
      downloadBtn.addEventListener('click', () => {
        // In a real implementation, this would download the file
        window.NovAssistant.ui.showToast(`Downloading "${fileName}"...`, "info");
      });
      
      deleteBtn.addEventListener('click', () => {
        if (confirm(`Are you sure you want to delete "${fileName}"?`)) {
          documentItem.remove();
          window.NovAssistant.ui.showToast(`"${fileName}" deleted`, "info");
        }
      });
      
      // Add to document list
      documentList.appendChild(documentItem);
      
      // Save document reference to customer data
      this.saveDocumentReference(file, fileName, fileSize, uploadDate, iconClass);
      
      return true;
    } catch (error) {
      AppLogger.error("Error adding document to list", error);
      return false;
    }
  },
  
  // Format file size for display
  formatFileSize: function(bytes) {
    if (bytes === 0) return '0 Bytes';
    
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  },
  
  // Save document reference to customer data
  saveDocumentReference: function(file, fileName, fileSize, uploadDate, iconClass) {
    try {
      // Only proceed if we have a current customer
      if (window.NovAssistant.state.currentCustomerIndex === null) {
        return false;
      }
      
      // Get current customer
      const customerManagement = window.NovAssistant.CustomerManagement;
      const customers = customerManagement.getCustomers();
      const customer = customers[window.NovAssistant.state.currentCustomerIndex];
      
      if (!customer) return false;
      
      // Initialize uploaded documents array if needed
      if (!customer.uploadedDocuments) {
        customer.uploadedDocuments = [];
      }
      
      // Add document reference
      customer.uploadedDocuments.push({
        name: fileName,
        size: fileSize,
        date: uploadDate,
        icon: iconClass,
        id: Date.now() // Use timestamp as simple ID
      });
      
      // Save customer changes
      customerManagement.saveCustomers();
      
      return true;
    } catch (error) {
      AppLogger.error("Error saving document reference", error);
      return false;
    }
  },
  
  // Load documents for current customer
  loadCustomerDocuments: function() {
    try {
      // Only proceed if we have a current customer
      if (window.NovAssistant.state.currentCustomerIndex === null) {
        return false;
      }
      
      const documentList = document.getElementById('documentList');
      if (!documentList) return false;
      
      // Clear existing documents
      documentList.innerHTML = '';
      
      // Get current customer
      const customers = window.NovAssistant.CustomerManagement.getCustomers();
      const customer = customers[window.NovAssistant.state.currentCustomerIndex];
      
      if (!customer || !customer.uploadedDocuments || customer.uploadedDocuments.length === 0) {
        documentList.innerHTML = '<p class="no-data">No documents uploaded yet.</p>';
        return true;
      }
      
      // Add each document to the list
      customer.uploadedDocuments.forEach(doc => {
        const documentItem = document.createElement('div');
        documentItem.className = 'document-item';
        documentItem.innerHTML = `
          <div class="document-icon">
            <i class="fas ${doc.icon}"></i>
          </div>
          <div class="document-info">
            <div class="document-name">${doc.name}</div>
            <div class="document-meta">
              <span class="document-date">${doc.date}</span>
              <span class="document-size">${doc.size}</span>
            </div>
          </div>
          <div class="document-actions">
            <button class="document-action-btn" data-action="download" title="Download">
              <i class="fas fa-download"></i>
            </button>
            <button class="document-action-btn" data-action="delete" title="Delete" data-id="${doc.id}">
              <i class="fas fa-trash-alt"></i>
            </button>
          </div>
        `;
        
        // Add event listeners for document actions
        const downloadBtn = documentItem.querySelector('[data-action="download"]');
        const deleteBtn = documentItem.querySelector('[data-action="delete"]');
        
        downloadBtn.addEventListener('click', () => {
          // In a real implementation, this would download the file
          window.NovAssistant.ui.showToast(`Downloading "${doc.name}"...`, "info");
        });
        
        deleteBtn.addEventListener('click', () => {
          if (confirm(`Are you sure you want to delete "${doc.name}"?`)) {
            this.deleteDocument(doc.id, documentItem);
          }
        });
        
        // Add to document list
        documentList.appendChild(documentItem);
      });
      
      return true;
    } catch (error) {
      AppLogger.error("Error loading customer documents", error);
      return false;
    }
  },
  
  // Delete document
  deleteDocument: function(docId, documentItem) {
    try {
      // Only proceed if we have a current customer
      if (window.NovAssistant.state.currentCustomerIndex === null) {
        return false;
      }
      
      // Get current customer
      const customerManagement = window.NovAssistant.CustomerManagement;
      const customers = customerManagement.getCustomers();
      const customer = customers[window.NovAssistant.state.currentCustomerIndex];
      
      if (!customer || !customer.uploadedDocuments) return false;
      
      // Find document index
      const docIndex = customer.uploadedDocuments.findIndex(doc => doc.id === docId);
      
      if (docIndex === -1) return false;
      
      // Get document name for message
      const docName = customer.uploadedDocuments[docIndex].name;
      
      // Remove document from array
      customer.uploadedDocuments.splice(docIndex, 1);
      
      // Remove from UI
      if (documentItem) {
        documentItem.remove();
      }
      
      // Save customer changes
      customerManagement.saveCustomers();
      
      // Show success message
      window.NovAssistant.ui.showToast(`"${docName}" deleted`, "info");
      
      return true;
    } catch (error) {
      AppLogger.error("Error deleting document", error);
      return false;
    }
  }
};

