}

.comparison-table {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-xs);
}

.comparison-row {
  display: flex;
  justify-content: space-between;
  padding: var(--spacing-xs) 0;
  border-bottom: 1px dashed var(--border-color);
}

.comparison-row:last-child {
  border-bottom: none;
}

.comparison-label {
  font-weight: 500;
  color: var(--text-color);
}

.comparison-value {
  font-weight: 600;
}

.savings-highlight {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-md);
}

.savings-item {
  padding: var(--spacing-sm);
  background-color: var(--primary-light);
  border-radius: var(--border-radius-sm);
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.savings-item:last-child {
  background-color: rgba(40, 167, 69, 0.1);
  border: 1px solid rgba(40, 167, 69, 0.3);
}

.savings-label {
  font-weight: 500;
}

.savings-value {
  font-weight: 700;
  font-size: 1.2rem;
}

.savings-value.highlight {
  color: #28a745;
  font-size: 1.4rem;
}

.benefits-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(130px, 1fr));
  gap: var(--spacing-md);
}

.benefit-item {
  text-align: center;
  padding: var(--spacing-sm);
  border: 1px solid var(--border-color);
  border-radius: var(--border-radius-sm);
  background-color: #f9f9f9;
}

.benefit-icon {
  font-size: 1.5rem;
  color: var(--primary-color);
  margin-bottom: var(--spacing-sm);
}

.benefit-label {
  font-size: 0.85rem;
  color: var(--text-light);
  margin-bottom: var(--spacing-xs);
}

.benefit-value {
  font-weight: 700;
  font-size: 1.1rem;
}

.calculator-actions {
  display: flex;
  justify-content: flex-end;
  gap: var(--spacing-md);
  margin-top: var(--spacing-lg);
}

.secondary-btn {
  padding: var(--spacing-sm) var(--spacing-md);
  background-color: white;
  border: 1px solid var(--border-color);
  color: var(--text-color);
  display: flex;
  align-items: center;
  gap: var(--spacing-xs);
  border-radius: var(--border-radius-sm);
  cursor: pointer;
  transition: all 0.2s ease;
}

.secondary-btn:hover {
  background-color: var(--background-color);
  color: var(--primary-color);
  border-color: var(--primary-color);
}

.calculator-explanation {
  background-color: var(--primary-light);
  border-radius: var(--border-radius-sm);
  padding: var(--spacing-md);
  margin-top: var(--spacing-lg);
}

.calculator-explanation h4 {
  color: var(--primary-color);
  margin-top: 0;
  margin-bottom: var(--spacing-sm);
}

.calculator-explanation p, 
.calculator-explanation ul {
  font-size: 0.95rem;
  color: var(--text-color);
  margin-bottom: var(--spacing-sm);
}

.calculator-explanation ul {
  padding-left: 20px;
}

.calculator-explanation li {
  margin-bottom: var(--spacing-xs);
}

@media (max-width: 768px) {
  .result-breakdown {
    grid-template-columns: 1fr;
  }

  .calculator-actions {
    flex-direction: column;
  }

  .secondary-btn {
    width: 100%;
    justify-content: center;
  }
}

/* =========================================================
   Phone Script Enhancements - CSS
   ========================================================= */

/* Script Flow Container */
.script-flow-container {
  position: relative;
  background: var(--card-color);
  border-radius: var(--border-radius-md);
  box-shadow: 0 2px 4px var(--shadow-color);
  padding: var(--spacing-md);
  margin-bottom: var(--spacing-lg);
  min-height: 350px;
}

/* Script Views (Conversation Steps) */
.script-view {
  display: none;
  opacity: 0;
  transition: opacity 0.3s ease;
}

.script-view.active {
  display: block;
  opacity: 1;
  animation: fadeIn 0.3s ease;
}

/* Path Header with Back Button */
.script-path-header {
  display: flex;
  align-items: center;
  margin-bottom: var(--spacing-md);
  padding-bottom: var(--spacing-sm);
  border-bottom: 1px solid var(--border-color);
}

.script-path-header h3 {
  flex: 1;
  margin: 0;
  font-family: var(--title-font);
  color: var(--primary-color);
}

.script-back-btn {
  background: var(--background-color);
  border: 1px solid var(--border-color);
  border-radius: var(--border-radius-sm);
  padding: 0.5rem 0.75rem;
  margin-right: var(--spacing-md);
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.2s ease;
  display: flex;
  align-items: center;
  gap: var(--spacing-xs);
}

.script-back-btn:hover {
  background: var(--primary-light);
  border-color: var(--primary-color);
  color: var(--primary-color);
}

/* Script Intro */
.script-intro {
  margin-bottom: var(--spacing-md);
  color: var(--text-light);
  font-style: italic;
}

/* Script Questions */
.script-question {
  background: var(--primary-light);
  border-radius: var(--border-radius-md);
  padding: var(--spacing-md);
  margin: var(--spacing-md) 0;
}

.script-question h4 {
  font-family: var(--title-font);
  margin-bottom: var(--spacing-md);
  color: var(--primary-color);
}

/* Script Options (Buttons) */
.script-options {
  display: flex;
  flex-wrap: wrap;
  gap: var(--spacing-sm);
  margin-top: var(--spacing-sm);
}

.script-option-btn {
  display: flex;
  align-items: center;
  gap: var(--spacing-sm);
  padding: 0.8rem 1.2rem;
  background: white;
  border: 1px solid var(--primary-color);
  border-radius: var(--border-radius-md);
  color: var(--primary-color);
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s ease;
}

.script-option-btn:hover {
  background: var(--primary-color);
  color: white;
  transform: translateY(-2px);
  box-shadow: 0 4px 8px var(--shadow-color);
}

.script-option-btn:active {
  transform: translateY(0);
}

/* Word Track Lists */
.word-track-section {
  background: white;
  border: 1px solid var(--border-color);
  border-radius: var(--border-radius-md);
  padding: var(--spacing-md);
  margin: var(--spacing-md) 0;
}

.word-track-section h4 {
  font-family: var(--title-font);
  color: var(--primary-color);
  margin-bottom: var(--spacing-sm);
  border-bottom: 1px solid var(--border-color);
  padding-bottom: var(--spacing-xs);
}

.word-track-list {
  list-style-type: none;
  padding: 0;
  margin: 0;
}

.word-track-list li {
  position: relative;
  padding: var(--spacing-xs) 0;
  padding-left: var(--spacing-md);
  margin-bottom: var(--spacing-xs);
  line-height: 1.5;
}

.word-track-list li::before {
  content: "•";
  position: absolute;
  left: 0;
  color: var(--primary-color);
  font-weight: bold;
}

/* Script Content */
.script-content {
  padding: var(--spacing-sm) 0;
}

/* Script Notes */
.script-notes {
  margin-top: var(--spacing-lg);
  padding: var(--spacing-md);
  background: #f9f9f9;
  border-left: 3px solid var(--primary-color);
  border-radius: var(--border-radius-sm);
}

.script-notes h4 {
  margin-top: 0;
  margin-bottom: var(--spacing-sm);
  color: var(--primary-color);
}

/* AI Section */
.script-ai-section {
  margin-top: var(--spacing-xl);
  padding-top: var(--spacing-lg);
  border-top: 1px solid var(--border-color);
}

.script-ai-section h3 {
  margin-bottom: var(--spacing-md);
  color: var(--primary-color);
}

/* Script Library */
.script-library {
  margin-top: var(--spacing-xl);
}

.script-library h3 {
  margin-bottom: var(--spacing-md);
  color: var(--primary-color);
}

.script-categories {
  display: flex;
  flex-wrap: wrap;
  gap: var(--spacing-sm);
}

.script-category-btn {
  background: var(--background-color);
  border: 1px solid var(--border-color);
  border-radius: var(--border-radius-lg);
  padding: 0.6rem 1rem;
  font-size: 0.9rem;
  cursor: pointer;
  transition: all 0.2s ease;
}

.script-category-btn:hover {
  background: var(--primary-light);
  border-color: var(--primary-color);
  color: var(--primary-color);
}

/* Responsive adjustments */
@media (max-width: 768px) {
  .script-options {
    flex-direction: column;
  }

  .script-option-btn {
    width: 100%;
  }
}

/* Animations */
@keyframes slideIn {
  from {
    transform: translateX(50px);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

.slide-in {
