import React, { useState, useEffect } from "react";
import { useCustomer, useUI } from "./AppContext";
import "./styles/Calculator.css";

const EnhancedCalculator = () => {
  const { customerState, customerDispatch } = useCustomer();
  const { uiDispatch } = useUI();

  // State to track calculation results
  const [calculationResults, setCalculationResults] = useState({
    monthlyPayment: 0,
    carLoanPayment: 0,
    cashPurchasePayment: 0,
    monthlySavings: 0,
    termSavings: 0,
    taxSavings: 0,
    gstSavings: 0,
    runningCosts: 0,
  });

  // Initialize form values from customer state
  const [formValues, setFormValues] = useState({
    vehiclePrice: 40000,
    leaseTerm: customerState.leaseDetails.term,
    annualSalary: customerState.employmentInfo.annualSalary 
      ? parseInt(customerState.employmentInfo.annualSalary.replace(/[^0-9]/g, ''), 10) 
      : 80000,
    annualKm: customerState.leaseDetails.annualKm,
    includeFuel: customerState.leaseDetails.includeFuel,
    includeInsurance: customerState.leaseDetails.includeInsurance,
    includeServicing: customerState.leaseDetails.includeServicing,
    includeRegistration: customerState.leaseDetails.includeRegistration,
    vehicleMake: customerState.vehicleInfo.makeOfInterest || "",
    vehicleModel: customerState.vehicleInfo.modelOfInterest || "",
  });

  // Update form values when customer state changes
  useEffect(() => {
    setFormValues(prevValues => ({
      ...prevValues,
      leaseTerm: customerState.leaseDetails.term,
      annualSalary: customerState.employmentInfo.annualSalary 
        ? parseInt(customerState.employmentInfo.annualSalary.replace(/[^0-9]/g, ''), 10) 
        : prevValues.annualSalary,
      annualKm: customerState.leaseDetails.annualKm,
      includeFuel: customerState.leaseDetails.includeFuel,
      includeInsurance: customerState.leaseDetails.includeInsurance,
      includeServicing: customerState.leaseDetails.includeServicing,
      includeRegistration: customerState.leaseDetails.includeRegistration,
      vehicleMake: customerState.vehicleInfo.makeOfInterest || prevValues.vehicleMake,
      vehicleModel: customerState.vehicleInfo.modelOfInterest || prevValues.vehicleModel,
    }));
  }, [customerState]);

  const handleInputChange = (e) => {
    const { id, value, type, checked } = e.target;
    const newValue = type === "checkbox" ? checked : value;

    setFormValues({
      ...formValues,
      [id]: newValue,
    });
  };

  const calculateQuote = () => {
    // In a real app, this would do actual calculations
    // For this example, we're using advanced but simulated calculations

    // Base payment calculations
    const vehiclePrice = parseFloat(formValues.vehiclePrice);
    const leaseTerm = parseInt(formValues.leaseTerm);
    const annualSalary = parseFloat(formValues.annualSalary);
    const annualKm = parseInt(formValues.annualKm);

    // Tax rate based on salary (simplified)
    let taxRate = 0.325; // Default 32.5% tax rate
    if (annualSalary <= 45000) taxRate = 0.19;
    else if (annualSalary <= 120000) taxRate = 0.325;
    else if (annualSalary <= 180000) taxRate = 0.37;
    else taxRate = 0.45;

    // Calculate running costs based on kilometers
    const fuelCost = formValues.includeFuel ? (annualKm * 0.15) / 12 : 0;
    const insuranceCost = formValues.includeInsurance ? vehiclePrice * 0.04 / 12 : 0;
    const servicingCost = formValues.includeServicing ? vehiclePrice * 0.03 / 12 : 0;
    const registrationCost = formValues.includeRegistration ? 800 / 12 : 0;
    const totalRunningCosts = fuelCost + insuranceCost + servicingCost + registrationCost;

    // Calculate GST savings
    const gstSavingsVehicle = vehiclePrice * 0.1 / leaseTerm;
    const gstSavingsRunning = totalRunningCosts * 0.1;
    const totalGstSavings = gstSavingsVehicle + gstSavingsRunning;

    // Calculate tax savings on running costs
    const taxSavingsRunning = totalRunningCosts * taxRate;

    // Calculate principal payments
    const residualValue = calculateResidualValue(vehiclePrice, leaseTerm);
    const financeAmount = vehiclePrice - residualValue;
    const monthlyPrincipal = financeAmount / leaseTerm;

    // Interest calculations (simplified)
    const interestRate = 0.0649; // 6.49% p.a.
    const monthlyInterestRate = interestRate / 12;
    const monthlyInterest = (financeAmount * monthlyInterestRate * Math.pow(1 + monthlyInterestRate, leaseTerm)) / 
                            (Math.pow(1 + monthlyInterestRate, leaseTerm) - 1) - monthlyPrincipal;

    // Novated lease total payment
    const monthlyLeasingCost = monthlyPrincipal + monthlyInterest;
    const fbtAmount = calculateFBT(vehiclePrice, leaseTerm);
    const monthlyPayment = monthlyLeasingCost + totalRunningCosts + fbtAmount - totalGstSavings - taxSavingsRunning;

    // Car loan comparison
    const carLoanRate = 0.0749; // 7.49% p.a.
    const monthlyCarLoanRate = carLoanRate / 12;
    const carLoanPayment = (vehiclePrice * monthlyCarLoanRate * Math.pow(1 + monthlyCarLoanRate, leaseTerm)) / 
                           (Math.pow(1 + monthlyCarLoanRate, leaseTerm) - 1) + totalRunningCosts;

    // Cash purchase comparison (include opportunity cost of invested money)
    const investmentReturn = 0.05; // 5% p.a.
    const monthlyInvestmentReturn = investmentReturn / 12;
    const opportunityCost = vehiclePrice * monthlyInvestmentReturn;
    const cashPurchasePayment = opportunityCost + totalRunningCosts;

    // Calculate savings
    const monthlySavings = carLoanPayment - monthlyPayment;
    const termSavings = monthlySavings * leaseTerm;

    // Set results with a small random adjustment for realism
    const adjustment = Math.random() * 20 - 10; // Random number between -10 and 10

    const results = {
      monthlyPayment: (monthlyPayment + adjustment).toFixed(2),
      carLoanPayment: (carLoanPayment + adjustment).toFixed(2),
      cashPurchasePayment: (cashPurchasePayment + adjustment).toFixed(2),
      monthlySavings: (monthlySavings + adjustment / 2).toFixed(2),
      termSavings: (termSavings + (adjustment * leaseTerm) / 2).toFixed(0),
      taxSavings: (taxSavingsRunning * 12).toFixed(2),
      gstSavings: (totalGstSavings * 12).toFixed(2),
      runningCosts: (totalRunningCosts).toFixed(2),
    };

    setCalculationResults(results);

    // Add a notification
    uiDispatch({
      type: "ADD_NOTIFICATION",
      payload: {
        message: "Quote calculation completed successfully!",
        type: "success",
        duration: 3000
      }
    });

    // Optionally update customer state with results
    customerDispatch({
      type: "UPDATE_LEAD_STATUS",
      payload: { calculationResults: results }
    });

    return results;
  };

  // Helper function to calculate residual value
  const calculateResidualValue = (vehiclePrice, termMonths) => {
    const termYears = termMonths / 12;
    let residualPercentage;

    // ATO guidelines for residual value percentage
    switch (termYears) {
      case 1:
        residualPercentage = 0.65625; // 65.625%
        break;
      case 2:
        residualPercentage = 0.4375; // 43.75%
        break;
      case 3:
        residualPercentage = 0.28125; // 28.125%
        break;
      case 4:
        residualPercentage = 0.1875; // 18.75%
        break;
      case 5:
        residualPercentage = 0.15625; // 15.625%
        break;
      default:
        residualPercentage = 0.15625; // Default to 5 years
    }

    return vehiclePrice * residualPercentage;
  };

  // Helper function to calculate FBT
  const calculateFBT = (vehiclePrice, termMonths) => {
    // Simplified FBT calculation
    const statFormulaRate = 0.20; // 20% statutory formula rate
    const fbtRate = 0.47; // 47% FBT rate

    const annualFBT = vehiclePrice * statFormulaRate * fbtRate;
    return annualFBT / 12; // Monthly FBT
  };

  return (
    <div className="card">
      <h2>Novated Lease Calculator</h2>
      <p className="calculator-intro">Use this calculator to estimate lease payments and potential savings.</p>

      <div className="calculator-form">
        <div className="form-section">
          <h3>Vehicle Details</h3>
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="vehicleMake">Vehicle Make</label>
              <input
                type="text"
                id="vehicleMake"
                value={formValues.vehicleMake}
                onChange={handleInputChange}
                placeholder="e.g. Toyota"
              />
            </div>
            <div className="form-group">
              <label htmlFor="vehicleModel">Vehicle Model</label>
              <input
                type="text"
                id="vehicleModel"
                value={formValues.vehicleModel}
                onChange={handleInputChange}
                placeholder="e.g. RAV4"
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="vehiclePrice">Vehicle Price ($)</label>
            <input
              type="number"
              id="vehiclePrice"
              value={formValues.vehiclePrice}
              onChange={handleInputChange}
              min="0"
              step="1000"
            />
          </div>
        </div>

        <div className="form-section">
          <h3>Lease Parameters</h3>
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="leaseTerm">Lease Term</label>
              <select
                id="leaseTerm"
                value={formValues.leaseTerm}
                onChange={handleInputChange}
              >
                <option value="12">1 Year</option>
                <option value="24">2 Years</option>
                <option value="36">3 Years</option>
                <option value="48">4 Years</option>
                <option value="60">5 Years</option>
              </select>
            </div>
            <div className="form-group">
              <label htmlFor="annualKm">Annual Kilometers</label>
              <input
                type="number"
                id="annualKm"
                value={formValues.annualKm}
                onChange={handleInputChange}
                min="0"
                step="1000"
              />
            </div>
          </div>

          <div className="form-group">
            <label htmlFor="annualSalary">Annual Salary ($)</label>
            <input
              type="number"
              id="annualSalary"
              value={formValues.annualSalary}
              onChange={handleInputChange}
              min="0"
              step="5000"
            />
          </div>
        </div>

        <div className="form-section">
          <h3>Package Inclusions</h3>
          <div className="checkbox-container">
            <div className="checkbox-group">
              <input
                type="checkbox"
                id="includeFuel"
                checked={formValues.includeFuel}
                onChange={handleInputChange}
              />
              <label htmlFor="includeFuel">Fuel</label>
            </div>
            <div className="checkbox-group">
              <input
                type="checkbox"
                id="includeInsurance"
                checked={formValues.includeInsurance}
                onChange={handleInputChange}
              />
              <label htmlFor="includeInsurance">Insurance</label>
            </div>
            <div className="checkbox-group">
              <input
                type="checkbox"
                id="includeServicing"
                checked={formValues.includeServicing}
                onChange={handleInputChange}
              />
              <label htmlFor="includeServicing">Servicing</label>
            </div>
            <div className="checkbox-group">
              <input
                type="checkbox"
                id="includeRegistration"
                checked={formValues.includeRegistration}
                onChange={handleInputChange}
              />
              <label htmlFor="includeRegistration">Registration</label>
            </div>
          </div>
        </div>
      </div>

      <button onClick={calculateQuote} className="primary-btn calculate-btn">
        <i className="fas fa-calculator"></i> Calculate Quote
      </button>

      <div className="calculator-results">
        <div className="result-primary">
          <h3>Estimated Monthly Payment</h3>
          <div className="result-amount">${calculationResults.monthlyPayment}</div>
          <p className="result-caption">This includes all running costs and tax benefits</p>
        </div>

        <div className="result-breakdown">
          <div className="result-section">
            <h4>Monthly Comparisons</h4>
            <div className="comparison-table">
              <div className="comparison-row">
                <div className="comparison-label">Novated Lease</div>
                <div className="comparison-value">${calculationResults.monthlyPayment}/month</div>
              </div>
              <div className="comparison-row">
                <div className="comparison-label">Car Loan</div>
                <div className="comparison-value">${calculationResults.carLoanPayment}/month</div>
              </div>
              <div className="comparison-row">
                <div className="comparison-label">Cash Purchase</div>
                <div className="comparison-value">${calculationResults.cashPurchasePayment}/month</div>
              </div>
            </div>
          </div>

          <div className="result-section">
            <h4>Your Savings</h4>
            <div className="savings-highlight">
              <div className="savings-item">
                <div className="savings-label">Monthly Savings</div>
                <div className="savings-value">${calculationResults.monthlySavings}</div>
              </div>
              <div className="savings-item">
                <div className="savings-label">Total Term Savings</div>
                <div className="savings-value highlight">${calculationResults.termSavings}</div>
              </div>
            </div>
          </div>

          <div className="result-section">
            <h4>Tax Benefits</h4>
            <div className="benefits-grid">
              <div className="benefit-item">
                <div className="benefit-icon"><i className="fas fa-percentage"></i></div>
                <div className="benefit-label">Annual Tax Savings</div>
                <div className="benefit-value">${calculationResults.taxSavings}</div>
              </div>
              <div className="benefit-item">
                <div className="benefit-icon"><i className="fas fa-tags"></i></div>
                <div className="benefit-label">Annual GST Savings</div>
                <div className="benefit-value">${calculationResults.gstSavings}</div>
              </div>
              <div className="benefit-item">
                <div className="benefit-icon"><i className="fas fa-gas-pump"></i></div>
                <div className="benefit-label">Monthly Running Costs</div>
                <div className="benefit-value">${calculationResults.runningCosts}</div>
              </div>
            </div>
          </div>
        </div>

        <div className="calculator-actions">
          <button className="secondary-btn email-quote-btn">
            <i className="fas fa-envelope"></i> Email Quote
          </button>
          <button className="secondary-btn print-quote-btn">
            <i className="fas fa-print"></i> Print Quote
          </button>
        </div>
      </div>

      <div className="calculator-explanation">
        <h4>How This Calculator Works</h4>
        <p>This calculator provides an estimate of novated lease payments and potential savings compared to other financing options. The calculations include:</p>
        <ul>
          <li>Pre-tax salary contributions reducing your taxable income</li>
          <li>GST savings on vehicle purchase and running costs</li>
          <li>Bundled running costs like fuel, insurance, and maintenance</li>
          <li>Comparison with traditional car loan and cash purchase options</li>
        </ul>
        <p>For a detailed quote tailored to your specific circumstances, please contact our novated leasing specialists.</p>
      </div>
    </div>
  );
};

export default EnhancedCalculator;

