    setupEventListeners: function () {
      // Option buttons
      document.querySelectorAll(".script-option-btn").forEach((button) => {
        button.addEventListener("click", (event) => {
          const targetView = event.currentTarget.getAttribute("data-target");
          if (targetView) {
            this.navigateToView(targetView);
          }
        });
      });

      // Back buttons
      document.querySelectorAll(".script-back-btn").forEach((button) => {
        button.addEventListener("click", (event) => {
          const targetView = event.currentTarget.getAttribute("data-target");
          if (targetView) {
            this.navigateToView(targetView);
          }
        });
      });

      // Response generation
      const getScriptBtn = document.getElementById("getScript");
      if (getScriptBtn) {
        getScriptBtn.addEventListener("click", () => {
          const callInput = document.getElementById("callInput");
          const scriptSuggestions =
            document.getElementById("scriptSuggestions");

          if (!callInput || !scriptSuggestions) return;

          const userInput = callInput.value.trim();
          if (!userInput) {
            scriptSuggestions.innerHTML = `<div class="error-message"><p>Please enter the customer's objection or scenario first.</p></div>`;
            return;
          }

          scriptSuggestions.innerHTML = `<div class="loading-indicator"><i class="fas fa-spinner fa-spin"></i> Generating custom response...</div>`;

          setTimeout(() => {
            // Simple rule-based response
            const inputLower = userInput.toLowerCase();
            if (
              inputLower.includes("expensive") ||
              inputLower.includes("cost")
            ) {
              this.displayObjectionResponse("price");
            } else if (
              inputLower.includes("think") ||
              inputLower.includes("time")
            ) {
              this.displayObjectionResponse("time");
            } else if (
              inputLower.includes("approve") ||
              inputLower.includes("qualify")
            ) {
              this.displayObjectionResponse("approval");
            } else if (
              inputLower.includes("employer") ||
              inputLower.includes("company")
            ) {
              this.displayObjectionResponse("employer");
            } else {
              scriptSuggestions.innerHTML = `
                <h4>Custom Response:</h4>
                <ul class="word-track-list">
                  <li>"I understand your concern about '${userInput}'. Let me address that specifically."</li>
                  <li>"With novated leasing, we often find that customers have similar questions, and there are several benefits that may address your concern."</li>
                  <li>"The key advantage of novated leasing is reducing your taxable income while bundling all vehicle costs into one simple payment."</li>
                  <li>"Would it be helpful if I showed you a detailed comparison of how novated leasing would work in your specific situation?"</li>
                </ul>
              `;
            }
          }, 1000);
        });
      }

      // Category buttons
      document.querySelectorAll(".script-category-btn").forEach((button) => {
        button.addEventListener("click", (event) => {
          const category = event.currentTarget.getAttribute("data-category");
          if (category) {
            this.displayObjectionResponse(category);
          }
        });
      });
    },

    navigateToView: function (viewId) {
      document.querySelectorAll(".script-view").forEach((view) => {
        view.classList.remove("active");
      });

      const targetView = document.getElementById(viewId);
      if (targetView) {
        targetView.classList.add("active");
      }
    },

    displayObjectionResponse: function (category) {
      const scriptSuggestions = document.getElementById("scriptSuggestions");
      if (!scriptSuggestions) return;

      let response;
      switch (category) {
        case "price":
          response = `
            <h4>Responses for "Too Expensive" Objection:</h4>
            <ul class="word-track-list">
              <li>"I understand price is an important consideration. Let's look at the total cost of ownership rather than just the monthly payment."</li>
              <li>"When we factor in the tax savings, the effective cost is actually lower than it initially appears."</li>
              <li>"Let me show you a detailed breakdown that includes all the tax benefits and how they reduce your actual out-of-pocket expense."</li>
              <li>"Have you considered the savings from bundling all your vehicle expenses into one pre-tax payment?"</li>
            </ul>
          `;
          break;
        case "time":
          response = `
            <h4>Responses for "Need to Think About It" Objection:</h4>
            <ul class="word-track-list">
              <li>"I completely understand you need time to consider this decision. It's important to be comfortable with your choice."</li>
              <li>"Would it help if I sent you a detailed quote that you can review at your convenience?"</li>
              <li>"Is there any specific information that would help you make your decision?"</li>
              <li>"When would be a good time to follow up with you?"</li>
            </ul>
          `;
          break;
        case "approval":
          response = `
            <h4>Responses for "Might Not Get Approved" Objection:</h4>
            <ul class="word-track-list">
              <li>"That's a valid concern. Let's discuss your situation to better understand your approval chances before proceeding."</li>
              <li>"We have flexible options for different financial situations, and can often find solutions even for challenging cases."</li>
              <li>"We can do a preliminary assessment without any impact on your credit score to give you a better idea."</li>
              <li>"What specific concerns do you have about approval?"</li>
            </ul>
          `;
          break;
        case "employer":
          response = `
            <h4>Responses for "Not Sure My Employer Allows It" Objection:</h4>
            <ul class="word-track-list">
              <li>"I can verify your employer's participation in novated leasing programs before we proceed further."</li>
              <li>"Many employers participate in these programs because they're cost-neutral for them while providing great benefits to employees."</li>
              <li>"If your employer isn't currently participating, we can provide information on how they can easily set up the program."</li>
              <li>"Would you like me to contact your HR department directly to confirm their policy?"</li>
            </ul>
          `;
          break;
        default:
          response = `
            <h4>Generic Response:</h4>
            <ul class="word-track-list">
              <li>"I understand your concern. Let me address that specifically."</li>
              <li>"This is a common question that many customers have. Let me explain how our program works."</li>
              <li>"That's a great point. Let me show you how novated leasing can actually help with this."</li>
            </ul>
          `;
      }

      scriptSuggestions.innerHTML = response;
      scriptSuggestions.scrollIntoView({
        behavior: "smooth",
        block: "nearest",
      });
    },
  };
})();

