// Add this to the existing initPhoneScripts function or replace it if it exists
function initPhoneScripts() {
  try {
    AppLogger.log("Initializing phone script system");

    // Check if our enhanced module exists
    if (NovAssistant.PhoneScripts && typeof NovAssistant.PhoneScripts.init === 'function') {
      // Use the enhanced module
      NovAssistant.PhoneScripts.init();
    } else {
      // Fallback to basic implementation
      setupBasicPhoneScripts();
    }

    AppLogger.log("Phone script system initialized");
  } catch (e) {
    AppLogger.error("Error initializing phone scripts", e, {
      phase: "component-initialization",
      component: "PhoneScripts"
    });
  }
}

// Basic fallback implementation
function setupBasicPhoneScripts() {
  try {
    const getScriptBtn = document.getElementById("getScript");
    const callInput = document.getElementById("callInput");
    const scriptSuggestions = document.getElementById("scriptSuggestions");

    if (!getScriptBtn || !callInput || !scriptSuggestions) {
      AppLogger.error("Missing essential phone script elements", new Error("DOM elements not found"), {
        component: "PhoneScripts"
      });
      return;
    }

    // Setup click handler for script generation
    getScriptBtn.addEventListener("click", function() {
      const inputText = callInput.value.trim();

      if (!inputText) {
        scriptSuggestions.innerHTML = "<p>Please enter a customer objection or context first.</p>";
        return;
      }

      // Show loading state
      scriptSuggestions.innerHTML = '<p><i class="fas fa-spinner fa-spin"></i> Generating suggestions...</p>';

      // Simple response generation (would be replaced with actual API call in production)
      setTimeout(() => {
        scriptSuggestions.innerHTML = `
          <h4>Suggested Responses:</h4>
          <ul>
            <li>"I understand your concern about ${inputText}. Let me address that by explaining how novated leasing can actually help with this."</li>
            <li>"Many customers have similar questions about ${inputText}. Here's how our solution works..."</li>
            <li>"That's a great point about ${inputText}. Let me show you how our novated leasing program specifically addresses this."</li>
          </ul>
        `;
      }, 1000);
    });

    // Setup category buttons
    document.querySelectorAll(".script-category-btn").forEach(button => {
      button.addEventListener("click", function() {
        const category = this.getAttribute("data-category");

        let response;
        switch(category) {
          case "price":
            response = "I understand cost is an important factor. Let's look at the total cost of ownership including the tax benefits, which often makes novated leasing more affordable than it initially appears.";
            break;
          case "time":
            response = "I completely understand you need time to consider this decision. Would it help if I sent you some information to review at your convenience?";
            break;
          case "employer":
            response = "We can certainly verify your employer's participation in novated leasing programs. Many employers are already set up in our system.";
            break;
          default:
            response = "That's a common consideration. Let me explain how our novated leasing program might be beneficial in your situation.";
        }

        scriptSuggestions.innerHTML = `
          <h4>Response for ${category}:</h4>
          <p>${response}</p>
        `;
      });
    });
  } catch (e) {
    AppLogger.error("Error setting up basic phone scripts", e);
  }
}

