import React, { createContext, useContext, useReducer, useEffect } from "react";

// Define initial states
const initialCustomerState = {
  personalInfo: {
    name: "",
    email: "",
    phone: "",
    address: "",
  },
  vehicleInfo: {
    currentVehicle: "",
    makeOfInterest: "",
    modelOfInterest: "",
    colorPreference: "",
    budgetRange: "",
  },
  employmentInfo: {
    employer: "",
    position: "",
    annualSalary: "",
    payCycle: "fortnightly", // default
    employmentLength: "",
  },
  leaseDetails: {
    term: 60, // 5 years default
    annualKm: 15000,
    includeFuel: true,
    includeInsurance: true,
    includeServicing: true,
    includeRegistration: true,
  },
  leadStatus: {
    type: "", // 'fresh-incoming', 'fresh-outbound', 'existing-incoming', etc.
    stage: "new", // 'new', 'contacted', 'quote-sent', 'follow-up', 'closed'
    priority: "medium", // 'low', 'medium', 'high'
    knowledgeLevel: "", // 'clueless', 'basic', 'informed'
    notes: [],
  },
  timestamp: null
};

const initialUIState = {
  sidePanelVisible: true,
  sidePanelWidth: 350, // default width in pixels
  activeTab: "dashboardTab",
  activeScriptFlow: null,
  scriptHistory: [],
  showNewLeadModal: false,
  isEditingCustomer: false,
  lastAction: null,
  notifications: []
};

// Define reducers
const customerReducer = (state, action) => {
  switch (action.type) {
    case "UPDATE_PERSONAL_INFO":
      return {
        ...state,
        personalInfo: { ...state.personalInfo, ...action.payload },
        timestamp: Date.now()
      };
    case "UPDATE_VEHICLE_INFO":
      return {
        ...state,
        vehicleInfo: { ...state.vehicleInfo, ...action.payload },
        timestamp: Date.now()
      };
    case "UPDATE_EMPLOYMENT_INFO":
      return {
        ...state,
        employmentInfo: { ...state.employmentInfo, ...action.payload },
        timestamp: Date.now()
      };
    case "UPDATE_LEASE_DETAILS":
      return {
        ...state,
        leaseDetails: { ...state.leaseDetails, ...action.payload },
        timestamp: Date.now()
      };
    case "UPDATE_LEAD_STATUS":
      return {
        ...state,
        leadStatus: { ...state.leadStatus, ...action.payload },
        timestamp: Date.now()
      };
    case "ADD_NOTE":
      return {
        ...state,
        leadStatus: {
          ...state.leadStatus,
          notes: [
            ...state.leadStatus.notes,
            { id: Date.now(), text: action.payload, timestamp: new Date().toISOString() }
          ]
        },
        timestamp: Date.now()
      };
    case "RESET_CUSTOMER":
      return { ...initialCustomerState, timestamp: Date.now() };
    case "LOAD_CUSTOMER":
      return { ...action.payload, timestamp: Date.now() };
    default:
      return state;
  }
};

const uiReducer = (state, action) => {
  switch (action.type) {
    case "SET_ACTIVE_TAB":
      return { ...state, activeTab: action.payload };
    case "TOGGLE_SIDE_PANEL":
      return { ...state, sidePanelVisible: !state.sidePanelVisible };
    case "SET_SIDE_PANEL":
      return { ...state, sidePanelVisible: action.payload };
    case "RESIZE_SIDE_PANEL":
      return { ...state, sidePanelWidth: action.payload };
    case "SET_SCRIPT_FLOW":
      return {
        ...state,
        activeScriptFlow: action.payload,
        scriptHistory: [...state.scriptHistory, state.activeScriptFlow].filter(Boolean)
      };
    case "SCRIPT_BACK":
      const previousScript = state.scriptHistory.pop() || null;
      return {
        ...state,
        activeScriptFlow: previousScript,
        scriptHistory: [...state.scriptHistory]
      };
    case "TOGGLE_NEW_LEAD_MODAL":
      return { ...state, showNewLeadModal: !state.showNewLeadModal };
    case "SET_EDITING_CUSTOMER":
      return { ...state, isEditingCustomer: action.payload };
    case "LOG_ACTION":
      return {
        ...state,
        lastAction: { type: action.payload, timestamp: Date.now() }
      };
    case "ADD_NOTIFICATION":
      return {
        ...state,
        notifications: [...state.notifications, { id: Date.now(), ...action.payload }]
      };
    case "REMOVE_NOTIFICATION":
      return {
        ...state,
        notifications: state.notifications.filter(n => n.id !== action.payload)
      };
    default:
      return state;
  }
};

// Create contexts
const CustomerContext = createContext();
const UIContext = createContext();

// Context providers
export const CustomerProvider = ({ children }) => {
  const [customerState, customerDispatch] = useReducer(customerReducer, initialCustomerState);

  // Load customer data from localStorage on initial render
  useEffect(() => {
    const savedCustomer = localStorage.getItem("currentCustomer");
    if (savedCustomer) {
      try {
        const parsedData = JSON.parse(savedCustomer);
        customerDispatch({ type: "LOAD_CUSTOMER", payload: parsedData });
      } catch (error) {
        console.error("Failed to parse saved customer data:", error);
      }
    }
  }, []);

  // Save customer data to localStorage whenever it changes
  useEffect(() => {
    if (customerState.timestamp) {
      localStorage.setItem("currentCustomer", JSON.stringify(customerState));
    }
  }, [customerState]);

  return (
    <CustomerContext.Provider value={{ customerState, customerDispatch }}>
      {children}
    </CustomerContext.Provider>
  );
};

export const UIProvider = ({ children }) => {
  const [uiState, uiDispatch] = useReducer(uiReducer, initialUIState);

  return (
    <UIContext.Provider value={{ uiState, uiDispatch }}>
      {children}
    </UIContext.Provider>
  );
};

// Custom hooks for using the contexts
export const useCustomer = () => {
  const context = useContext(CustomerContext);
  if (!context) {
    throw new Error("useCustomer must be used within a CustomerProvider");
  }
  return context;
};

export const useUI = () => {
  const context = useContext(UIContext);
  if (!context) {
    throw new Error("useUI must be used within a UIProvider");
  }
  return context;
};

// Combined provider for convenience
export const AppProvider = ({ children }) => {
  return (
    <CustomerProvider>
      <UIProvider>{children}</UIProvider>
    </CustomerProvider>
  );
};

/**
 * Customer Management Module
 * 
 * Manages customer data, CRUD operations, and state synchronization
 * with localStorage for persistence.
 */

export const CustomerManagement = {
  version: '1.0.0',
  initialized: false,
  customers: [],

  // Initialize the module
  init: async function() {
    try {
      if (this.initialized) {
        return this;
      }

      AppLogger.log("Initializing Customer Management module");

      // Load customer data from storage
      await this.loadCustomers();

      // Initialize dashboard
      this.refreshDashboard();

      // Set up event listeners
      this.setupEventListeners();

      this.initialized = true;
      AppLogger.log("Customer Management module initialized successfully");

      return this;
    } catch (error) {
      AppLogger.error("Failed to initialize Customer Management module", error);
      throw error;
    }
  },

  // Load customers from localStorage
  loadCustomers: async function() {
    try {
      const storedCustomers = localStorage.getItem("customers");

      if (storedCustomers) {
        this.customers = JSON.parse(storedCustomers);
        AppLogger.log(`Loaded ${this.customers.length} customers from localStorage`);
      } else {
        // Initialize with empty array
        this.customers = [];
        this.saveCustomers();
        AppLogger.log("No customers found in localStorage, initialized empty array");
      }

      return this.customers;
    } catch (error) {
      AppLogger.error("Error loading customers from localStorage", error);
      this.customers = [];
      return [];
    }
  },

  // Save customers to localStorage
  saveCustomers: function() {
    try {
      localStorage.setItem("customers", JSON.stringify(this.customers));
      return true;
    } catch (error) {
      AppLogger.error("Error saving customers to localStorage", error);
      return false;
    }
  },

  // Get customers array
  getCustomers: function() {
    return this.customers;
  },

  // Add a new customer
  addCustomer: function(customerData) {
    try {
      // Validate required fields
      if (!customerData.name || !customerData.email || !customerData.phone) {
        throw new Error("Missing required customer fields");
      }

      // Generate a unique ID
      const newCustomer = {
        id: Date.now(),
        name: customerData.name,
        email: customerData.email,
        phone: customerData.phone,
        currentEmployer: customerData.currentEmployer || "Not specified",
        annualSalary: customerData.annualSalary || "0",
        latestNote: customerData.initialNote || "",
        status: "green",
        journeyData: {}, // Initialize empty journey data
        lastUpdated: new Date().toISOString(),
        createdAt: new Date().toISOString(),
        // Additional fields from enhanced form
        address: customerData.address || "",
        employmentType: customerData.employmentType || "fullTime",
        payCycle: customerData.payCycle || "fortnightly",
        currentVehicle: customerData.currentVehicle || "",
        leaseStatus: customerData.leaseStatus || "",
        interestedVehicle: customerData.interestedVehicle || "",
        annualKilometers: customerData.annualKilometers || "15000",
        vehicleType: customerData.vehicleType || "new",
        isEVPHEV: customerData.isEVPHEV || "no",
        vehicleValue: customerData.vehicleValue || "",
        aftermarketProducts: this.processAftermarketProducts(customerData)
      };

      // Add to customers array
      this.customers.push(newCustomer);

      // Save to localStorage
      const saved = this.saveCustomers();

      if (saved) {
        AppLogger.log(`Added new customer: ${customerData.name}`);
        return this.customers.length - 1; // Return index of new customer
      } else {
        throw new Error("Failed to save customer data");
      }
    } catch (error) {
      AppLogger.error("Error adding customer", error);
      throw error;
    }
  },

  // Process aftermarket products from form data
  processAftermarketProducts: function(customerData) {
    const products = [];

    // Check each possible aftermarket product
    const possibleProducts = [
      "motorInsurance", "leaseGuard", "minorDamageRepair", "carbonOffset",
      "roadside", "windowTint", "exteriorProtection", "interiorProtection", "dashCam"
    ];

    possibleProducts.forEach(product => {
      if (customerData[product]) {
        // Convert camelCase to display name
        const displayName = product
          .replace(/([A-Z])/g, ' $1')
          .replace(/^./, str => str.toUpperCase());

        products.push(displayName);
      }
    });

    return products;
  },

  // Update an existing customer
  updateCustomer: function(index, customerData) {
    try {
      if (index < 0 || index >= this.customers.length) {
        throw new Error("Invalid customer index");
      }

      const customer = this.customers[index];

      // Update fields
      for (const [key, value] of Object.entries(customerData)) {
        customer[key] = value;
      }

      // Update last modified timestamp
      customer.lastUpdated = new Date().toISOString();

      // Save changes
      const saved = this.saveCustomers();

      if (saved) {
        AppLogger.log(`Updated customer: ${customer.name}`);
        return true;
      } else {
        throw new Error("Failed to save customer data");
      }
    } catch (error) {
      AppLogger.error("Error updating customer", error);
      return false;
    }
  },

  // Delete a customer
  deleteCustomer: function(index) {
    try {
      if (index < 0 || index >= this.customers.length) {
        throw new Error("Invalid customer index");
      }

      const customerName = this.customers[index].name;

      // Remove customer from array
      this.customers.splice(index, 1);

      // Save changes
      const saved = this.saveCustomers();

      if (saved) {
        AppLogger.log(`Deleted customer: ${customerName}`);
        return true;
      } else {
        throw new Error("Failed to save customer data");
      }
    } catch (error) {
      AppLogger.error("Error deleting customer", error);
      return false;
    }
  },

