export default NovAssistant;

/**
 * Deal Tracking Module
 * 
 * Tracks sales pipeline, KPIs, and provides reporting functionality
 * for sales consultants.
 */

export const DealTracking = {
    version: '1.0.0',
    initialized: false,
    
    // Deal stages
    dealStages: [
      { id: "inquiry", name: "Initial Inquiry", color: "#90caf9" },
      { id: "quoted", name: "Quote Provided", color: "#4fc3f7" },
      { id: "application", name: "Application Submitted", color: "#29b6f6" },
      { id: "approved", name: "Finance Approved", color: "#03a9f4" },
      { id: "settled", name: "Deal Settled", color: "#00897b" },
      { id: "delivered", name: "Vehicle Delivered", color: "#43a047" },
      { id: "lost", name: "Deal Lost", color: "#e53935" }
    ],
    
    // Performance targets
    targets: {
      monthlyDeals: 8,
      monthlyRevenue: 400000,
      aftermarketAttachment: 85, // percentage
      quoteToSale: 35, // percentage
      averageDealValue: 45000
    },
    
    // Current period data
    currentPeriod: {
      startDate: null,
      endDate: null,
      deals: [],
      performance: {}
    },
    
    // Initialize the module
    init: async function() {
      try {
        if (this.initialized) {
          return this;
        }
        
        AppLogger.log("Initializing Deal Tracking module");
        
        // Set up current period
        this.setupCurrentPeriod();
        
        // Load deal data
        await this.loadDeals();
        
        // Set up event listeners
        this.setupEventListeners();
        
        // Update dashboard
        this.updateDealsDashboard();
        
        this.initialized = true;
        AppLogger.log("Deal Tracking module initialized successfully");
        
        return this;
      } catch (error) {
        AppLogger.error("Failed to initialize Deal Tracking module", error);
        throw error;
      }
    },
    
    // Set up current period (current month by default)
    setupCurrentPeriod: function() {
      try {
        const now = new Date();
        const firstDay = new Date(now.getFullYear(), now.getMonth(), 1);
        const lastDay = new Date(now.getFullYear(), now.getMonth() + 1, 0);
        
        this.currentPeriod.startDate = firstDay;
        this.currentPeriod.endDate = lastDay;
        
        // Update period display
        const periodDisplay = document.getElementById('currentPeriod');
        if (periodDisplay) {
          periodDisplay.textContent = `${firstDay.toLocaleDateString()} - ${lastDay.toLocaleDateString()}`;
        }
        
        return true;
      } catch (error) {
        AppLogger.error("Error setting up current period", error);
        return false;
      }
    },
    
    // Load deals from storage
    loadDeals: async function() {
      try {
        // Try to load deals from localStorage
        const storedDeals = localStorage.getItem("deals");
        
        if (storedDeals) {
          const allDeals = JSON.parse(storedDeals);
          
          // Filter for current period
          this.currentPeriod.deals = allDeals.filter(deal => {
            const dealDate = new Date(deal.timestamp);
            return dealDate >= this.currentPeriod.startDate && dealDate <= this.currentPeriod.endDate;
          });
          
          AppLogger.log(`Loaded ${this.currentPeriod.deals.length} deals for current period`);
        } else {
          // Initialize with empty array
          this.currentPeriod.deals = [];
        }
        
        // Calculate performance metrics
        this.calculatePerformance();
        
        return this.currentPeriod.deals;
      } catch (error) {
        AppLogger.error("Error loading deals", error);
        this.currentPeriod.deals = [];
        return [];
      }
    },
    
    // Save all deals to storage
    saveDeals: function() {
      try {
        const storedDeals = localStorage.getItem("deals");
        let allDeals = storedDeals ? JSON.parse(storedDeals) : [];
        
        // Remove current period deals
        allDeals = allDeals.filter(deal => {
          const dealDate = new Date(deal.timestamp);
          return !(dealDate >= this.currentPeriod.startDate && dealDate <= this.currentPeriod.endDate);
        });
        
        // Add current period deals
        allDeals = [...allDeals, ...this.currentPeriod.deals];
        
        // Save to localStorage
        localStorage.setItem("deals", JSON.stringify(allDeals));
        
        return true;
      } catch (error) {
        AppLogger.error("Error saving deals", error);
        return false;
      }
    },
    
    // Set up event listeners
    setupEventListeners: function() {
      try {
        // Add deal button
        const addDealBtn = document.getElementById('addDealBtn');
        if (addDealBtn) {
          addDealBtn.addEventListener('click', this.showAddDealModal.bind(this));
        }
        
        // Period selector
        const periodSelector = document.getElementById('periodSelector');
        if (periodSelector) {
          periodSelector.addEventListener('change', this.changePeriod.bind(this));
        }
        
        // Export report button
        const exportReportBtn = document.getElementById('exportReportBtn');
        if (exportReportBtn) {
          exportReportBtn.addEventListener('click', this.exportReport.bind(this));
        }
        
        return true;
      } catch (error) {
        AppLogger.error("Error setting up event listeners", error);
        return false;
      }
    },
    
    // Show the add deal modal
    showAddDealModal: function() {
      try {
        const addDealModal = document.getElementById('addDealModal');
        if (!addDealModal) return false;
        
        // Reset form
        const dealForm = addDealModal.querySelector('form');
        if (dealForm) {
          dealForm.reset();
        }
        
        // Populate stage select
        const stageSelect = document.getElementById('dealStage');
        if (stageSelect) {
          stageSelect.innerHTML = '';
          
          this.dealStages.forEach(stage => {
            if (stage.id === 'lost') return; // Skip 'lost' stage for new deals
            
            const option = document.createElement('option');
            option.value = stage.id;
            option.textContent = stage.name;
            stageSelect.appendChild(option);
          });
        }
        
        // Populate customer select
        const customerSelect = document.getElementById('dealCustomer');
        if (customerSelect) {
          customerSelect.innerHTML = '<option value="">-- Select Customer --</option>';
          
          const customers = window.NovAssistant.CustomerManagement.getCustomers();
          customers.forEach((customer, index) => {
            const option = document.createElement('option');
            option.value = index;
            option.textContent = customer.name;
            customerSelect.appendChild(option);
          });
        }
        
        // Show modal
        addDealModal.style.display = 'block';
        addDealModal.removeAttribute('hidden');
        
        return true;
      } catch (error) {
        AppLogger.error("Error showing add deal modal", error);
        return false;
      }
    },
    
    // Add a new deal
    addDeal: function(formData) {
      try {
        // Validate required fields
        if (!formData.customer || !formData.vehicle || !formData.value || !formData.stage) {
          window.NovAssistant.ui.showToast("Please fill in all required fields", "warning");
          return false;
        }
        
        // Create new deal object
        const deal = {
          id: Date.now(),
          timestamp: new Date().toISOString(),
          customerId: formData.customer,
          customerName: formData.customerName,
          vehicle: formData.vehicle,
          value: parseFloat(formData.value),
          stage: formData.stage,
          term: parseInt(formData.term) || 60,
          commission: this.calculateCommission(parseFloat(formData.value)),
          aftermarketValue: parseFloat(formData.aftermarketValue) || 0,
          aftermarketItems: formData.aftermarketItems || [],
          notes: formData.notes || ""
        };
        
        // Add to deals array
        this.currentPeriod.deals.push(deal);
        
        // Save deals
        this.saveDeals();
        
        // Recalculate performance
        this.calculatePerformance();
        
        // Update dashboard
        this.updateDealsDashboard();
        
        // Show success message
        window.NovAssistant.ui.showToast("Deal added successfully", "success");
        
        return true;
      } catch (error) {
        AppLogger.error("Error adding deal", error);
        return false;
      }
    },
    
    // Update an existing deal
    updateDeal: function(dealId, formData) {
      try {
        // Find deal index
        const dealIndex = this.currentPeriod.deals.findIndex(deal => deal.id === dealId);
        
        if (dealIndex === -1) {
          window.NovAssistant.ui.showToast("Deal not found", "error");
          return false;
        }
        
        // Update deal
        this.currentPeriod.deals[dealIndex] = {
          ...this.currentPeriod.deals[dealIndex],
          vehicle: formData.vehicle,
          value: parseFloat(formData.value),
          stage: formData.stage,
          term: parseInt(formData.term) || 60,
          commission: this.calculateCommission(parseFloat(formData.value)),
          aftermarketValue: parseFloat(formData.aftermarketValue) || 0,
          aftermarketItems: formData.aftermarketItems || [],
          notes: formData.notes || ""
        };
        
        // Save deals
        this.saveDeals();
        
        // Recalculate performance
        this.calculatePerformance();
        
        // Update dashboard
        this.updateDealsDashboard();
        
        // Show success message
        window.NovAssistant.ui.showToast("Deal updated successfully", "success");
        
        return true;
      } catch (error) {
        AppLogger.error("Error updating deal", error);
        return false;
      }
    },
    
    // Mark a deal as lost
    markDealAsLost: function(dealId, reason) {
      try {
        // Find deal index
        const dealIndex = this.currentPeriod.deals.findIndex(deal => deal.id === dealId);
        
        if (dealIndex === -1) {
          window.NovAssistant.ui.showToast("Deal not found", "error");
          return false;
        }
        
        // Update deal stage
        this.currentPeriod.deals[dealIndex].stage = "lost";
        this.currentPeriod.deals[dealIndex].lostReason = reason || "No reason specified";
        
        // Save deals
        this.saveDeals();
        
        // Recalculate performance
        this.calculatePerformance();
        
        // Update dashboard
        this.updateDealsDashboard();
        
        // Show success message
        window.NovAssistant.ui.showToast("Deal marked as lost", "info");
        
        return true;
      } catch (error) {
        AppLogger.error("Error marking deal as lost", error);
        return false;
      }
    },
    
    // Calculate commission based on deal value
    calculateCommission: function(dealValue) {
      try {
        // Commission tiers
        // Under $30,000: 1.5%
        // $30,000 - $50,000: 2%
        // Over $50,000: 2.5%
        let commissionRate;
        
        if (dealValue < 30000) {
          commissionRate = 0.015;
        } else if (dealValue <= 50000) {
          commissionRate = 0.02;
        } else {
          commissionRate = 0.025;
        }
        
        return dealValue * commissionRate;
      } catch (error) {
        AppLogger.error("Error calculating commission", error);
        return 0;
      }
    },
    
    // Calculate performance metrics
    calculatePerformance: function() {
      try {
        const performance = {
          totalDeals: 0,
          totalRevenue: 0,
          totalCommission: 0,
          aftermarketAttachment: 0,
          stageBreakdown: {}
        };
        
        // Initialize stage breakdown
        this.dealStages.forEach(stage => {
          performance.stageBreakdown[stage.id] = 0;
        });
        
        // Skip if no deals
        if (this.currentPeriod.deals.length === 0) {
          this.currentPeriod.performance = performance;
          return performance;
        }
        
        // Calculate metrics
        this.currentPeriod.deals.forEach(deal => {
          // Count deals that aren't lost
          if (deal.stage !== "lost") {
            performance.totalDeals++;
            performance.totalRevenue += deal.value;
            performance.totalCommission += deal.commission;
          }
          
          // Count deals with aftermarket products
          if (deal.aftermarketValue > 0) {
            performance.aftermarketValue = (performance.aftermarketValue || 0) + deal.aftermarketValue;
          }
          
          // Update stage breakdown
          performance.stageBreakdown[deal.stage]++;
        });
        
        // Calculate aftermarket attachment rate
        const dealsWithAftermarket = this.currentPeriod.deals.filter(deal => deal.aftermarketValue > 0).length;
        performance.aftermarketAttachment = (dealsWithAftermarket / performance.totalDeals) * 100;
        
        // Calculate average deal value
        performance.averageDealValue = performance.totalDeals > 0 ? 
          performance.totalRevenue / performance.totalDeals : 0;
        
        // Calculate quote to sale conversion
        const quotedDeals = this.currentPeriod.deals.filter(deal => 
          deal.stage === "quoted" || 
          deal.stage === "application" || 
          deal.stage === "approved" || 
          deal.stage === "settled" || 
          deal.stage === "delivered").length;
        
        const closedDeals = this.currentPeriod.deals.filter(deal => 
          deal.stage === "settled" || 
          deal.stage === "delivered").length;
        
        performance.quoteToSale = quotedDeals > 0 ? 
          (closedDeals / quotedDeals) * 100 : 0;
        
        // Calculate target achievement
        performance.targetAchievement = {
          deals: (performance.totalDeals / this.targets.monthlyDeals) * 100,
          revenue: (performance.totalRevenue / this.targets.monthlyRevenue) * 100,
          aftermarketAttachment: (performance.aftermarketAttachment / this.targets.aftermarketAttachment) * 100,
          quoteToSale: (performance.quoteToSale / this.targets.quoteToSale) * 100,
          averageDealValue: (performance.averageDealValue / this.targets.averageDealValue) * 100
        };
        
        // Store performance metrics
        this.currentPeriod.performance = performance;
        
        return performance;
      } catch (error) {
        AppLogger.error("Error calculating performance", error);
        return {};
      }
    },
    
    // Update the deals dashboard
    updateDealsDashboard: function() {
      try {
        // Update deals table
        this.updateDealsTable();
        
        // Update performance metrics
        this.updatePerformanceMetrics();
        
        // Update pipeline chart
        this.updatePipelineChart();
        
        return true;
      } catch (error) {
        AppLogger.error("Error updating deals dashboard", error);
        return false;
      }
    },
    
    // Update the deals table
    updateDealsTable: function() {
      try {
        const dealsTableBody = document.querySelector('.deals-table tbody');
        if (!dealsTableBody) return false;
        
        // Clear existing rows
        dealsTableBody.innerHTML = '';
        
        // Add rows for each deal
        this.currentPeriod.deals.forEach(deal => {
          const row = document.createElement('tr');
          
          // Get stage display and color
          const stage = this.dealStages.find(s => s.id === deal.stage);
          const stageDisplay = stage ? stage.name : deal.stage;
          const stageColor = stage ? stage.color : '#999';
          
          // Create row content
          row.innerHTML = `
            <td>${deal.customerName}</td>
            <td>${deal.vehicle}</td>
            <td>${deal.value.toLocaleString()}</td>
            <td><span class="stage-badge" style="background-color: ${stageColor}">${stageDisplay}</span></td>
            <td>${deal.aftermarketValue ? deal.aftermarketValue.toLocaleString() : '0'}</td>
            <td>
              <button class="action-btn edit-deal-btn" data-id="${deal.id}">
                <i class="fas fa-edit"></i>
              </button>
              ${deal.stage !== 'lost' ? `
                <button class="action-btn lost-deal-btn" data-id="${deal.id}">
                  <i class="fas fa-times"></i>
                </button>
              ` : ''}
            </td>
          `;
          
          // Add event listeners
          const editBtn = row.querySelector('.edit-deal-btn');
          if (editBtn) {
            editBtn.addEventListener('click', () => {
              this.showEditDealModal(deal.id);
            });
          }
          
          const lostBtn = row.querySelector('.lost-deal-btn');
          if (lostBtn) {
            lostBtn.addEventListener('click', () => {
              this.showLostDealModal(deal.id);
            });
          }
          
          dealsTableBody.appendChild(row);
        });
        
        // Show message if no deals
        if (this.currentPeriod.deals.length === 0) {
          const emptyRow = document.createElement('tr');
          emptyRow.innerHTML = `
            <td colspan="6" class="text-center">No deals found for this period</td>
          `;
          dealsTableBody.appendChild(emptyRow);
        }
        
        return true;
      } catch (error) {
        AppLogger.error("Error updating deals table", error);
        return false;
      }
    },
    
    // Update performance metrics display
    updatePerformanceMetrics: function() {
      try {
        const performance = this.currentPeriod.performance;
        
        // Update total deals
        const totalDealsElement = document.getElementById('totalDeals');
        if (totalDealsElement) {
          totalDealsElement.textContent = performance.totalDeals || 0;
        }
        
        // Update total revenue
        const totalRevenueElement = document.getElementById('totalRevenue');
        if (totalRevenueElement) {
          totalRevenueElement.textContent = `${(performance.totalRevenue || 0).toLocaleString()}`;
        }
        
        // Update total commission
        const totalCommissionElement = document.getElementById('totalCommission');
        if (totalCommissionElement) {
          totalCommissionElement.textContent = `${(performance.totalCommission || 0).toLocaleString()}`;
        }
        
        // Update aftermarket attachment
        const aftermarketAttachmentElement = document.getElementById('aftermarketAttachment');
        if (aftermarketAttachmentElement) {
          aftermarketAttachmentElement.textContent = `${(performance.aftermarketAttachment || 0).toFixed(1)}%`;
        }
        
        // Update quote to sale
        const quoteToSaleElement = document.getElementById('quoteToSale');
        if (quoteToSaleElement) {
          quoteToSaleElement.textContent = `${(performance.quoteToSale || 0).toFixed(1)}%`;
        }
        
