        return false;
      }
    },
    
    // Update target progress bars
    updateTargetProgressBars: function(targetAchievement) {
      try {
        // Update deals target
        const dealsTargetElement = document.getElementById('dealsTarget');
        if (dealsTargetElement) {
          const percentage = targetAchievement.deals || 0;
          dealsTargetElement.style.width = `${Math.min(percentage, 100)}%`;
          dealsTargetElement.textContent = `${percentage.toFixed(0)}%`;
          
          // Set color based on achievement
          if (percentage >= 100) {
            dealsTargetElement.classList.add('target-exceeded');
          } else if (percentage >= 75) {
            dealsTargetElement.classList.add('target-good');
          } else if (percentage >= 50) {
            dealsTargetElement.classList.add('target-average');
          } else {
            dealsTargetElement.classList.add('target-below');
          }
        }
        
        // Update revenue target
        const revenueTargetElement = document.getElementById('revenueTarget');
        if (revenueTargetElement) {
          const percentage = targetAchievement.revenue || 0;
          revenueTargetElement.style.width = `${Math.min(percentage, 100)}%`;
          revenueTargetElement.textContent = `${percentage.toFixed(0)}%`;
          
          // Set color based on achievement
