            revenueTargetElement.classList.add('target-exceeded');
          } else if (percentage >= 75) {
            revenueTargetElement.classList.add('target-good');
          } else if (percentage >= 50) {
            revenueTargetElement.classList.add('target-average');
          } else {
            revenueTargetElement.classList.add('target-below');
          }
        }
        
        // Update aftermarket target
        const aftermarketTargetElement = document.getElementById('aftermarketTarget');
        if (aftermarketTargetElement) {
          const percentage = targetAchievement.aftermarketAttachment || 0;
          aftermarketTargetElement.style.width = `${Math.min(percentage, 100)}%`;
          aftermarketTargetElement.textContent = `${percentage.toFixed(0)}%`;
          
          // Set color based on achievement
          if (percentage >= 100) {
            aftermarketTargetElement.classList.add('target-exceeded');
          } else if (percentage >= 75) {
            aftermarketTargetElement.classList.add('target-good');
          } else if (percentage >= 50) {
            aftermarketTargetElement.classList.add('target-average');
          } else {
            aftermarketTargetElement.classList.add('target-below');
          }
        }
        
        return true;
      } catch (error) {
        AppLogger.error("Error updating target progress bars", error);
        return false;
      }
    },
    
    // Update pipeline chart
    updatePipelineChart: function() {
      try {
        const pipelineChartElement = document.getElementById('pipelineChart');
        if (!pipelineChartElement) return false;
        
        // Clear chart
        pipelineChartElement.innerHTML = '';
        
        // Get stage breakdown
        const stageBreakdown = this.currentPeriod.performance.stageBreakdown || {};
        
        // Calculate the maximum value for scaling
        const maxValue = Math.max(...this.dealStages.map(stage => stageBreakdown[stage.id] || 0));
        
        // Create chart bars
        this.dealStages.forEach(stage => {
          const count = stageBreakdown[stage.id] || 0;
          const percentage = maxValue > 0 ? (count / maxValue) * 100 : 0;
          
          const barContainer = document.createElement('div');
          barContainer.className = 'pipeline-bar-container';
          
          const label = document.createElement('div');
          label.className = 'pipeline-label';
          label.textContent = stage.name;
          
          const barWrapper = document.createElement('div');
          barWrapper.className = 'pipeline-bar-wrapper';
          
          const bar = document.createElement('div');
          bar.className = 'pipeline-bar';
          bar.style.height = `${percentage}%`;
          bar.style.backgroundColor = stage.color;
          
          const value = document.createElement('div');
          value.className = 'pipeline-value';
          value.textContent = count;
          
          barWrapper.appendChild(bar);
          barWrapper.appendChild(value);
          
          barContainer.appendChild(barWrapper);
          barContainer.appendChild(label);
          
          pipelineChartElement.appendChild(barContainer);
        });
        
        return true;
      } catch (error) {
        AppLogger.error("Error updating pipeline chart", error);
        return false;
      }
    },
    
    // Show edit deal modal
    showEditDealModal: function(dealId) {
      try {
