        const deal = this.currentPeriod.deals.find(d => d.id === dealId);
        if (!deal) {
          window.NovAssistant.ui.showToast("Deal not found", "error");
          return false;
        }
        
        const editDealModal = document.getElementById('editDealModal');
        if (!editDealModal) return false;
        
        // Set form values
