        if (vehicleInput) vehicleInput.value = deal.vehicle;
        
        const valueInput = editDealModal.querySelector('#editValue');
        if (valueInput) valueInput.value = deal.value;
        
        const termInput = editDealModal.querySelector('#editTerm');
        if (termInput) termInput.value = deal.term;
        
        const stageSelect = editDealModal.querySelector('#editStage');
        if (stageSelect) {
          stageSelect.innerHTML = '';
          
          this.dealStages.forEach(stage => {
            if (stage.id === 'lost') return; // Skip 'lost' stage
            
            const option = document.createElement('option');
            option.value = stage.id;
            option.textContent = stage.name;
            option.selected = stage.id === deal.stage;
            stageSelect.appendChild(option);
          });
        }
        
        const aftermarketValueInput = editDealModal.querySelector('#editAftermarketValue');
        if (aftermarketValueInput) aftermarketValueInput.value = deal.aftermarketValue || 0;
        
        const notesInput = editDealModal.querySelector('#editNotes');
        if (notesInput) notesInput.value = deal.notes || '';
        
        // Set deal ID in form
        const dealIdInput = editDealModal.querySelector('#editDealId');
        if (dealIdInput) dealIdInput.value = deal.id;
        
        // Show modal
        editDealModal.style.display = 'block';
        editDealModal.removeAttribute('hidden');
        
        return true;
      } catch (error) {
        AppLogger.error("Error showing edit deal modal", error);
        return false;
      }
    },
    
    // Show lost deal modal
    showLostDealModal: function(dealId) {
      try {
        // Find deal
        const deal = this.currentPeriod.deals.find(d => d.id === dealId);
        if (!deal) {
          window.NovAssistant.ui.showToast("Deal not found", "error");
          return false;
        }
        
        const lostDealModal = document.getElementById('lostDealModal');
        if (!lostDealModal) return false;
        
        // Set deal information
        const dealInfoElement = lostDealModal.querySelector('.deal-info');
        if (dealInfoElement) {
          dealInfoElement.innerHTML = `
            <p><strong>Customer:</strong> ${deal.customerName}</p>
            <p><strong>Vehicle:</strong> ${deal.vehicle}</p>
            <p><strong>Value:</strong> ${deal.value.toLocaleString()}</p>
          `;
        }
        
        // Set deal ID in form
        const dealIdInput = lostDealModal.querySelector('#lostDealId');
        if (dealIdInput) dealIdInput.value = deal.id;
        
        // Show modal
        lostDealModal.style.display = 'block';
        lostDealModal.removeAttribute('hidden');
        
        return true;
      } catch (error) {
        AppLogger.error("Error showing lost deal modal", error);
        return false;
      }
    },
    
    // Change the current period
    changePeriod: function(event) {
      try {
        const periodValue = event.target.value;
        const now = new Date();
        
        let startDate, endDate;
        
        switch (periodValue) {
          case 'month':
            // Current month
            startDate = new Date(now.getFullYear(), now.getMonth(), 1);
            endDate = new Date(now.getFullYear(), now.getMonth() + 1, 0);
            break;
          case 'quarter':
            // Current quarter
            const quarter = Math.floor(now.getMonth() / 3);
            startDate = new Date(now.getFullYear(), quarter * 3, 1);
            endDate = new Date(now.getFullYear(), (quarter + 1) * 3, 0);
            break;
          case 'year':
            // Current year
            startDate = new Date(now.getFullYear(), 0, 1);
            endDate = new Date(now.getFullYear(), 11, 31);
            break;
          case 'lastMonth':
            // Last month
            startDate = new Date(now.getFullYear(), now.getMonth() - 1, 1);
            endDate = new Date(now.getFullYear(), now.getMonth(), 0);
            break;
          case 'lastQuarter':
            // Last quarter
            const currentQuarter = Math.floor(now.getMonth() / 3);
            const lastQuarter = currentQuarter - 1 >= 0 ? currentQuarter - 1 : 3;
            const yearOffset = lastQuarter === 3 ? -1 : 0;
            startDate = new Date(now.getFullYear() + yearOffset, lastQuarter * 3, 1);
            endDate = new Date(now.getFullYear() + yearOffset, (lastQuarter + 1) * 3, 0);
            break;
          case 'lastYear':
            // Last year
            startDate = new Date(now.getFullYear() - 1, 0, 1);
            endDate = new Date(now.getFullYear() - 1, 11, 31);
            break;
          default:
            // Default to current month
            startDate = new Date(now.getFullYear(), now.getMonth(), 1);
            endDate = new Date(now.getFullYear(), now.getMonth() + 1, 0);
        }
        
        // Update current period
        this.currentPeriod.startDate = startDate;
        this.currentPeriod.endDate = endDate;
        
        // Update period display
        const periodDisplay = document.getElementById('currentPeriod');
        if (periodDisplay) {
          periodDisplay.textContent = `${startDate.toLocaleDateString()} - ${endDate.toLocaleDateString()}`;
        }
        
        // Reload deals for the new period
        this.loadDeals();
        
        // Update dashboard
        this.updateDealsDashboard();
        
        return true;
      } catch (error) {
        AppLogger.error("Error changing period", error);
        return false;
      }
    },
    
    // Export performance report
    exportReport: function() {
      try {
        const performance = this.currentPeriod.performance;
        
        // Format date range
        const startDate = this.currentPeriod.startDate.toLocaleDateString();
        const endDate = this.currentPeriod.endDate.toLocaleDateString();
        
        // Build report content
        let reportContent = `# SG Fleet Performance Report\n`;
        reportContent += `## Period: ${startDate} - ${endDate}\n\n`;
        
        // Add performance metrics
        reportContent += `### Performance Metrics\n\n`;
        reportContent += `- Total Deals: ${performance.totalDeals || 0}\n`;
        reportContent += `- Total Revenue: ${(performance.totalRevenue || 0).toLocaleString()}\n`;
        reportContent += `- Total Commission: ${(performance.totalCommission || 0).toLocaleString()}\n`;
        reportContent += `- Aftermarket Attachment: ${(performance.aftermarketAttachment || 0).toFixed(1)}%\n`;
        reportContent += `- Quote to Sale Conversion: ${(performance.quoteToSale || 0).toFixed(1)}%\n`;
        reportContent += `- Average Deal Value: ${(performance.averageDealValue || 0).toLocaleString()}\n\n`;
        
        // Add target achievement
        const targetAchievement = performance.targetAchievement || {};
        reportContent += `### Target Achievement\n\n`;
        reportContent += `- Deals: ${(targetAchievement.deals || 0).toFixed(1)}% (${performance.totalDeals || 0}/${this.targets.monthlyDeals})\n`;
        reportContent += `- Revenue: ${(targetAchievement.revenue || 0).toFixed(1)}% (${(performance.totalRevenue || 0).toLocaleString()}/${this.targets.monthlyRevenue.toLocaleString()})\n`;
        reportContent += `- Aftermarket Attachment: ${(targetAchievement.aftermarketAttachment || 0).toFixed(1)}% (${(performance.aftermarketAttachment || 0).toFixed(1)}%/${this.targets.aftermarketAttachment}%)\n`;
        reportContent += `- Quote to Sale Conversion: ${(targetAchievement.quoteToSale || 0).toFixed(1)}% (${(performance.quoteToSale || 0).toFixed(1)}%/${this.targets.quoteToSale}%)\n\n`;
        
        // Add pipeline breakdown
        const stageBreakdown = performance.stageBreakdown || {};
        reportContent += `### Pipeline Breakdown\n\n`;
        
        this.dealStages.forEach(stage => {
          reportContent += `- ${stage.name}: ${stageBreakdown[stage.id] || 0}\n`;
        });
        
        reportContent += `\n### Deal List\n\n`;
        
        // Add deal list
        if (this.currentPeriod.deals.length === 0) {
          reportContent += `No deals found for this period.\n`;
        } else {
          // Table header
          reportContent += `| Customer | Vehicle | Value | Stage | Aftermarket |\n`;
          reportContent += `| -------- | ------- | ----- | ----- | ----------- |\n`;
          
          // Table rows
          this.currentPeriod.deals.forEach(deal => {
            const stage = this.dealStages.find(s => s.id === deal.stage);
            const stageDisplay = stage ? stage.name : deal.stage;
            
            reportContent += `| ${deal.customerName} | ${deal.vehicle} | ${deal.value.toLocaleString()} | ${stageDisplay} | ${(deal.aftermarketValue || 0).toLocaleString()} |\n`;
          });
        }
        
        // Copy to clipboard or trigger download
        navigator.clipboard.writeText(reportContent)
          .then(() => {
            window.NovAssistant.ui.showToast("Report copied to clipboard", "success");
          })
          .catch((error) => {
            // If clipboard fails, show error
            window.NovAssistant.ui.showToast("Failed to copy report to clipboard", "error");
            AppLogger.error("Error copying report to clipboard", error);
          });
        
        return true;
      } catch (error) {
        AppLogger.error("Error exporting report", error);
        return false;
      }
    }
  };

/**
 * NovAssistant Browser Extension
 * Architecture Overview
 * 
 * This extension provides real-time assistance for novated lease consultants
 * by integrating with existing web applications without requiring admin rights.
 */

// Extension Structure
const NovAssistantExtension = {
  // Core Components
  components: {
    sidebar: null,       // Sidebar UI component
    scriptEngine: null,  // Script suggestion engine
    customerTracker: null, // Customer journey tracker
    contextDetector: null, // Detects current application context
  },

  // Application State
  state: {
    currentApp: null,    // Current detected application (Outlook, Dynamics, etc.)
    currentCustomer: null, // Current customer data
    activeScript: null,  // Active script flow
    sidebarVisible: true, // Sidebar visibility state
  },

  // Initialization
  init: function() {
    // Initialize context detector
    this.components.contextDetector = new ContextDetector();
    this.components.contextDetector.init();

    // Initialize sidebar
    this.components.sidebar = new Sidebar();
    this.components.sidebar.init();

    // Initialize script engine
    this.components.scriptEngine = new ScriptEngine();
    this.components.scriptEngine.init();

    // Initialize customer tracker
    this.components.customerTracker = new CustomerTracker();
    this.components.customerTracker.init();

    // Set up event listeners
    this.setupEventListeners();

    // Perform initial context detection
    this.detectInitialContext();
  },

  // Set up event listeners
  setupEventListeners: function() {
    // Listen for URL changes
    window.addEventListener('hashchange', this.handleUrlChange.bind(this));
    window.addEventListener('popstate', this.handleUrlChange.bind(this));

    // Listen for customer selection events
    document.addEventListener('novassistant:customerSelected', this.handleCustomerSelection.bind(this));

    // Listen for sidebar toggle events
    document.addEventListener('novassistant:toggleSidebar', this.toggleSidebar.bind(this));
  },

  // Handle URL changes
  handleUrlChange: function(event) {
    this.detectContext();
  },

  // Detect initial context
  detectInitialContext: function() {
    const context = this.components.contextDetector.detectContext(window.location.href);
    this.updateContext(context);
  },

  // Detect current context
  detectContext: function() {
    const context = this.components.contextDetector.detectContext(window.location.href);
    this.updateContext(context);
  },

  // Update context
  updateContext: function(context) {
    this.state.currentApp = context.app;

    // Update UI based on context
    this.components.sidebar.updateForContext(context);

    // Extract customer data if available
    if (context.customerId) {
      this.fetchCustomerData(context.customerId);
    }
  },

  // Fetch customer data
  fetchCustomerData: function(customerId) {
    // Could fetch from local storage or from page DOM elements
    const customerData = this.components.contextDetector.extractCustomerData();
    this.state.currentCustomer = customerData;

    // Update components with customer data
    this.components.sidebar.updateCustomerInfo(customerData);
    this.components.scriptEngine.setCustomerContext(customerData);
    this.components.customerTracker.updateCustomer(customerData);
  },

  // Handle customer selection
  handleCustomerSelection: function(event) {
    const customerId = event.detail.customerId;
    this.fetchCustomerData(customerId);
  },

  // Toggle sidebar visibility
  toggleSidebar: function() {
    this.state.sidebarVisible = !this.state.sidebarVisible;
    this.components.sidebar.setVisible(this.state.sidebarVisible);
  }
};

/**
 * Context Detector
 * Detects the current application context and extracts relevant data
 */
class ContextDetector {
  constructor() {
    this.appPatterns = {
      outlook: /outlook.office.com/i,
      dynamics: /dynamics.com/i,
      excel: /excel.office.com/i,
      teams: /teams.microsoft.com/i,
      powerApp: /apps.powerapps.com/i
    };
  }

  init() {
    // Initialize mutation observers for DOM changes
    this.setupMutationObservers();
  }

  setupMutationObservers() {
    // Create a mutation observer to detect changes to the DOM
    const observer = new MutationObserver(this.handleDomChanges.bind(this));

    // Start observing
    observer.observe(document.body, {
      childList: true,
      subtree: true
    });
  }

  handleDomChanges(mutations) {
    // Check if customer data elements have been added or changed
    const customerDataChanged = this.checkForCustomerDataChanges(mutations);

    if (customerDataChanged) {
      // Dispatch event for customer data changes
      const event = new CustomEvent('novassistant:customerDataChanged');
      document.dispatchEvent(event);
    }
  }

  checkForCustomerDataChanges(mutations) {
    // Implementation depends on the specific applications
    // For example, looking for specific DOM elements in Dynamics

    // Simplified example:
    for (const mutation of mutations) {
      if (mutation.type === 'childList') {
        for (const node of mutation.addedNodes) {
          if (node.nodeType === 1) { // Element node
            // Check for customer data elements
            if (node.querySelector('.customer-card, .customer-name, .customer-email')) {
              return true;
            }
          }
        }
      }
    }

    return false;
  }

  detectContext(url) {
    let context = {
      app: null,
      view: null,
      customerId: null
    };

    // Detect application
    if (this.appPatterns.outlook.test(url)) {
      context.app = 'outlook';
      context = this.detectOutlookContext(context);
    } else if (this.appPatterns.dynamics.test(url)) {
      context.app = 'dynamics';
      context = this.detectDynamicsContext(context);
    } else if (this.appPatterns.excel.test(url)) {
      context.app = 'excel';
      context = this.detectExcelContext(context);
    } else if (this.appPatterns.teams.test(url)) {
      context.app = 'teams';
      context = this.detectTeamsContext(context);
    } else if (this.appPatterns.powerApp.test(url)) {
      context.app = 'powerApp';
      context = this.detectPowerAppContext(context);
    }

    return context;
  }

  detectOutlookContext(context) {
    // Detect if we're in an email view
    if (document.querySelector('.ReadingPaneContainer, .itemView')) {
      context.view = 'email';

      // Try to extract customer info from email
      const customerInfo = this.extractCustomerInfoFromEmail();
      if (customerInfo) {
        context.customerId = customerInfo.id;
      }
    }

    return context;
  }

  detectDynamicsContext(context) {
    // Detect if we're in a contact/customer view
    if (document.querySelector('.contactCard, .customerForm')) {
      context.view = 'customer';

      // Extract customer ID from URL or DOM
      const urlParams = new URLSearchParams(window.location.search);
      const id = urlParams.get('id');
      if (id) {
        context.customerId = id;
      }
    }

    return context;
  }

  detectExcelContext(context) {
    // Detect if we're in a specific worksheet
    const sheetName = this.getExcelSheetName();
    if (sheetName && sheetName.toLowerCase().includes('customer')) {
      context.view = 'customerData';
    }

    return context;
  }

  detectTeamsContext(context) {
    // Detect if we're in a specific Teams context
    return context;
  }

  detectPowerAppContext(context) {
    // Detect if we're in a Power App
    return context;
  }

  getExcelSheetName() {
    // This would need to be implemented specifically for Excel web interface
    // Simplified placeholder implementation
    const sheetTab = document.querySelector('.sheetTab.selected');
    return sheetTab ? sheetTab.textContent : null;
  }

  extractCustomerInfoFromEmail() {
    // Extract customer information from email content
    // This is highly dependent on email format

    // Simplified implementation
    const emailBody = document.querySelector('.message-body, .itemBody');
    if (!emailBody) return null;

    const emailText = emailBody.textContent;

    // Look for patterns like "Customer: John Smith" or "Customer ID: 12345"
    const customerNameMatch = emailText.match(/Customer:\s*([^,\n]+)/i);
    const customerIdMatch = emailText.match(/Customer ID:\s*([^,\n]+)/i);

    if (customerNameMatch || customerIdMatch) {
      return {
        id: customerIdMatch ? customerIdMatch[1].trim() : null,
        name: customerNameMatch ? customerNameMatch[1].trim() : null
      };
    }

    return null;
  }

  extractCustomerData() {
    // Extract customer data from the current page
    // Implementation depends on the current application

    let customerData = {
      id: null,
      name: null,
      email: null,
      phone: null,
      stage: null,
      vehicle: null
    };

    // Extract based on current app
    if (this.state && this.state.currentApp === 'dynamics') {
      customerData = this.extractDynamicsCustomerData();
    } else if (this.state && this.state.currentApp === 'outlook') {
      customerData = this.extractOutlookCustomerData();
    }

    return customerData;
  }

  extractDynamicsCustomerData() {
    // Extract customer data from Dynamics 365
    const customerData = {
      id: null,
      name: null,
      email: null,
      phone: null,
      stage: null,
      vehicle: null
    };

    // Find customer name
    const nameElement = document.querySelector('[data-id="fullname_compositionLinkControl-fullname_textInputBox_text"]');
    if (nameElement) {
      customerData.name = nameElement.value || nameElement.textContent;
    }

    // Find email
    const emailElement = document.querySelector('[data-id="emailaddress1.fieldControl-mail-text-input"]');
    if (emailElement) {
      customerData.email = emailElement.value || emailElement.textContent;
    }

    // Find phone
    const phoneElement = document.querySelector('[data-id="telephone1.fieldControl-phone-text-input"]');
    if (phoneElement) {
      customerData.phone = phoneElement.value || phoneElement.textContent;
    }

    return customerData;
  }

  extractOutlookCustomerData() {
    // This would need custom implementation based on your email format
    // Could use regex or other pattern matching to find customer details

    // Simplified placeholder
    return {
      id: null,
      name: null,
      email: null,
      phone: null,
      stage: null,
      vehicle: null
    };
  }
}

