              "Your employer makes lease payments on your behalf directly from your salary - part from pre-tax and part from post-tax.",
              "This arrangement can offer significant tax benefits, which I'll explain shortly."
            ]
          }
        ],
        question: "Does that make sense so far?",
        options: [
          { label: "Yes, Tell Me More", flow: "collect-info" },
          { label: "I Need More Clarity", flow: "explain-with-examples" }
        ]
      },

      // More script templates would be defined here
    };
  }

  setCustomerContext(customerData) {
    this.customerContext = customerData;
    this.updateScriptUI();
  }

  setCurrentFlow(flowId) {
    this.currentFlow = flowId;
    this.updateScriptUI();
  }

  updateScriptUI() {
    // Find script engine element
    const scriptEngineElement = document.querySelector('.script-engine');
    if (!scriptEngineElement) return;

    // Get current flow or default to initial
    const flow = this.currentFlow ? this.scriptTemplates[this.currentFlow] : this.scriptTemplates.initial;
    if (!flow) return;

    // Create script UI
    const scriptUI = document.createElement('div');
    scriptUI.className = 'script-flow';

    // Add title and intro
    scriptUI.innerHTML = `
      <h3>${this.processTemplate(flow.title)}</h3>
      <p class="script-intro">${this.processTemplate(flow.intro || '')}</p>
    `;

    // Add sections if available
    if (flow.sections && flow.sections.length > 0) {
      const sectionsContainer = document.createElement('div');
      sectionsContainer.className = 'script-sections';

      flow.sections.forEach(section => {
        const sectionElement = document.createElement('div');
        sectionElement.className = 'script-section';

        sectionElement.innerHTML = `
          <h4>${this.processTemplate(section.title)}</h4>
          <ul class="word-track-list">
            ${section.script.map(line => `<li>${this.processTemplate(line)}</li>`).join('')}
          </ul>
        `;

        sectionsContainer.appendChild(sectionElement);
      });

      scriptUI.appendChild(sectionsContainer);
    }

    // Add general script if available
    if (flow.generalScript && flow.generalScript.length > 0) {
      const generalScriptElement = document.createElement('div');
      generalScriptElement.className = 'general-script';

      generalScriptElement.innerHTML = `
        <h4>General Script</h4>
        <ul class="word-track-list">
          ${flow.generalScript.map(line => `<li>${this.processTemplate(line)}</li>`).join('')}
        </ul>
      `;

      scriptUI.appendChild(generalScriptElement);
    }

    // Add question and options if available
    if (flow.question) {
      const questionElement = document.createElement('div');
      questionElement.className = 'script-question';

      questionElement.innerHTML = `
        <h4>${this.processTemplate(flow.question)}</h4>
      `;

      if (flow.options && flow.options.length > 0) {
        const optionsContainer = document.createElement('div');
        optionsContainer.className = 'script-options';

        flow.options.forEach(option => {
          const optionButton = document.createElement('button');
          optionButton.className = 'script-option-btn';
          optionButton.textContent = option.label;
          optionButton.setAttribute('data-flow', option.flow);

          optionButton.addEventListener('click', () => {
            this.setCurrentFlow(option.flow);
          });

          optionsContainer.appendChild(optionButton);
        });

        questionElement.appendChild(optionsContainer);
      }

      scriptUI.appendChild(questionElement);
    }

    // Replace content
    scriptEngineElement.innerHTML = '';
    scriptEngineElement.appendChild(scriptUI);
  }

  processTemplate(template) {
    if (!template) return '';
    if (!this.customerContext) return template;

    // Replace placeholders with customer data
    return template.replace(/\{\{([^}]+)\}\}/g, (match, key) => {
      return this.customerContext[key] || match;
    });
  }
}

/**
 * Customer Tracker
 * Tracks customer journey stages
 */
class CustomerTracker {
  constructor() {
    this.customer = null;
    this.journeyStages = [
      { id: 'initial-contact', name: 'Initial Contact' },
      { id: 'needs-assessment', name: 'Needs Assessment' },
      { id: 'quote-provided', name: 'Quote Provided' },
      { id: 'quote-followup', name: 'Quote Follow-up' },
      { id: 'application-process', name: 'Application Process' },
      { id: 'documentation', name: 'Documentation' },
      { id: 'settlement', name: 'Settlement' },
      { id: 'delivery', name: 'Delivery' }
    ];
  }

  init() {
    // Initialize customer tracker
  }

  updateCustomer(customerData) {
    this.customer = customerData;
    this.updateJourneyUI();
  }

  updateJourneyUI() {
    // Find journey tracker element
    const journeyElement = document.querySelector('.journey-tracker');
    if (!journeyElement) return;

    // Clear existing content
    journeyElement.innerHTML = '';

    // If no customer, show placeholder
    if (!this.customer) {
      journeyElement.innerHTML = '<p>No customer selected</p>';
      return;
    }

    // Create journey UI
    const journeyUI = document.createElement('div');
    journeyUI.className = 'journey-flow';

    // Add customer info
    journeyUI.innerHTML = `
      <h3>${this.customer.name || 'Customer'}</h3>
      <p class="customer-info">
        ${this.customer.email ? `<span><strong>Email:</strong> ${this.customer.email}</span>` : ''}
        ${this.customer.phone ? `<span><strong>Phone:</strong> ${this.customer.phone}</span>` : ''}
      </p>
    `;

    // Add journey progress
    const journeyProgress = document.createElement('div');
    journeyProgress.className = 'journey-progress';

    // Determine current stage
    const currentStageIndex = this.determineCurrentStageIndex();

    // Add progress bar
    journeyProgress.innerHTML = `
      <div class="progress-bar">
        <div class="progress-fill" style="width: ${(currentStageIndex / (this.journeyStages.length - 1)) * 100}%"></div>
      </div>
    `;

    // Add journey stages
    const stagesContainer = document.createElement('div');
    stagesContainer.className = 'journey-stages';

    this.journeyStages.forEach((stage, index) => {
      const stageElement = document.createElement('div');
      stageElement.className = `journey-stage ${index < currentStageIndex ? 'completed' : index === currentStageIndex ? 'active' : ''}`;

      stageElement.innerHTML = `
        <div class="stage-marker"></div>
        <div class="stage-name">${stage.name}</div>
      `;

      stagesContainer.appendChild(stageElement);
    });

    journeyProgress.appendChild(stagesContainer);
    journeyUI.appendChild(journeyProgress);

    // Replace content
    journeyElement.appendChild(journeyUI);
  }

  determineCurrentStageIndex() {
    // Determine current stage based on customer data
    // In a real implementation, this would use customer.stage or other data

    // Simplified implementation - random stage for demo
    return Math.floor(Math.random() * this.journeyStages.length);
  }
}

