/**
 * NovAssistant Lease Calculator
 * 
 * Provides financial calculations for novated leases, including tax benefits,
 * running costs, and comparison with other financing methods.
 */

// Calculator Module
const LeaseCalculator = {
  // Constants for calculations
  constants: {
    // Tax brackets (2023-2024 Australian tax rates)
    taxBrackets: [
      { threshold: 0, rate: 0 },
      { threshold: 18201, rate: 0.19 },
      { threshold: 45001, rate: 0.325 },
      { threshold: 120001, rate: 0.37 },
      { threshold: 180001, rate: 0.45 }
    ],

    // ATO residual value percentages
    residualValuePercentages: {
      12: 0.65625, // 1 year: 65.625%
      24: 0.4375,  // 2 years: 43.75%
      36: 0.28125, // 3 years: 28.125%
      48: 0.1875,  // 4 years: 18.75%
      60: 0.15625  // 5 years: 15.625%
    },

    // Interest rates
    interestRate: 0.0649,         // 6.49% p.a. for novated lease
    carLoanRate: 0.0749,          // 7.49% p.a. for car loan
    investmentReturnRate: 0.05,   // 5% p.a. for opportunity cost calculation

    // FBT rates
    fbtRate: 0.47,                // 47% FBT rate
    statutoryFormulaRate: 0.20,   // 20% statutory formula rate

    // Running costs estimates
    fuelCostPerKm: 0.15,          // $0.15 per km
    insuranceRatePerValue: 0.04,  // 4% of vehicle value per year
    servicingRatePerValue: 0.03,  // 3% of vehicle value per year
    annualRegistrationCost: 800,  // $800 per year

    // GST rate
    gstRate: 0.10                 // 10% GST
  },

  // Customer data
  customerData: null,

  // Form data
  formData: {
    vehiclePrice: 40000,
    leaseTerm: 60,
    annualKm: 15000,
    annualSalary: 80000,
    includeFuel: true,
    includeInsurance: true,
    includeServicing: true,
    includeRegistration: true
  },

  // Calculation results
  results: null,

  // Initialize the calculator
  init: function() {
    this.loadCustomerData();
    this.setupEventListeners();
    this.populateFormFromCustomerData();
  },

  // Set up event listeners
  setupEventListeners: function() {
    // Calculate button
    const calculateBtn = document.getElementById('calculateBtn');
    if (calculateBtn) {
      calculateBtn.addEventListener('click', this.calculate.bind(this));
    }

    // Form inputs - update form data when changed
    const formInputs = document.querySelectorAll('#vehiclePrice, #leaseTerm, #annualKm, #annualSalary');
    formInputs.forEach(input => {
      input.addEventListener('change', this.updateFormData.bind(this));
    });

    // Checkbox inputs
    const checkboxInputs = document.querySelectorAll('#includeFuel, #includeInsurance, #includeServicing, #includeRegistration');
    checkboxInputs.forEach(checkbox => {
      checkbox.addEventListener('change', this.updateFormData.bind(this));
    });

    // Listen for tab activation
    document.addEventListener('click', (event) => {
      if (event.target.classList.contains('nov-tab-btn') && 
          event.target.getAttribute('data-tab') === 'calculator') {
        this.populateFormFromCustomerData();
      }
    });

    // Listen for customer data updates
    chrome.runtime.onMessage.addListener((message) => {
      if (message.type === 'CUSTOMER_UPDATED') {
        this.loadCustomerData();
      }
    });
  },

  // Load customer data
  loadCustomerData: function() {
    chrome.runtime.sendMessage({ type: 'GET_STATE' }, (response) => {
      if (response && response.currentCustomer) {
        this.customerData = response.currentCustomer;
        this.populateFormFromCustomerData();
      }
    });
  },

  // Update form data from input changes
  updateFormData: function(event) {
    const input = event.target;
    const id = input.id;
    let value;

    if (input.type === 'checkbox') {
      value = input.checked;
    } else if (input.type === 'number') {
      value = parseFloat(input.value) || 0;
    } else {
      value = input.value;
    }

    this.formData[id] = value;
  },

  // Populate form from customer data
  populateFormFromCustomerData: function() {
    // Only populate if customer data exists
    if (!this.customerData) return;

    // Initialize form fields from customer data if available
    if (this.customerData.vehicleValue) {
      document.getElementById('vehiclePrice').value = this.customerData.vehicleValue;
      this.formData.vehiclePrice = this.customerData.vehicleValue;
    }

    if (this.customerData.annualKm) {
      document.getElementById('annualKm').value = this.customerData.annualKm;
      this.formData.annualKm = this.customerData.annualKm;
    }

    // Try to parse and set annual salary
    if (this.customerData.annualSalary) {
      // Handle different formats (string with $ or number)
      let salary = this.customerData.annualSalary;

      if (typeof salary === 'string') {
        // Remove non-numeric characters except decimal point
        salary = parseFloat(salary.replace(/[^0-9.]/g, '')) || 0;
      }

      if (salary > 0) {
        document.getElementById('annualSalary').value = salary;
        this.formData.annualSalary = salary;
      }
    }

    // If we have previous calculation results for this customer, restore form state
    chrome.storage.local.get('calculations', (data) => {
      const calculations = data.calculations || {};
      if (this.customerData.id && calculations[this.customerData.id]) {
        const savedCalculation = calculations[this.customerData.id];

        // Restore saved form state if available
        if (savedCalculation.formData) {
          this.restoreFormState(savedCalculation.formData);
        }

        // Show previous results
        if (savedCalculation.results) {
          this.results = savedCalculation.results;
          this.displayResults();
        }
      }
    });
  },

  // Restore form state from saved data
  restoreFormState: function(savedFormData) {
    // Only restore if data exists
    if (!savedFormData) return;

    // Update form with saved values
    for (const [key, value] of Object.entries(savedFormData)) {
      const input = document.getElementById(key);
      if (input) {
        if (input.type === 'checkbox') {
          input.checked = value;
        } else {
          input.value = value;
        }

        // Update internal form data
        this.formData[key] = value;
      }
    }
  },

  // Perform calculation
  calculate: function() {
    // Get current form values
    this.captureFormValues();

    // Validate inputs
    if (!this.validateInputs()) {
      return;
    }

    // Perform calculations
    this.results = this.performCalculations();

    // Display results
    this.displayResults();

    // Save calculation
    this.saveCalculation();
  },

  // Capture current form values
  captureFormValues: function() {
    // Get numeric values
    this.formData.vehiclePrice = parseFloat(document.getElementById('vehiclePrice').value) || 0;
    this.formData.leaseTerm = parseInt(document.getElementById('leaseTerm').value) || 60;
    this.formData.annualKm = parseInt(document.getElementById('annualKm').value) || 15000;
    this.formData.annualSalary = parseFloat(document.getElementById('annualSalary').value) || 80000;

    // Get checkbox values
    this.formData.includeFuel = document.getElementById('includeFuel').checked;
    this.formData.includeInsurance = document.getElementById('includeInsurance').checked;
    this.formData.includeServicing = document.getElementById('includeServicing').checked;
    this.formData.includeRegistration = document.getElementById('includeRegistration').checked;
  },

  // Validate form inputs
  validateInputs: function() {
    // Check vehicle price
    if (this.formData.vehiclePrice <= 0) {
      this.showError('Please enter a valid vehicle price');
      return false;
    }

    // Check lease term
    if (![12, 24, 36, 48, 60].includes(this.formData.leaseTerm)) {
      this.showError('Please select a valid lease term');
      return false;
    }

    // Check annual kilometers
    if (this.formData.annualKm <= 0) {
      this.showError('Please enter valid annual kilometers');
      return false;
    }

    // Check annual salary
    if (this.formData.annualSalary <= 0) {
      this.showError('Please enter a valid annual salary');
      return false;
    }

    return true;
  },

  // Show error message
  showError: function(message) {
    // Create notification element
    const notification = document.createElement('div');
    notification.className = 'nov-notification error';
    notification.innerHTML = `
      <div class="nov-notification-content">
        <span class="nov-notification-message">${message}</span>
        <button class="nov-notification-close">×</button>
      </div>
    `;

    // Add to document
    document.body.appendChild(notification);

    // Handle close button
    notification.querySelector('.nov-notification-close').addEventListener('click', () => {
      notification.remove();
    });

    // Auto-remove after 5 seconds
    setTimeout(() => {
      if (notification.parentElement) {
        notification.remove();
      }
    }, 5000);
  },

  // Perform all calculations
  performCalculations: function() {
    const params = this.formData;

    // Calculate tax rate based on income
    const taxRate = this.calculateTaxRate(params.annualSalary);

    // Calculate running costs
    const runningCosts = this.calculateRunningCosts(params);

    // Calculate GST savings
    const gstSavings = this.calculateGstSavings(params, runningCosts);

    // Calculate tax savings
    const taxSavings = this.calculateTaxSavings(params, runningCosts, taxRate);

    // Calculate residual value
    const residualValue = this.calculateResidualValue(params.vehiclePrice, params.leaseTerm);

    // Calculate novated lease payments
    const leasePayments = this.calculateLeasePayments(params, residualValue);

    // Calculate FBT
    const fbtAmount = this.calculateFBT(params);

    // Calculate total novated lease cost
    const monthlyLeasePayment = leasePayments.monthlyPayment + runningCosts.totalMonthly + fbtAmount - gstSavings.totalMonthlyGstSavings - taxSavings.monthlyTaxSavings;

    // Calculate car loan comparison
    const carLoanPayment = this.calculateCarLoanPayment(params) + runningCosts.totalMonthly;

    // Calculate cash purchase comparison
    const cashPurchasePayment = this.calculateCashPurchasePayment(params) + runningCosts.totalMonthly;

    // Calculate savings
    const monthlySavings = carLoanPayment - monthlyLeasePayment;
    const termSavings = monthlySavings * params.leaseTerm;

    // Return comprehensive results
    return {
      // Main results
      monthlyLeasePayment,
      carLoanPayment,
      cashPurchasePayment,
      monthlySavings,
      termSavings,

      // Detailed breakdowns
      taxRate,
      residualValue,
      fbtAmount,

      runningCosts,
      gstSavings,
      taxSavings,
      leasePayments,

      // Original form data for restoring state
      formData: {...params}
    };
  },

  // Calculate tax rate based on income
  calculateTaxRate: function(annualSalary) {
    let applicableTaxRate = 0;

    // Find applicable tax bracket
    for (let i = this.constants.taxBrackets.length - 1; i >= 0; i--) {
      if (annualSalary > this.constants.taxBrackets[i].threshold) {
        applicableTaxRate = this.constants.taxBrackets[i].rate;
        break;
      }
    }

    return applicableTaxRate;
  },

  // Calculate running costs
  calculateRunningCosts: function(params) {
    // Monthly fuel costs
    const monthlyFuelCost = params.includeFuel ? 
      (params.annualKm * this.constants.fuelCostPerKm) / 12 : 0;

    // Monthly insurance costs
    const monthlyInsuranceCost = params.includeInsurance ? 
      (params.vehiclePrice * this.constants.insuranceRatePerValue) / 12 : 0;

    // Monthly servicing costs
    const monthlyServicingCost = params.includeServicing ? 
      (params.vehiclePrice * this.constants.servicingRatePerValue) / 12 : 0;

    // Monthly registration costs
    const monthlyRegistrationCost = params.includeRegistration ? 
      this.constants.annualRegistrationCost / 12 : 0;

    // Total monthly running costs
    const totalMonthly = monthlyFuelCost + monthlyInsuranceCost + 
                        monthlyServicingCost + monthlyRegistrationCost;

    return {
      monthlyFuelCost,
      monthlyInsuranceCost,
      monthlyServicingCost,
      monthlyRegistrationCost,
      totalMonthly,
      annualTotal: totalMonthly * 12
    };
  },

  // Calculate GST savings
  calculateGstSavings: function(params, runningCosts) {
    // GST savings on vehicle purchase
    const vehicleGst = params.vehiclePrice * this.constants.gstRate;
    const monthlyVehicleGstSavings = vehicleGst / params.leaseTerm;

    // GST savings on running costs
    const monthlyRunningCostsGstSavings = runningCosts.totalMonthly * this.constants.gstRate;

    // Total monthly GST savings
    const totalMonthlyGstSavings = monthlyVehicleGstSavings + monthlyRunningCostsGstSavings;

    return {
      vehicleGst,
      monthlyVehicleGstSavings,
      monthlyRunningCostsGstSavings,
      totalMonthlyGstSavings,
      annualGstSavings: totalMonthlyGstSavings * 12
    };
  },

  // Calculate tax savings
  calculateTaxSavings: function(params, runningCosts, taxRate) {
    // Tax savings on running costs
    const monthlyTaxSavings = runningCosts.totalMonthly * taxRate;

    return {
      monthlyTaxSavings,
      annualTaxSavings: monthlyTaxSavings * 12,
      taxRate
    };
  },

  // Calculate residual value
  calculateResidualValue: function(vehiclePrice, leaseTerm) {
    // Get residual percentage based on lease term
    const residualPercentage = this.constants.residualValuePercentages[leaseTerm] || 0.15625;

    return vehiclePrice * residualPercentage;
  },

  // Calculate lease payments
  calculateLeasePayments: function(params, residualValue) {
    // Finance amount (vehicle price less residual)
    const financeAmount = params.vehiclePrice - residualValue;

    // Monthly principal payment
    const monthlyPrincipal = financeAmount / params.leaseTerm;

    // Monthly interest calculations
    const monthlyInterestRate = this.constants.interestRate / 12;

    // Use loan formula: PMT = P * (r * (1 + r)^n) / ((1 + r)^n - 1)
    const numerator = financeAmount * monthlyInterestRate * Math.pow(1 + monthlyInterestRate, params.leaseTerm);
    const denominator = Math.pow(1 + monthlyInterestRate, params.leaseTerm) - 1;
    const monthlyPayment = numerator / denominator;

    // Calculate monthly interest (payment minus principal)
    const monthlyInterest = monthlyPayment - monthlyPrincipal;

    return {
      financeAmount,
      residualValue,
      monthlyPrincipal,
      monthlyInterest,
      monthlyPayment
    };
  },

  // Calculate FBT (Fringe Benefits Tax)
  calculateFBT: function(params) {
    // Use statutory formula method
    const annualFBT = params.vehiclePrice * this.constants.statutoryFormulaRate * this.constants.fbtRate;

    // Convert to monthly
    return annualFBT / 12;
  },

  // Calculate car loan payment
  calculateCarLoanPayment: function(params) {
    // Calculate monthly interest rate
    const monthlyInterestRate = this.constants.carLoanRate / 12;

    // Use loan formula: PMT = P * (r * (1 + r)^n) / ((1 + r)^n - 1)
    const numerator = params.vehiclePrice * monthlyInterestRate * Math.pow(1 + monthlyInterestRate, params.leaseTerm);
    const denominator = Math.pow(1 + monthlyInterestRate, params.leaseTerm) - 1;

    return numerator / denominator;
  },

  // Calculate cash purchase payment (opportunity cost)
  calculateCashPurchasePayment: function(params) {
    // Monthly investment return rate
    const monthlyInvestmentRate = this.constants.investmentReturnRate / 12;

    // Opportunity cost of using cash instead of investing
    return params.vehiclePrice * monthlyInvestmentRate;
  },

  // Display calculation results
  displayResults: function() {
    if (!this.results) return;

    const resultsContainer = document.getElementById('calculationResults');
    if (!resultsContainer) return;

    // Format currency values
    const formatCurrency = (value) => {
      return new Intl.NumberFormat('en-AU', {
        style: 'currency',
        currency: 'AUD',
        minimumFractionDigits: 2,
        maximumFractionDigits: 2
      }).format(value);
    };

    // Format percentage values
    const formatPercentage = (value) => {
      return new Intl.NumberFormat('en-AU', {
        style: 'percent',
        minimumFractionDigits: 1,
        maximumFractionDigits: 1
      }).format(value);
    };

    // Generate results HTML
    let html = `
      <div class="nov-result-primary">
        <h3>Estimated Monthly Payment</h3>
        <div class="nov-result-amount">${formatCurrency(this.results.monthlyLeasePayment)}</div>
        <p class="nov-result-caption">Including all running costs and tax benefits</p>
      </div>

      <div class="nov-result-comparison">
        <h4>Monthly Comparison</h4>
        <div class="nov-comparison-row">
          <div class="nov-comparison-label">Novated Lease</div>
          <div class="nov-comparison-value">${formatCurrency(this.results.monthlyLeasePayment)}</div>
        </div>
        <div class="nov-comparison-row">
          <div class="nov-comparison-label">Car Loan</div>
          <div class="nov-comparison-value">${formatCurrency(this.results.carLoanPayment)}</div>
        </div>
        <div class="nov-comparison-row">
          <div class="nov-comparison-label">Cash Purchase</div>
          <div class="nov-comparison-value">${formatCurrency(this.results.cashPurchasePayment)}</div>
        </div>
      </div>

      <div class="nov-result-comparison">
        <h4>Your Savings</h4>
        <div class="nov-comparison-row">
          <div class="nov-comparison-label">Monthly Savings</div>
          <div class="nov-comparison-value">${formatCurrency(this.results.monthlySavings)}</div>
        </div>
        <div class="nov-comparison-row">
          <div class="nov-comparison-label">Total Term Savings</div>
          <div class="nov-comparison-value">${formatCurrency(this.results.termSavings)}</div>
        </div>
      </div>

      <div class="nov-result-comparison">
        <h4>Tax Benefits</h4>
        <div class="nov-comparison-row">
          <div class="nov-comparison-label">Your Tax Rate</div>
          <div class="nov-comparison-value">${formatPercentage(this.results.taxRate)}</div>
        </div>
        <div class="nov-comparison-row">
          <div class="nov-comparison-label">Annual Tax Savings</div>
          <div class="nov-comparison-value">${formatCurrency(this.results.taxSavings.annualTaxSavings)}</div>
        </div>
        <div class="nov-comparison-row">
          <div class="nov-comparison-label">Annual GST Savings</div>
          <div class="nov-comparison-value">${formatCurrency(this.results.gstSavings.annualGstSavings)}</div>
        </div>
      </div>
    `;

    // Add breakdown section with toggle
    html += `
      <div class="nov-result-section">
        <button class="nov-toggle-breakdown" data-target="costBreakdown">
          <i class="fas fa-chevron-right"></i>
          <span>View Detailed Breakdown</span>
        </button>

        <div class="nov-detailed-breakdown" id="costBreakdown">
          <h4>Monthly Cost Breakdown</h4>

          <div class="nov-breakdown-section">
            <h5>Lease Components</h5>
            <div class="nov-breakdown-row">
              <div class="nov-breakdown-label">Principal</div>
              <div class="nov-breakdown-value">${formatCurrency(this.results.leasePayments.monthlyPrincipal)}</div>
            </div>
            <div class="nov-breakdown-row">
              <div class="nov-breakdown-label">Interest</div>
              <div class="nov-breakdown-value">${formatCurrency(this.results.leasePayments.monthlyInterest)}</div>
            </div>
            <div class="nov-breakdown-row">
              <div class="nov-breakdown-label">FBT</div>
              <div class="nov-breakdown-value">${formatCurrency(this.results.fbtAmount)}</div>
            </div>
          </div>

          <div class="nov-breakdown-section">
            <h5>Running Costs</h5>
            ${this.formData.includeFuel ? `
            <div class="nov-breakdown-row">
              <div class="nov-breakdown-label">Fuel</div>
              <div class="nov-breakdown-value">${formatCurrency(this.results.runningCosts.monthlyFuelCost)}</div>
            </div>
            ` : ''}
            ${this.formData.includeInsurance ? `
            <div class="nov-breakdown-row">
              <div class="nov-breakdown-label">Insurance</div>
              <div class="nov-breakdown-value">${formatCurrency(this.results.runningCosts.monthlyInsuranceCost)}</div>
            </div>
            ` : ''}
            ${this.formData.includeServicing ? `
            <div class="nov-breakdown-row">
              <div class="nov-breakdown-label">Servicing</div>
              <div class="nov-breakdown-value">${formatCurrency(this.results.runningCosts.monthlyServicingCost)}</div>
            </div>
            ` : ''}
            ${this.formData.includeRegistration ? `
            <div class="nov-breakdown-row">
              <div class="nov-breakdown-label">Registration</div>
              <div class="nov-breakdown-value">${formatCurrency(this.results.runningCosts.monthlyRegistrationCost)}</div>
            </div>
            ` : ''}
          </div>

          <div class="nov-breakdown-section">
            <h5>Savings</h5>
            <div class="nov-breakdown-row">
              <div class="nov-breakdown-label">GST Savings</div>
              <div class="nov-breakdown-value">-${formatCurrency(this.results.gstSavings.totalMonthlyGstSavings)}</div>
            </div>
            <div class="nov-breakdown-row">
              <div class="nov-breakdown-label">Tax Savings</div>
              <div class="nov-breakdown-value">-${formatCurrency(this.results.taxSavings.monthlyTaxSavings)}</div>
            </div>
          </div>

          <div class="nov-breakdown-section">
            <h5>End of Lease</h5>
            <div class="nov-breakdown-row">
              <div class="nov-breakdown-label">Residual Value</div>
              <div class="nov-breakdown-value">${formatCurrency(this.results.leasePayments.residualValue)}</div>
            </div>
            <div class="nov-breakdown-row">
              <div class="nov-breakdown-label">Residual Percentage</div>
              <div class="nov-breakdown-value">${formatPercentage(this.results.leasePayments.residualValue / this.formData.vehiclePrice)}</div>
            </div>
          </div>
        </div>
      </div>
    `;

    // Add email and print buttons
    html += `
      <div class="nov-calculator-actions">
        <button class="nov-secondary-btn" id="emailQuoteBtn">
          <i class="fas fa-envelope"></i> Email Quote
        </button>
        <button class="nov-secondary-btn" id="printQuoteBtn">
          <i class="fas fa-print"></i> Print Quote
        </button>
      </div>
    `;

    // Set the HTML content
    resultsContainer.innerHTML = html;
    resultsContainer.classList.add('active');

    // Set up toggle for detailed breakdown
    const toggleBtn = resultsContainer.querySelector('.nov-toggle-breakdown');
    if (toggleBtn) {
      toggleBtn.addEventListener('click', this.toggleBreakdown.bind(this));
    }

    // Set up email and print buttons
    const emailBtn = document.getElementById('emailQuoteBtn');
    if (emailBtn) {
      emailBtn.addEventListener('click', this.emailQuote.bind(this));
    }

    const printBtn = document.getElementById('printQuoteBtn');
    if (printBtn) {
      printBtn.addEventListener('click', this.printQuote.bind(this));
    }
  },

  // Toggle detailed breakdown visibility
  toggleBreakdown: function(event) {
    const button = event.currentTarget;
    const targetId = button.getAttribute('data-target');
    const breakdownElement = document.getElementById(targetId);

    if (breakdownElement) {
      const isExpanded = breakdownElement.classList.contains('expanded');

      if (isExpanded) {
        breakdownElement.classList.remove('expanded');
        button.querySelector('i').className = 'fas fa-chevron-right';
      } else {
        breakdownElement.classList.add('expanded');
        button.querySelector('i').className = 'fas fa-chevron-down';
      }
    }
  },

  // Email quote
  emailQuote: function() {
    // Get customer data
    if (!this.customerData || !this.customerData.email) {
      this.showError('Customer email is missing. Please add an email address.');
      return;
    }

    // In a real implementation, this would generate an email with the quote
    // For now, we'll just show a notification
    const notification = document.createElement('div');
    notification.className = 'nov-notification success';
    notification.innerHTML = `
      <div class="nov-notification-content">
        <span class="nov-notification-message">Quote would be emailed to ${this.customerData.email}</span>
        <button class="nov-notification-close">×</button>
      </div>
    `;

    // Add to document
    document.body.appendChild(notification);

    // Handle close button
    notification.querySelector('.nov-notification-close').addEventListener('click', () => {
      notification.remove();
    });

    // Auto-remove after 5 seconds
    setTimeout(() => {
      if (notification.parentElement) {
        notification.remove();
      }
    }, 5000);
  },

  // Print quote
  printQuote: function() {
    // In a real implementation, this would format and print the quote
    // For now, we'll just show a notification
    const notification = document.createElement('div');
    notification.className = 'nov-notification success';
    notification.innerHTML = `
      <div class="nov-notification-content">
        <span class="nov-notification-message">Quote would be printed or saved as PDF</span>
        <button class="nov-notification-close">×</button>
      </div>
    `;

    // Add to document
    document.body.appendChild(notification);

    // Handle close button
    notification.querySelector('.nov-notification-close').addEventListener('click', () => {
      notification.remove();
    });

    // Auto-remove after 5 seconds
    setTimeout(() => {
      if (notification.parentElement) {
        notification.remove();
      }
    }, 5000);
  },

  // Save calculation to storage
  saveCalculation: function() {
    if (!this.results || !this.customerData || !this.customerData.id) return;

    chrome.storage.local.get('calculations', (data) => {
      const calculations = data.calculations || {};
      calculations[this.customerData.id] = this.results;
      chrome.storage.local.set({ calculations });
    });
  }
};

