/**
 * NovAssistant - Excel Online Content Script
 * 
 * Handles integration with Excel Online, including:
 * - Customer data extraction from sheets
 * - Excel data analysis
 * - Sidebar integration
 */

// Import common utilities
importScripts(chrome.runtime.getURL('content/common-content.js'));

// Excel Online integration
const ExcelIntegration = {
  // Excel selectors for key UI elements
  selectors: {
    // Sheet & cell selectors
    sheetTabs: '.SheetTabBase, .SheetTab, [data-automationid="SheetTab"]',
    activeSheet: '.SheetTabBase--active, .SheetTab--active, [data-automationid="SheetTab"][aria-selected="true"]',
    cellData: '.CellData, [data-automationid="CellContent"]',

    // Column header selectors
    headerRow: '.headerRow, [role="row"]:first-child, [data-automationid="RowHeader"]',

    // Sheet container selectors
    sheetContentContainer: '#SheetContentContainer, [data-automationid="SheetContentContainer"]',
    sheetCanvas: '.canvasContainer, [data-automationid="canvasContainer"]'
  },

  // Current sheet info
  currentSheet: {
    name: null,
    headers: [],
    customerIndex: -1,
    data: []
  },

  // Column mapping patterns for customer data
  columnMappings: {
    id: ['id', 'customer id', 'client id', 'customer #', 'client #', 'reference'],
    name: ['name', 'customer name', 'client name', 'full name', 'customer', 'client'],
    email: ['email', 'e-mail', 'email address', 'customer email', 'client email'],
    phone: ['phone', 'telephone', 'mobile', 'phone number', 'contact number', 'customer phone'],
    stage: ['stage', 'status', 'customer stage', 'phase', 'journey stage'],
    vehicle: ['vehicle', 'car', 'model', 'make model', 'vehicle type', 'vehicle model'],
    employer: ['employer', 'company', 'organization', 'business', 'employment', 'workplace']
  },

  // Initialize Excel integration
  init: function() {
    // Set up MutationObserver to detect sheet changes
    this.setupSheetObserver();

    // Inject sidebar container if not exists
    this.injectSidebarContainer();

    // Set up message listener for background communication
    this.setupMessageListener();

    // Detect current sheet after a delay to ensure Excel is loaded
    setTimeout(() => this.detectCurrentSheet(), 1500);
  },

  // Set up observer to detect sheet changes
  setupSheetObserver: function() {
    const observer = new MutationObserver((mutations) => {
      // Check if mutations affect our target elements
      const sheetChanged = mutations.some(mutation => {
        // Check if sheet tabs changed
        if (mutation.target.matches(this.selectors.sheetTabs) || 
            mutation.target.closest(this.selectors.sheetTabs)) {
          return true;
        }

        // Check if active sheet changed
        if ((mutation.type === 'attributes' && 
             mutation.attributeName === 'aria-selected') &&
            (mutation.target.matches(this.selectors.sheetTabs) || 
             mutation.target.closest(this.selectors.sheetTabs))) {
          return true;
        }

        // Check if a significant part of the sheet content changed
        if (mutation.target.matches(this.selectors.sheetContentContainer) || 
            mutation.target.closest(this.selectors.sheetContentContainer)) {
          return true;
        }

        return false;
      });

      if (sheetChanged) {
        setTimeout(() => this.detectCurrentSheet(), 500);
      }
    });

    observer.observe(document.body, { 
      childList: true, 
      subtree: true,
      attributes: true,
      attributeFilter: ['aria-selected', 'data-sheet-name']
    });
  },

  // Detect current sheet and analyze it
  detectCurrentSheet: function() {
    // Reset current sheet info
    this.currentSheet = {
      name: null,
      headers: [],
      customerIndex: -1,
      data: []
    };

    // Try to find active sheet tab
    const activeSheetTab = document.querySelector(this.selectors.activeSheet);
    if (!activeSheetTab) return;

    // Get sheet name
    this.currentSheet.name = activeSheetTab.textContent || 
                            activeSheetTab.getAttribute('data-sheet-name') || 
                            'Unknown Sheet';

    // Only proceed with analysis if sheet name contains customer-related terms
    const customerSheetPattern = /customer|client|lead|prospect|contact|sale|lease/i;
    if (!customerSheetPattern.test(this.currentSheet.name)) {
      return;
    }

    // Analyze current sheet
    this.analyzeCurrentSheet();
  },

  // Analyze the current sheet to find customer data
  analyzeCurrentSheet: function() {
    // Try to identify header row
    this.identifyHeaderRow();

    // If we found headers, try to map them
    if (this.currentSheet.headers.length > 0) {
      this.mapColumns();

      // If we found column mappings, extract data
      if (this.isCustomerDataSheet()) {
        this.extractSheetData();
      }
    }
  },

  // Identify the header row in the current sheet
  identifyHeaderRow: function() {
    // Try different strategies to find the header row
    // Strategy 1: Look for first row with multiple cells
    const rows = Array.from(document.querySelectorAll('[role="row"], .RowGroup, [data-automationid="RowContainer"] > div'));

    if (rows.length > 0) {
      const firstRow = rows[0];
      const cells = firstRow.querySelectorAll('[role="cell"], .Cell, [data-automationid="CellContainer"]');

      if (cells.length > 1) {
        // Extract header text from cells
        this.currentSheet.headers = Array.from(cells).map(cell => {
          return cell.textContent.trim().toLowerCase();
        }).filter(header => header.length > 0);
      }
    }

    // If still no headers, try strategy 2: Look for bold cells in first few rows
    if (this.currentSheet.headers.length === 0) {
      for (let i = 0; i < Math.min(3, rows.length); i++) {
        const row = rows[i];
        const boldCells = Array.from(row.querySelectorAll('[role="cell"], .Cell, [data-automationid="CellContainer"]'))
          .filter(cell => {
            // Check if cell has bold styling
            const style = window.getComputedStyle(cell);
            return style.fontWeight === 'bold' || style.fontWeight === '700';
          });

        if (boldCells.length > 1) {
          // Extract header text from bold cells
          this.currentSheet.headers = boldCells.map(cell => {
            return cell.textContent.trim().toLowerCase();
          }).filter(header => header.length > 0);
          break;
        }
      }
    }
  },

  // Map columns to customer data fields
  mapColumns: function() {
    const mappings = {};

    // For each customer data field, check if any header matches
    for (const [field, patterns] of Object.entries(this.columnMappings)) {
      const index = this.findColumnIndex(patterns);
      if (index !== -1) {
        mappings[field] = index;
      }
    }

    // Store mappings in currentSheet
    this.currentSheet.mappings = mappings;
  },

  // Check if current sheet has enough mappings to be a customer data sheet
  isCustomerDataSheet: function() {
    // Consider it a customer data sheet if we can map at least 3 fields
    // and at least one of them is name, id, or email
    const mappingsCount = Object.keys(this.currentSheet.mappings || {}).length;
    const hasKeyField = this.currentSheet.mappings?.name !== undefined || 
                       this.currentSheet.mappings?.id !== undefined || 
                       this.currentSheet.mappings?.email !== undefined;

    return mappingsCount >= 3 && hasKeyField;
  },

  // Find index of column that matches any of the given patterns
  findColumnIndex: function(patterns) {
    for (let i = 0; i < this.currentSheet.headers.length; i++) {
      const header = this.currentSheet.headers[i];

      // Check if header matches any pattern
      if (patterns.some(pattern => {
        // Exact match
        if (header === pattern) return true;

        // Contains match
        if (header.includes(pattern)) return true;

        return false;
      })) {
        return i;
      }
    }

    return -1;
  },

  // Extract customer data from the sheet
  extractSheetData: function() {
    // Get all rows
    const rows = Array.from(document.querySelectorAll('[role="row"], .RowGroup, [data-automationid="RowContainer"] > div'));

    // Skip header row(s)
    const dataRows = rows.slice(1);

    // Extract data from each row
    const customersData = [];

    for (const row of dataRows) {
      const cells = row.querySelectorAll('[role="cell"], .Cell, [data-automationid="CellContainer"]');

      // Skip rows with insufficient cells
      if (cells.length <= Math.max(...Object.values(this.currentSheet.mappings))) {
        continue;
      }

      // Extract customer data using mappings
      const customerData = {
        id: null,
        name: null,
        email: null,
        phone: null,
        stage: null,
        vehicle: null,
        employer: null,
        detectionSource: 'excel_sheet',
        sheetName: this.currentSheet.name
      };

      // For each mapped field, extract data from corresponding cell
      for (const [field, index] of Object.entries(this.currentSheet.mappings)) {
        const cell = cells[index];
        if (cell) {
          customerData[field] = cell.textContent.trim();
        }
      }

      // Only add rows with valid data (name or id plus at least one other field)
      if ((customerData.name || customerData.id) && 
          (customerData.email || customerData.phone || customerData.vehicle || customerData.employer)) {
        customersData.push(customerData);
      }
    }

    // Store extracted data
    this.currentSheet.data = customersData;

    // Check if we found any customer data
    if (customersData.length > 0) {
      // Log detection
      NovAssistant.utils.log(`Found ${customersData.length} customers in Excel sheet "${this.currentSheet.name}"`);

      // Send first customer to background script if it has sufficient data
      const firstCustomer = customersData[0];
      if (firstCustomer && (firstCustomer.name || firstCustomer.id) && firstCustomer.email) {
        chrome.runtime.sendMessage({
          type: 'CUSTOMER_DETECTED',
          data: { customer: firstCustomer }
        });

        this.currentSheet.customerIndex = 0;
      }
    }
  },

  // Select a specific customer from the sheet
  selectCustomer: function(index) {
    if (index < 0 || index >= this.currentSheet.data.length) {
      return false;
    }

    const customerData = this.currentSheet.data[index];
    if (!customerData) return false;

    // Send selected customer to background script
    chrome.runtime.sendMessage({
      type: 'CUSTOMER_DETECTED',
      data: { customer: customerData }
    });

    this.currentSheet.customerIndex = index;
    return true;
  },

  // Inject sidebar container into Excel interface
  injectSidebarContainer: function() {
    if (document.getElementById('nov-assistant-container')) return;

    // Create container for the iframe
    const container = document.createElement('div');
    container.id = 'nov-assistant-container';
    container.style.cssText = `
      position: fixed;
      top: 0;
      right: 0;
      width: 0;
      height: 100vh;
      z-index: 9999;
      transition: width 0.3s ease;
      overflow: hidden;
    `;

    // Create iframe for the sidebar
    const iframe = document.createElement('iframe');
    iframe.id = 'nov-assistant-sidebar';
    iframe.src = chrome.runtime.getURL('sidebar.html');
    iframe.style.cssText = `
      border: none;
      width: 100%;
      height: 100%;
      background-color: white;
      box-shadow: -2px 0 10px rgba(0, 0, 0, 0.1);
    `;

    // Add iframe to container
    container.appendChild(iframe);

    // Add container to page
    document.body.appendChild(container);
  },

  // Set up listener for messages from background script
  setupMessageListener: function() {
    chrome.runtime.onMessage.addListener((message, sender, sendResponse) => {
      if (message.type === 'TOGGLE_SIDEBAR') {
        this.toggleSidebar();
        sendResponse({ success: true });
      }
      else if (message.type === 'GET_EXCEL_DATA') {
        // Return current sheet data
        sendResponse({
          sheetName: this.currentSheet.name,
          headers: this.currentSheet.headers,
          mappings: this.currentSheet.mappings,
          data: this.currentSheet.data,
          customerIndex: this.currentSheet.customerIndex
        });
      }
      else if (message.type === 'SELECT_EXCEL_CUSTOMER') {
        // Select a specific customer
        const success = this.selectCustomer(message.data.index);
        sendResponse({ success });
      }
    });

    // Also listen for messages from sidebar iframe
    window.addEventListener('message', (event) => {
      // Make sure message is from our sidebar
      if (event.source !== document.getElementById('nov-assistant-sidebar').contentWindow) {
        return;
      }

      // Handle sidebar messages
      if (event.data.type === 'SIDEBAR_TOGGLED') {
        this.toggleSidebar(event.data.visible);
      }
      else if (event.data.type === 'GET_EXCEL_DATA') {
        // Send current sheet data to sidebar
        event.source.postMessage({
          type: 'EXCEL_DATA_RESPONSE',
          data: {
            sheetName: this.currentSheet.name,
            headers: this.currentSheet.headers,
            mappings: this.currentSheet.mappings,
            data: this.currentSheet.data,
            customerIndex: this.currentSheet.customerIndex
          }
        }, '*');
      }
      else if (event.data.type === 'SELECT_EXCEL_CUSTOMER') {
        // Select a specific customer
        this.selectCustomer(event.data.index);
      }
    });
  },

  // Toggle sidebar visibility
  toggleSidebar: function(visible) {
    const container = document.getElementById('nov-assistant-container');
    if (!container) return;

    // If visibility is explicitly specified, set it; otherwise toggle
    if (visible === undefined) {
      // Toggle current state
      visible = container.offsetWidth === 0;
    }

    if (visible) {
      container.style.width = '350px';

      // Adjust Excel UI if needed (to prevent overlap with key UI elements)
      this.adjustExcelUI(true);
    } else {
      container.style.width = '0';

      // Restore Excel UI
      this.adjustExcelUI(false);
    }
  },

  // Adjust Excel UI to accommodate sidebar
  adjustExcelUI: function(sidebarVisible) {
    // Find main content area
    const mainContent = document.querySelector('.mainContent, [data-automationid="mainContent"], #WebApplicationFrame');
    if (!mainContent) return;

    if (sidebarVisible) {
      // Apply margin to make room for sidebar
      mainContent.style.width = 'calc(100% - 350px)';
      mainContent.style.transition = 'width 0.3s ease';
    } else {
      // Restore original width
      mainContent.style.width = '100%';
    }
  }
};

