/**
 * NovAssistant Dynamics 365 Integration
 * Specialized content script for Microsoft Dynamics 365 CRM
 */

// Import common utilities
importScripts(chrome.runtime.getURL('content/common-content.js'));

// Dynamics-specific detection and integration
const DynamicsIntegration = {
  // Dynamics-specific selectors
  selectors: {
    customerForm: '[data-id="entity_control"], .entity-form, [data-id="form-container"]',
    customerName: '[data-id="fullname_compositionLinkControl-fullname_textInputBox_text"], [data-id="fullname.fieldControl"]',
    customerEmail: '[data-id="emailaddress1.fieldControl-mail-text-input"], [data-id="emailaddress1.fieldControl"]',
    customerPhone: '[data-id="telephone1.fieldControl-phone-text-input"], [data-id="telephone1.fieldControl"]',
    customerStatus: '[data-id="statuscode.fieldControl-option-set-select"]',
    sidebarContainer: 'body'
  },

  /**
   * Initialize Dynamics integration
   */
  init: function() {
    // Determine Dynamics view
    const currentView = this.detectDynamicsView();
    NovAssistant.utils.log(`Detected Dynamics view: ${currentView}`);

    // Register message handlers
    this.setupMessageHandlers();

    // Setup mutation observer for form changes
    this.setupFormObserver();

    // Inject sidebar container if not present
    this.ensureSidebarContainer();

    // Extract customer info if in customer form view
    if (currentView === 'customer') {
      const customerInfo = this.extractCustomerFromForm();
      if (customerInfo) {
        chrome.runtime.sendMessage({
          type: 'CUSTOMER_DETECTED',
          data: { customer: customerInfo }
        });
      }
    }
  },

  /**
   * Detect current Dynamics view
   * @returns {string} View type (customer, list, dashboard, etc.)
   */
  detectDynamicsView: function() {
    const url = window.location.href;

    // Check URL patterns
    if (url.includes('userdashboard')) return 'dashboard';
    if (url.includes('&pagetype=entitylist')) return 'list';
    if (url.includes('&pagetype=entityrecord')) return 'customer';

    // Check DOM elements as fallback
    if (document.querySelector(this.selectors.customerForm)) return 'customer';

    return 'unknown';
  },

  /**
   * Set up message handlers for this context
   */
  setupMessageHandlers: function() {
    chrome.runtime.onMessage.addListener((message, sender, sendResponse) => {
      switch (message.type) {
        case 'TOGGLE_SIDEBAR':
          this.toggleSidebar();
          sendResponse({success: true});
          break;

        case 'APP_CONTEXT_CHANGED':
          // Update UI based on context change
          if (message.data && message.data.app) {
            this.updateForAppContext(message.data.app);
          }
          sendResponse({success: true});
          break;
      }
    });
  },

  /**
   * Setup observer to detect when customer forms are loaded or changed
   */
  setupFormObserver: function() {
    const observer = new MutationObserver((mutations) => {
      // Debounce frequent mutations
      clearTimeout(this.formObserverDebounce);
      this.formObserverDebounce = setTimeout(() => {
        // Check if we're in a customer form view
        if (this.detectDynamicsView() === 'customer') {
          const customerInfo = this.extractCustomerFromForm();
          if (customerInfo) {
            chrome.runtime.sendMessage({
              type: 'CUSTOMER_DETECTED',
              data: { customer: customerInfo }
            });
          }
        }
      }, 500);
    });

    // Observe document body for changes
    observer.observe(document.body, {
      childList: true,
      subtree: true,
      attributes: true,
      attributeFilter: ['data-id', 'value']
    });
  },

  /**
   * Extract customer information from Dynamics form
   * Focuses on the most common contact/account fields
   */
  extractCustomerFromForm: function() {
    // Check if we're in a customer form
    const formElement = document.querySelector(this.selectors.customerForm);
    if (!formElement) return null;

    // Extract customer ID from URL
    const url = new URL(window.location.href);
    const customerId = url.searchParams.get('id');

    // Extract common customer fields
    const nameElement = document.querySelector(this.selectors.customerName);
    const emailElement = document.querySelector(this.selectors.customerEmail);
    const phoneElement = document.querySelector(this.selectors.customerPhone);
    const statusElement = document.querySelector(this.selectors.customerStatus);

    // Extract field values
    const name = nameElement ? (nameElement.value || nameElement.textContent).trim() : null;
    const email = emailElement ? (emailElement.value || emailElement.textContent).trim() : null;
    const phone = phoneElement ? (phoneElement.value || phoneElement.textContent).trim() : null;
    const status = statusElement ? (statusElement.value || statusElement.textContent).trim() : null;

    // Extract additional fields - look for vehicle information
    const vehicleFieldPatterns = ['vehic', 'car', 'automobile', 'make', 'model'];
    let vehicle = null;

    // Try to find vehicle fields in the form
    for (const pattern of vehicleFieldPatterns) {
      const vehicleElements = document.querySelectorAll(`[data-id*="${pattern}"]`);
      for (const element of vehicleElements) {
        const value = element.value || element.textContent;
        if (value && value.trim()) {
          vehicle = value.trim();
          break;
        }
      }
      if (vehicle) break;
    }

    // Try to find stage information
    const stageFieldPatterns = ['stage', 'status', 'step', 'phase', 'progress'];
    let stage = status; // Default to status if available

    // Look for specific stage fields
    for (const pattern of stageFieldPatterns) {
      const stageElements = document.querySelectorAll(`[data-id*="${pattern}"]`);
      for (const element of stageElements) {
        const value = element.value || element.textContent;
        if (value && value.trim()) {
          stage = value.trim();
          break;
        }
      }
      if (stage && stage !== status) break;
    }

    // Only create customer object if we have minimum required information
    if (customerId && (name || email || phone)) {
      return {
        id: customerId,
        name: name,
        email: email,
        phone: phone,
        vehicle: vehicle,
        stage: stage,
        source: 'dynamics-crm',
        timestamp: new Date().toISOString()
      };
    }

    return null;
  },

  /**
   * Ensure the sidebar container exists
   */
  ensureSidebarContainer: function() {
    if (!document.getElementById('novassistant-container')) {
      const container = document.createElement('div');
      container.id = 'novassistant-container';
      document.body.appendChild(container);
    }
  },

  /**
   * Toggle sidebar visibility
   */
  toggleSidebar: function() {
    const container = document.getElementById('novassistant-container');
    if (!container) return;

    // Check if sidebar iframe exists
    let sidebarFrame = document.getElementById('novassistant-sidebar');

    if (sidebarFrame) {
      // Toggle visibility
      if (sidebarFrame.style.display === 'none') {
        sidebarFrame.style.display = 'block';
      } else {
        sidebarFrame.style.display = 'none';
      }
    } else {
      // Create sidebar iframe
      sidebarFrame = document.createElement('iframe');
      sidebarFrame.id = 'novassistant-sidebar';
      sidebarFrame.src = chrome.runtime.getURL('sidebar.html');
      sidebarFrame.style.position = 'fixed';
      sidebarFrame.style.top = '0';
      sidebarFrame.style.right = '0';
      sidebarFrame.style.width = '350px';
      sidebarFrame.style.height = '100vh';
      sidebarFrame.style.border = 'none';
      sidebarFrame.style.zIndex = '9999';

      container.appendChild(sidebarFrame);
    }
  },

  /**
   * Update UI based on application context
   */
  updateForAppContext: function(appContext) {
    // No-op for now, will implement app-specific behaviors
  }
};

