/**
 * NovAssistant Common Content Script
 * 
 * This script is injected into all supported web applications.
 * It provides shared functionality for all content scripts.
 */

// Create a namespace for the extension
window.NovAssistant = window.NovAssistant || {};

// Common utilities and components
NovAssistant.utils = {
  // Log messages with extension prefix
  log: function(message) {
    console.log(`[NovAssistant] ${message}`);
  },

  // Handle errors with extension prefix
  error: function(message, err) {
    console.error(`[NovAssistant] Error: ${message}`, err);
  },

  // Display a notification to the user
  notify: function(message, type = 'info') {
    const notification = document.createElement('div');
    notification.className = `nov-notification ${type}`;
    notification.innerHTML = `
      <div class="nov-notification-content">
        <span class="nov-notification-message">${message}</span>
        <button class="nov-notification-close">×</button>
      </div>
    `;

    // Add to document
    document.body.appendChild(notification);

    // Handle close button
    notification.querySelector('.nov-notification-close').addEventListener('click', () => {
      notification.remove();
    });

    // Auto-remove after 5 seconds
    setTimeout(() => {
      if (notification.parentElement) {
        notification.remove();
      }
    }, 5000);
  },

  // Format currency values
  formatCurrency: function(value) {
    return new Intl.NumberFormat('en-AU', {
      style: 'currency',
      currency: 'AUD',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }).format(value);
  },

  // Process templates with customer data
  processTemplate: function(template, customerData) {
    if (!template) return '';
    if (!customerData) return template;

    return template.replace(/\{\{([^}]+)\}\}/g, (match, key) => {
      const keys = key.trim().split('.');
      let value = customerData;

      for (const k of keys) {
        if (!value || value[k] === undefined) return match;
        value = value[k];
      }

      return value || match;
    });
  }
};

// Context detection system
NovAssistant.contextDetector = {
  // Define patterns for supported applications
  appPatterns: {
    outlook: /outlook.office.com/i,
    dynamics: /dynamics.com/i,
    excel: /excel.office.com/i,
    teams: /teams.microsoft.com/i,
    powerApps: /apps.powerapps.com/i
  },

  // Detect current application
  detectApp: function() {
    const url = window.location.href;

    for (const [app, pattern] of Object.entries(this.appPatterns)) {
      if (pattern.test(url)) {
        return app;
      }
    }

    return null;
  },

  // Detect the specific context within an application
  detectContext: function() {
    const app = this.detectApp();

    if (!app) return null;

    let context = {
      app,
      view: null,
      customerId: null
    };

    // Application-specific context detection
    switch (app) {
      case 'outlook':
        // Check if we're in an email
        if (document.querySelector('.ReadingPaneContainer, .itemView')) {
          context.view = 'email';
          // Try to extract customer info from email
          const customerInfo = this.extractCustomerInfoFromEmail();
          if (customerInfo && customerInfo.id) {
            context.customerId = customerInfo.id;
          }
        }
        break;

      case 'dynamics':
        // Check if we're in a contact/customer view
        if (document.querySelector('.contactCard, .customerForm, [data-id="contactPanel"]')) {
          context.view = 'customer';
          // Extract customer ID from URL or DOM
          const urlParams = new URLSearchParams(window.location.search);
          const id = urlParams.get('id');
          if (id) {
            context.customerId = id;
          }
        }
        break;

      case 'excel':
        // Check if we're in a spreadsheet with customer data
        const sheetName = this.getExcelSheetName();
        if (sheetName && /customer|client/i.test(sheetName)) {
          context.view = 'customerData';
        }
        break;
    }

    return context;
  },

  // Get the current Excel sheet name
  getExcelSheetName: function() {
    const sheetTab = document.querySelector('.sheetTab.selected');
    return sheetTab ? sheetTab.textContent : null;
  },

  // Extract customer info from an email
  extractCustomerInfoFromEmail: function() {
    const emailBody = document.querySelector('.message-body, .itemBody');
    if (!emailBody) return null;

    const emailText = emailBody.textContent;

    // Look for patterns like "Customer: John Smith" or "Customer ID: 12345"
    const customerNameMatch = emailText.match(/Customer:\s*([^,\n]+)/i);
    const customerIdMatch = emailText.match(/Customer ID:\s*([^,\n]+)/i);

    if (customerNameMatch || customerIdMatch) {
      return {
        id: customerIdMatch ? customerIdMatch[1].trim() : null,
        name: customerNameMatch ? customerNameMatch[1].trim() : null
      };
    }

    return null;
  },

  // Look for customer data on the page
  extractCustomerData: function() {
    const app = this.detectApp();

    if (!app) return null;

    let customerData = {
      id: null,
      name: null,
      email: null,
      phone: null,
      stage: null,
      vehicle: null
    };

    switch (app) {
      case 'dynamics':
        // Extract from Dynamics 365
        const nameElement = document.querySelector('[data-id="fullname_compositionLinkControl-fullname_textInputBox_text"]');
        if (nameElement) {
          customerData.name = nameElement.value || nameElement.textContent;
        }

        const emailElement = document.querySelector('[data-id="emailaddress1.fieldControl-mail-text-input"]');
        if (emailElement) {
          customerData.email = emailElement.value || emailElement.textContent;
        }

        const phoneElement = document.querySelector('[data-id="telephone1.fieldControl-phone-text-input"]');
        if (phoneElement) {
          customerData.phone = phoneElement.value || phoneElement.textContent;
        }

        // Extract ID from URL
        const urlParams = new URLSearchParams(window.location.search);
        customerData.id = urlParams.get('id') || null;
        break;

      case 'outlook':
        // Extract from email contents
        const emailInfo = this.extractCustomerInfoFromEmail();
        if (emailInfo) {
          customerData = { ...customerData, ...emailInfo };
        }
        break;
    }

    return customerData;
  },

  // Initialize context detection and monitoring
  init: function() {
    // Detect initial context
    const context = this.detectContext();

    // Notify background script of detected app
    if (context && context.app) {
      chrome.runtime.sendMessage({
        type: 'APP_DETECTED',
        data: { app: context.app }
      });
    }

    // Detect customer data if available
    const customerData = this.extractCustomerData();

    // Notify background script of detected customer
    if (customerData && customerData.id) {
      chrome.runtime.sendMessage({
        type: 'CUSTOMER_DETECTED',
        data: { customer: customerData }
      });
    }

    // Set up mutation observer to watch for changes
    this.setupMutationObserver();
  },

  // Set up mutation observer to detect DOM changes
  setupMutationObserver: function() {
    const observer = new MutationObserver(this.handleDomChanges.bind(this));

    observer.observe(document.body, {
      childList: true,
      subtree: true,
      attributes: true,
      attributeFilter: ['data-id', 'class']
    });
  },

  // Handle DOM changes
  handleDomChanges: function(mutations) {
    // Check if URL has changed (SPA navigation)
    const context = this.detectContext();

    // Check if customer data might have changed
    const customerData = this.extractCustomerData();

    // Notify if customer data has changed
    if (customerData && customerData.id) {
      chrome.runtime.sendMessage({
        type: 'CUSTOMER_DETECTED',
        data: { customer: customerData }
      });
    }
  }
};

