// Refresh the document checklist when a customer profile is opened
  refreshDocumentChecklist: function(customer, saleType) {
    try {
      if (!customer) return false;

      // Update sale type selector if provided
      const saleTypeSelector = document.getElementById('saleTypeSelector');
      if (saleTypeSelector && saleType) {
        saleTypeSelector.value = saleType;
      } else if (saleTypeSelector && customer.saleType) {
        saleTypeSelector.value = customer.saleType;
      }

      // Update document checklist based on current sale type
      if (saleTypeSelector) {
        this.updateDocumentChecklist({ target: saleTypeSelector });
      }

      // Load customer's uploaded documents
      this.loadCustomerDocuments();

      return true;
    } catch (error) {
      AppLogger.error("Error refreshing document checklist", error);
      return false;
    }
  },

  // Generate a document requirements email for the current customer
  generateRequirementsEmail: function() {
    try {
      // Only proceed if we have a current customer
      if (window.NovAssistant.state.currentCustomerIndex === null) {
        window.NovAssistant.ui.showToast("No customer selected", "warning");
        return false;
      }

      // Get current customer
      const customers = window.NovAssistant.CustomerManagement.getCustomers();
      const customer = customers[window.NovAssistant.state.currentCustomerIndex];

      if (!customer) return false;

      // Get current sale type
      const saleTypeSelector = document.getElementById('saleTypeSelector');
      if (!saleTypeSelector) return false;

      const saleType = saleTypeSelector.value;
      const checklist = this.documentChecklists[saleType];

      if (!checklist) {
        window.NovAssistant.ui.showToast("No checklist available for this sale type", "warning");
        return false;
      }

      // Load email template based on sale type
      let templateKey = "";

      switch (saleType) {
        case "dealer":
          templateKey = "dealerSaleDocuments";
          break;
        case "private":
          templateKey = "privateSaleDocuments";
          break;
        case "company":
          templateKey = "companySaleDocuments";
          break;
        case "saleAndLeaseback":
          templateKey = "saleAndLeasebackDocuments";
          break;
        case "refinance":
          templateKey = "refinanceDocuments";
          break;
        default:
          templateKey = "dealerSaleDocuments";
      }

      // Get the template
      const emailTemplates = window.NovAssistant.EmailTemplates;
      const template = emailTemplates.getTemplate(templateKey);

      if (!template) {
        window.NovAssistant.ui.showToast("Email template not found", "error");
        return false;
      }

      // Populate template fields
      const emailSubject = document.getElementById('emailSubject');
      const templateInput = document.getElementById('templateInput');

      if (emailSubject) emailSubject.value = template.subject.replace("[Vehicle]", customer.interestedVehicle || "Vehicle");

      if (templateInput) {
        let content = template.content;

        // Replace placeholders with customer data
        content = content.replace(/\[Customer Name\]/g, customer.name || "Valued Customer");
        content = content.replace(/\[Customer First Name\]/g, customer.name ? customer.name.split(' ')[0] : "Valued Customer");
        content = content.replace(/\[Vehicle\]/g, customer.interestedVehicle || "Vehicle");
        content = content.replace(/\[Vehicle Model\]/g, customer.interestedVehicle || "Vehicle Model");

        // Add list of missing required documents
        let missingDocs = this.getMissingRequiredDocuments(customer, saleType);
        if (missingDocs.length > 0) {
          content += "\n\nThe following required documents are still needed:\n";
          missingDocs.forEach(doc => {
            content += `• ${doc}\n`;
          });
        }

        templateInput.value = content;
      }

      // Switch to the email tab
      const emailTab = document.querySelector('.main-tab-btn[data-target="emailTab"]');
      if (emailTab) {
        emailTab.click();
      }

      window.NovAssistant.ui.showToast("Document requirements email generated", "success");

      return true;
    } catch (error) {
      AppLogger.error("Error generating requirements email", error);
      return false;
    }
  },

  // Get list of missing required documents
  getMissingRequiredDocuments: function(customer, saleType) {
    try {
      const checklist = this.documentChecklists[saleType];
      const missingDocs = [];

      if (!checklist || !customer) return missingDocs;

      // Check if customer has documents data
      if (!customer.documents || !customer.documents[saleType]) {
        // Return all required documents if none are checked
        for (const [section, items] of Object.entries(checklist)) {
          items.forEach(item => {
            if (item.required) {
              missingDocs.push(item.name);
            }
          });
        }

        return missingDocs;
      }

      // Check which required documents are not checked
      const documentCheckboxes = customer.documents[saleType];

      for (const [section, items] of Object.entries(checklist)) {
        items.forEach(item => {
          if (item.required && (!documentCheckboxes[item.id] || documentCheckboxes[item.id] === false)) {
            missingDocs.push(item.name);
          }
        });
      }

      return missingDocs;
    } catch (error) {
      AppLogger.error("Error getting missing required documents", error);
      return [];
    }
  },

  // Get overall document completion percentage
  getDocumentCompletionPercentage: function(customer, saleType) {
    try {
      if (!customer || !saleType) return 0;

      const checklist = this.documentChecklists[saleType];
      if (!checklist) return 0;

      let totalDocuments = 0;
      let completedDocuments = 0;

      // Count total documents
      for (const [section, items] of Object.entries(checklist)) {
        totalDocuments += items.length;
      }

      // Count completed documents
      if (customer.documents && customer.documents[saleType]) {
        const documentCheckboxes = customer.documents[saleType];

        for (const [id, checked] of Object.entries(documentCheckboxes)) {
          if (checked) {
            completedDocuments++;
          }
        }
      }

      if (totalDocuments === 0) return 0;

      // Calculate percentage
      return Math.round((completedDocuments / totalDocuments) * 100);
    } catch (error) {
      AppLogger.error("Error calculating document completion percentage", error);
      return 0;
    }
  },

  // Check if all required documents are completed
  areAllRequiredDocumentsCompleted: function(customer, saleType) {
    try {
      const missingDocs = this.getMissingRequiredDocuments(customer, saleType);
      return missingDocs.length === 0;
    } catch (error) {
      AppLogger.error("Error checking if all required documents are completed", error);
      return false;
    }
  },

