      AppLogger.error("Error setting up template buttons", error);
      return false;
    }
  },

  // Export document checklist for external use
  exportDocumentChecklist: function(customer, saleType) {
    try {
      if (!customer || !saleType) {
        window.NovAssistant.ui.showToast("Customer or sale type not specified", "error");
        return false;
      }

      const checklist = this.documentChecklists[saleType];
      if (!checklist) {
        window.NovAssistant.ui.showToast("Checklist not found for this sale type", "error");
        return false;
      }

      // Format sale type for display
      let saleTypeDisplay = saleType.charAt(0).toUpperCase() + saleType.slice(1);
      if (saleType === "saleAndLeaseback") {
        saleTypeDisplay = "Sale & Leaseback";
      }

      // Build checklist content
      let content = `# SG Fleet Document Checklist - ${saleTypeDisplay} Sale\n`;
      content += `## Customer: ${customer.name}\n`;
      content += `## Vehicle: ${customer.interestedVehicle || "Not specified"}\n\n`;

      // Add checklist items by section
      for (const [section, items] of Object.entries(checklist)) {
        content += `### ${this.formatSectionName(section)}\n\n`;

        items.forEach(item => {
          // Check if item is completed
          let isCompleted = false;
          if (customer.documents && 
              customer.documents[saleType] && 
              customer.documents[saleType][item.id]) {
            isCompleted = true;
          }

          // Add required tag if needed
          let requiredTag = item.required ? " (Required)" : "";

          // Add completed status
          let status = isCompleted ? "✓" : "□";

          content += `- [${status}] ${item.name}${requiredTag}\n`;
        });

        content += "\n";
      }

      // Add completion summary
      const completionPercentage = this.getDocumentCompletionPercentage(customer, saleType);
      content += `## Completion Progress: ${completionPercentage}%\n\n`;

      // Add missing required documents
      const missingDocs = this.getMissingRequiredDocuments(customer, saleType);
      if (missingDocs.length > 0) {
        content += "## Missing Required Documents:\n\n";
        missingDocs.forEach(doc => {
          content += `- ${doc}\n`;
        });
      } else {
        content += "## All required documents have been provided.\n";
      }

      // In a real implementation, this would trigger a download
      // For now, we'll just simulate a copy to clipboard
      navigator.clipboard.writeText(content)
        .then(() => {
          window.NovAssistant.ui.showToast("Checklist copied to clipboard", "success");
        })
        .catch((error) => {
          throw error;
        });

      return true;
    } catch (error) {
      AppLogger.error("Error exporting document checklist", error);
      return false;
    }
  },

  // Create a document requirements report for the client
  createRequirementsReport: function(customer, saleType) {
    try {
      if (!customer || !saleType) {
        window.NovAssistant.ui.showToast("Customer or sale type not specified", "error");
        return false;
      }

      const checklist = this.documentChecklists[saleType];
      if (!checklist) {
        window.NovAssistant.ui.showToast("Checklist not found for this sale type", "error");
        return false;
      }

      // Build HTML report
      let reportHTML = `
        <div class="requirements-report">
          <div class="report-header">
            <h2>Document Requirements Report</h2>
            <p class="customer-name">${customer.name}</p>
            <p class="vehicle-name">${customer.interestedVehicle || "Vehicle not specified"}</p>
            <p class="sale-type">${this.formatSaleType(saleType)} Sale</p>
          </div>
          <div class="completion-bar">
            <div class="progress-title">Overall Completion:</div>
            <div class="progress-container">
              <div class="progress-bar" style="width: ${this.getDocumentCompletionPercentage(customer, saleType)}%"></div>
            </div>
            <div class="progress-percentage">${this.getDocumentCompletionPercentage(customer, saleType)}%</div>
          </div>
          <div class="sections-container">
      `;

      // Add each section
      for (const [section, items] of Object.entries(checklist)) {
        reportHTML += `
          <div class="document-section">
            <h3>${this.formatSectionName(section)}</h3>
            <ul class="document-list">
        `;

        items.forEach(item => {
          // Check if item is completed
          let isCompleted = false;
          if (customer.documents && 
              customer.documents[saleType] && 
              customer.documents[saleType][item.id]) {
            isCompleted = true;
          }

          // Add document item
          reportHTML += `
            <li class="document-item ${isCompleted ? 'completed' : 'pending'}">
              <div class="doc-status-icon">
                <i class="fas ${isCompleted ? 'fa-check-circle' : 'fa-clock'}"></i>
              </div>
              <div class="doc-info">
                <span class="doc-name">${item.name}</span>
                ${item.required ? '<span class="required-label">Required</span>' : ''}
              </div>
              <div class="doc-status">
                ${isCompleted ? 'Provided' : 'Pending'}
              </div>
            </li>
          `;
        });

        reportHTML += `
            </ul>
          </div>
        `;
      }

      // Add missing documents section if needed
      const missingDocs = this.getMissingRequiredDocuments(customer, saleType);
      if (missingDocs.length > 0) {
        reportHTML += `
          <div class="missing-documents">
            <h3>Missing Required Documents</h3>
            <ul class="missing-list">
        `;

        missingDocs.forEach(doc => {
          reportHTML += `<li class="missing-item">${doc}</li>`;
        });

        reportHTML += `
            </ul>
          </div>
        `;
      }

      // Close containers
      reportHTML += `
          </div>
          <div class="report-footer">
            <p>Generated by NovAssistant on ${new Date().toLocaleDateString()}</p>
          </div>
        </div>
      `;

      // Display in a modal or new window
      // For this demo, we'll just copy to clipboard
      window.NovAssistant.ui.showToast("Report generated", "success");

      return reportHTML;
    } catch (error) {
      AppLogger.error("Error creating requirements report", error);
      return false;
    }
  },

  // Format sale type for display
  formatSaleType: function(saleType) {
    try {
      switch (saleType) {
        case "dealer":
          return "Dealer";
        case "private":
          return "Private";
        case "company":
          return "Company";
        case "saleAndLeaseback":
          return "Sale & Leaseback";
        case "refinance":
          return "Refinance";
        default:
          return saleType.charAt(0).toUpperCase() + saleType.slice(1);
      }
    } catch (error) {
      AppLogger.error("Error formatting sale type", error);
      return saleType;
    }
  },

  // Set the document tab's active state when a customer profile is opened
  setDocumentTabActive: function() {
    try {
      const documentTab = document.querySelector('.main-tab-btn[data-target="documentsTab"]');
      if (documentTab) {
        documentTab.click();
      }

      return true;
    } catch (error) {
      AppLogger.error("Error setting document tab active", error);
      return false;
    }
  }
};

