    // Initialize the module
    init: async function() {
      try {
        if (this.initialized) {
          return this;
        }
        
        AppLogger.log("Initializing Email Templates module");
        
        // Set up template category dropdown
        const categorySelect = document.getElementById('templateCategory');
        const templateSelect = document.getElementById('templateSelect');
        
        if (categorySelect && templateSelect) {
          // Populate template categories
          for (const [key, name] of Object.entries(this.templateCategories)) {
            const option = document.createElement('option');
            option.value = key;
            option.textContent = name;
            categorySelect.appendChild(option);
          }
          
          // Set up event listener for category changes
          categorySelect.addEventListener('change', () => {
            this.updateTemplateOptions(categorySelect.value);
          });
          
          // Set up event listener for template selection
          templateSelect.addEventListener('change', () => {
            this.loadSelectedTemplate();
          });
        }
        
        // Set up template action buttons
        this.setupTemplateActions();
        
        this.initialized = true;
        AppLogger.log("Email Templates module initialized successfully");
        
        return this;
      } catch (error) {
        AppLogger.error("Failed to initialize Email Templates module", error);
        throw error;
      }
    },
    
    // Update template dropdown options based on selected category
    updateTemplateOptions: function(categoryKey) {
      try {
        const templateSelect = document.getElementById('templateSelect');
        if (!templateSelect) return false;
        
        // Clear existing options
        templateSelect.innerHTML = '<option value="">-- Choose a Template --</option>';
        
        // If no category selected, return
        if (!categoryKey) return true;
        
        // Find templates for this category
        let templatesFound = 0;
        for (const [key, template] of Object.entries(this.templates)) {
          if (template.category === categoryKey) {
            const option = document.createElement('option');
            option.value = key;
            option.textContent = template.title;
            templateSelect.appendChild(option);
            templatesFound++;
          }
        }
        
        // Enable/disable the select based on available templates
        templateSelect.disabled = templatesFound === 0;
        
        return true;
      } catch (error) {
        AppLogger.error("Error updating template options", error);
        return false;
      }
    },
    
    // Load the selected template
    loadSelectedTemplate: function() {
      try {
        const templateSelect = document.getElementById('templateSelect');
        const templateKey = templateSelect.value;
        
        if (!templateKey) {
          this.clearTemplateContent();
          return false;
        }
        
        const template = this.templates[templateKey];
        if (!template) {
          AppLogger.error(`Template ${templateKey} not found`, new Error("Invalid template key"));
          return false;
        }
        
        // Store current template
        this.currentTemplate = template;
        
        // Update template elements
        const templateTitle = document.getElementById('templateTitle');
        const emailSubject = document.getElementById('emailSubject');
        const templateInput = document.getElementById('templateInput');
        
        if (templateTitle) templateTitle.textContent = template.title;
        if (emailSubject) emailSubject.value = template.subject;
        if (templateInput) templateInput.value = template.content;
        
        return true;
      } catch (error) {
        AppLogger.error("Error loading selected template", error);
        return false;
      }
    },
    
    // Clear template content
    clearTemplateContent: function() {
      try {
        const templateTitle = document.getElementById('templateTitle');
        const emailSubject = document.getElementById('emailSubject');
        const templateInput = document.getElementById('templateInput');
        
        if (templateTitle) templateTitle.textContent = "Email Template";
        if (emailSubject) emailSubject.value = "";
        if (templateInput) templateInput.value = "";
        
        this.currentTemplate = null;
        
        return true;
      } catch (error) {
        AppLogger.error("Error clearing template content", error);
        return false;
      }
    },
    
    // Set up template action buttons
    setupTemplateActions: function() {
      try {
        // Personalize button
        const personalizeBtn = document.getElementById('personalize');
        if (personalizeBtn) {
          personalizeBtn.addEventListener('click', this.personalizeTemplate.bind(this));
        }
        
        // Copy to clipboard button
        const copyBtn = document.getElementById('copyTemplate');
        if (copyBtn) {
          copyBtn.addEventListener('click', this.copyTemplateToClipboard.bind(this));
        }
        
        // Open in Outlook button
        const outlookBtn = document.getElementById('openInOutlook');
        if (outlookBtn) {
          outlookBtn.addEventListener('click', this.openInOutlook.bind(this));
        }
        
        return true;
      } catch (error) {
        AppLogger.error("Error setting up template actions", error);
        return false;
      }
    },
    
    // Personalize template with customer data
    personalizeTemplate: function() {
      try {
        // Check if we have a template loaded
        if (!this.currentTemplate) {
          window.NovAssistant.ui.showToast("Please select a template first", "warning");
          return false;
        }
        
        // Check if we have a current customer
        if (window.NovAssistant.state.currentCustomerIndex === null) {
          window.NovAssistant.ui.showToast("No customer selected", "warning");
          return false;
        }
        
        // Get customer data
        const customerManagement = window.NovAssistant.CustomerManagement;
        if (!customerManagement || !customerManagement.getCustomers) {
          window.NovAssistant.ui.showToast("Customer data not available", "error");
          return false;
        }
        
        const customers = customerManagement.getCustomers();
        const customer = customers[window.NovAssistant.state.currentCustomerIndex];
        
        if (!customer) {
          window.NovAssistant.ui.showToast("Could not load customer data", "error");
          return false;
        }
        
        // Get template content and subject
        let subject = this.currentTemplate.subject;
        let content = this.currentTemplate.content;
        
        // Replace placeholders with customer data
        const replacements = {
          "[Customer Name]": customer.name || "Valued Customer",
          "[Customer First Name]": customer.name ? customer.name.split(' ')[0] : "Valued Customer",
          "[Vehicle]": customer.interestedVehicle || "[Vehicle]",
          "[Vehicle Model]": customer.interestedVehicle || "[Vehicle Model]",
          "[Your Name]": "Harrington", // This should be dynamic in a real implementation
          "[Your Phone Number]": "1300 123 456", // This should be dynamic
          "[Your Email]": "harrington@sgfleet.com", // This should be dynamic
        };
        
        // Replace placeholders in subject and content
        for (const [placeholder, value] of Object.entries(replacements)) {
          subject = subject.replace(new RegExp(placeholder, 'g'), value);
          content = content.replace(new RegExp(placeholder, 'g'), value);
        }
        
        // Update the template elements
        const emailSubject = document.getElementById('emailSubject');
        const templateInput = document.getElementById('templateInput');
        
        if (emailSubject) emailSubject.value = subject;
        if (templateInput) templateInput.value = content;
        
        window.NovAssistant.ui.showToast("Template personalized with customer data", "success");
        
        return true;
      } catch (error) {
        AppLogger.error("Error personalizing template", error);
        window.NovAssistant.ui.showToast("Error personalizing template", "error");
        return false;
      }
    },
    
    // Copy template to clipboard
    copyTemplateToClipboard: function() {
      try {
        const emailSubject = document.getElementById('emailSubject');
        const templateInput = document.getElementById('templateInput');
        
        if (!emailSubject || !templateInput) {
          window.NovAssistant.ui.showToast("Template content not found", "error");
          return false;
        }
        
        // Create clipboard content with subject and body
        const clipboardContent = `Subject: ${emailSubject.value}\n\n${templateInput.value}`;
        
        // Copy to clipboard
        navigator.clipboard.writeText(clipboardContent)
          .then(() => {
            window.NovAssistant.ui.showToast("Template copied to clipboard", "success");
          })
          .catch((error) => {
            throw error;
          });
        
        return true;
      } catch (error) {
        AppLogger.error("Error copying template to clipboard", error);
        window.NovAssistant.ui.showToast("Error copying to clipboard", "error");
        return false;
      }
    },
    
    // Open template in Outlook
    openInOutlook: function() {
      try {
        const emailSubject = document.getElementById('emailSubject');
        const templateInput = document.getElementById('templateInput');
        
        if (!emailSubject || !templateInput) {
          window.NovAssistant.ui.showToast("Template content not found", "error");
          return false;
        }
        
        // Get customer email if available
        let recipientEmail = "";
        if (window.NovAssistant.state.currentCustomerIndex !== null) {
          const customers = window.NovAssistant.CustomerManagement.getCustomers();
          const customer = customers[window.NovAssistant.state.currentCustomerIndex];
          if (customer && customer.email) {
            recipientEmail = customer.email;
          }
        }
        
        // Create mailto link
        const subject = encodeURIComponent(emailSubject.value);
        const body = encodeURIComponent(templateInput.value);
        const mailtoLink = `mailto:${recipientEmail}?subject=${subject}&body=${body}`;
        
        // Open link
        window.location.href = mailtoLink;
        
        return true;
      } catch (error) {
        AppLogger.error("Error opening template in Outlook", error);
        window.NovAssistant.ui.showToast("Error opening in Outlook", "error");
        return false;
      }
    },
    
    // Get template by key
    getTemplate: function(templateKey) {
      return this.templates[templateKey] || null;
    }
  };

